mapper.select:
	 	  SELECT 
	 		COUNT(*) 
	 	FROM 
	 		TB_BM01M01

	 		USE_YN = #{useYn}

 			AND PRDT_CD = #{prdtCd}


 			AND PRDT_NM LIKE '%'||#{prdtNm}||'%'


			AND PRDT_GRP = #{prdtGrp}


			AND PRDT_DIV = #{prdtDiv}


			AND ${searchType} LIKE '%'||UPPER(#{searchValue})||'%'

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT PRDT_CD , PRDT_NM , PRDT_GRP , FN_CM05M01_CD_TO_NM(PRDT_GRP) AS PRDT_GRP_NM , PRDT_DIV , FN_CM05M01_CD_TO_NM(PRDT_DIV) AS PRDT_DIV_NM , PRDT_FRN , FN_CM05M01_CD_TO_NM(PRDT_FRN) AS PRDT_FRN_NM , PRDT_SIZE , PRDT_SPEC , PRDT_UNIT , NVL(FN_CM05M01_CD_TO_NM(PRDT_UNIT), PRDT_UNIT) AS PRDT_UNIT_NM , PRDT_WT , PRDT_UPR , TO_CHAR(TO_DATE(PRDT_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS PRDT_DT , USE_YN , ETC_FIELD1, ETC_FIELD2 , ETC_FIELD3 , CREAT_ID , CREAT_PGM , CREAT_DTTM , UDT_ID , UDT_PGM , UDT_DTTM FROM TB_BM01M01 T WHERE 1=1
AND USE_YN = #{useYn}
AND PRDT_CD = #{prdtCd}
AND PRDT_NM LIKE '%'||#{prdtNm}||'%'
AND PRDT_GRP = #{prdtGrp}
AND PRDT_DIV = #{prdtDiv}
AND ${searchType} LIKE '%'||UPPER(#{searchValue})||'%'
ORDER BY PRDT_GRP, PRDT_DIV, PRDT_NM, PRDT_SIZE, PRDT_SPEC ) A ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

SELECT COUNT(*) FROM TB_BM01M01
WHERE 1=1 

	AND PRDT_GRP = #{prdtGrp}


	AND PRDT_DIV = #{prdtDiv}


	AND PRDT_FRN = #{prdtFrn}


	AND PRDT_ITEM = #{prdtItem}

mapper.insert:
INSERT INTO TB_BM01M01
 (
		 PRDT_CD
	 , PRDT_NM
	 , PRDT_GRP
	 , PRDT_DIV
	 , PRDT_FRN
	 , PRDT_SPEC
	 , PRDT_SIZE
	 , PRDT_CLNT
	 , PRDT_ITEM
	 , PRDT_UNIT
	 , PRDT_WT
	 , PRDT_UPR
	 , PRDT_DT
	 , USE_YN
	 , ETC_FIELD1
	 , ETC_FIELD2
	 , ETC_FIELD3
	 , CREAT_ID
	 , CREAT_PGM
	 , CREAT_DTTM
 	) 	
 	VALUES
 	(
 		  #{prdtCd}
 		, #{prdtNm}
		, #{prdtGrp}
		, #{prdtDiv}
		, #{prdtFrn}
		, #{prdtSpec,  jdbcType=VARCHAR}
		, #{prdtSize,  jdbcType=VARCHAR}
		, #{prdtClnt,  jdbcType=VARCHAR}
		, #{prdtItem,  jdbcType=VARCHAR}
		, #{prdtUnit,  jdbcType=VARCHAR}
		, #{prdtWt,  jdbcType=VARCHAR}
		, #{prdtUpr,  jdbcType=VARCHAR}
		, #{prdtDt}
		, #{useYn}
		, #{etcField1,  jdbcType=VARCHAR}
		, #{etcField2,  jdbcType=VARCHAR}
		, #{etcField3,  jdbcType=VARCHAR}
		, #{userId}
		, #{pgmId}
		, sysdate
 	)

mapper.update:
UPDATE TB_BM01M01 
SET
 		  PRDT_NM     = #{prdtNm}
		, PRDT_GRP    = #{prdtGrp}
		, PRDT_DIV    = #{prdtDiv}
		, PRDT_FRN    = #{prdtFrn,  jdbcType=VARCHAR}
		, PRDT_SPEC   = #{prdtSpec,  jdbcType=VARCHAR}
		, PRDT_SIZE   = #{prdtSize,  jdbcType=VARCHAR}
		, PRDT_CLNT   = #{prdtClnt,  jdbcType=VARCHAR}
		, PRDT_ITEM   = #{prdtItem,  jdbcType=VARCHAR}
		, PRDT_UNIT   = #{prdtUnit}
		, PRDT_WT     = #{prdtWt}
		, PRDT_UPR    = #{prdtUpr}
		, PRDT_DT     = #{prdtDt}
		, USE_YN      = #{useYn}
		, ETC_FIELD1  = #{etcField1,  jdbcType=VARCHAR}
		, ETC_FIELD2  = #{etcField2,  jdbcType=VARCHAR}
		, ETC_FIELD3  = #{etcField3,  jdbcType=VARCHAR}
		, UDT_ID      = #{userId}
		, UDT_PGM     = #{pgmId}
		, UDT_DTTM    = SYSDATE
WHERE PRDT_CD     = #{prdtCd}

mapper.select:
SELECT COUNT(PRDT_CD)
  FROM TB_BM01M01 AS T
 WHERE PRDT_CD = #{prdtCd}
