mapper.select:
SELECT COUNT(*) FROM ( SELECT ROWNUM AS RNUM, T.* FROM (
SELECT A.CLNT_CD, A.CLNT_NM, A.CRN, A.CRN_TYP, FN_CM05M01_CD_TO_NM(A.CRN_TYP) AS CRN_TYP_NM, A.COPRT_NO, A.CLNT_DIV_CD, FN_CM05M01_CD_TO_NM(A.CLNT_DIV_CD) CLNT_DIV_NM, A.BIZCON_NM, A.BSTY_NM, A.BIZ_TEL_NO, TO_CHAR(A.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM, TO_CHAR(A.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM, A.BASIS_CDTLN_AMT, LINK_GRP_YN, A.LINK_GRP_CLNT_CD, (SELECT CLNT_NM FROM TB_BM02M01 B WHERE B.CLNT_CD = A.LINK_GRP_CLNT_CD ) AS LINK_GRP_CLNT_NM, A.REPST_NM, A.BIZ_ZIP, A.BIZ_ADDR, A.BIZ_ADDR_DTL, A.BIZ_FAX_NO, A.SUB_RMK_URL, A.BANK_CD, A.BKAC_NO, A.BKAC_OWNER, A.PCHS_PAY_DIV_CD, FN_CM05M01_CD_TO_NM(A.PCHS_PAY_DIV_CD) AS PCHS_PAY_DIV_NM, A.PCHS_CLMN_DIV_CD, FN_CM05M01_CD_TO_NM(A.PCHS_CLMN_DIV_CD) AS PCHS_CLMN_DIV_NM, A.PCHS_PMNT_MTD_CD, FN_CM05M01_CD_TO_NM(A.PCHS_PMNT_MTD_CD) AS PCHS_PMNT_MTD_NM, A.PCHS_VAT_CD, FN_CM05M01_CD_TO_NM(A.PCHS_VAT_CD) AS PCHS_VAT_NM, A.SELL_PAY_DIV_CD, FN_CM05M01_CD_TO_NM(A.SELL_PAY_DIV_CD) AS SELL_PAY_DIV_NM, A.SELL_CLMN_DIV_CD, FN_CM05M01_CD_TO_NM(A.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_NM, A.SELL_PMNT_MTD_CD, FN_CM05M01_CD_TO_NM(A.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_NM, A.SELL_VAT_CD, FN_CM05M01_CD_TO_NM(A.SELL_VAT_CD) AS SELL_VAT_NM, NVL((SELECT CODE_RPRC FROM TB_CM05M01 WHERE CODE_ID = A.SELL_VAT_CD), 10) AS SELL_VAT, (SELECT CODE_ID FROM TB_CM05M01 WHERE CODE_KIND = 'MAKR' AND CODE_ETC = A.CLNT_CD ) AS MAKR_CD, A.CLNT_RMK, A.USE_YN, A.ETC_FIELD1, A.ETC_FIELD2, A.ETC_FIELD3, A.CREAT_ID, (SELECT NAME FROM TB_CM06M01 WHERE ID = A.CREAT_ID) AS CREAT_NM, A.CREAT_PGM, A.UDT_ID, (SELECT NAME FROM TB_CM06M01 WHERE ID = A.UDT_ID) AS UDT_NM, A.UDT_PGM, A.PCHS_CLMN_DAY, A.PCHS_CLMN_RMK, A.SELL_CLMN_DAY, A.SELL_CLMN_RMK, (SELECT SALES_EMP_ID FROM TB_BM02D01 BMD01 WHERE USE_YN = 'Y' AND BMD01.CLNT_CD = A.CLNT_CD AND ROWNUM =1) AS SALES_EMP_ID , (SELECT NAME FROM TB_CM06M01 WHERE ID = (SELECT SALES_EMP_ID FROM TB_BM02D01 BMD01 WHERE USE_YN = 'Y' AND BMD01.CLNT_CD = A.CLNT_CD AND ROWNUM =1)) AS SALES_EMP_NM FROM TB_BM02M01 A
USE_YN = #{useYn}
AND ${searchType} LIKE '%'||#{searchValue}||'%'
AND A.CLNT_CD = #{searchValue}
AND A.CLNT_DIV_CD = #{clntDivCd}
AND A.CLNT_CD = #{clntCd}
AND A.CLNT_NM LIKE '%'||#{clntNm}||'%'
AND A.CRN LIKE '%'|| #{crn} ||'%'
AND A.CLNT_DIV_CD NOT IN (#{pchsClntCd})
ORDER BY NVL(A.UDT_DTTM, A.CREAT_DTTM) DESC, A.CLNT_CD
ORDER BY A.CLNT_CD
) T WHERE 1=1
AND SALES_EMP_ID = #{salesEmpId}
AND SALES_EMP_NM LIKE '%'||#{salesEmpNm}||'%'
)

/* selectClntList */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T.* FROM ( SELECT A.CLNT_CD, A.CLNT_NM, A.CRN, A.CRN_TYP, FN_CM05M01_CD_TO_NM(A.CRN_TYP) AS CRN_TYP_NM, A.COPRT_NO, A.CLNT_DIV_CD, FN_CM05M01_CD_TO_NM(A.CLNT_DIV_CD) CLNT_DIV_NM, A.BIZCON_NM, A.BSTY_NM, A.BIZ_TEL_NO, TO_CHAR(A.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM, TO_CHAR(A.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM, A.BASIS_CDTLN_AMT, LINK_GRP_YN, A.LINK_GRP_CLNT_CD, (SELECT CLNT_NM FROM TB_BM02M01 B WHERE B.CLNT_CD = A.LINK_GRP_CLNT_CD ) AS LINK_GRP_CLNT_NM, A.REPST_NM, A.BIZ_ZIP, A.BIZ_ADDR, A.BIZ_ADDR_DTL, A.BIZ_FAX_NO, A.SUB_RMK_URL, A.BANK_CD, A.BKAC_NO, A.BKAC_OWNER, A.PCHS_PAY_DIV_CD, FN_CM05M01_CD_TO_NM(A.PCHS_PAY_DIV_CD) AS PCHS_PAY_DIV_NM, A.PCHS_CLMN_DIV_CD, FN_CM05M01_CD_TO_NM(A.PCHS_CLMN_DIV_CD) AS PCHS_CLMN_DIV_NM, A.PCHS_PMNT_MTD_CD, FN_CM05M01_CD_TO_NM(A.PCHS_PMNT_MTD_CD) AS PCHS_PMNT_MTD_NM, A.PCHS_VAT_CD, FN_CM05M01_CD_TO_NM(A.PCHS_VAT_CD) AS PCHS_VAT_NM, A.SELL_PAY_DIV_CD, FN_CM05M01_CD_TO_NM(A.SELL_PAY_DIV_CD) AS SELL_PAY_DIV_NM, A.SELL_CLMN_DIV_CD, FN_CM05M01_CD_TO_NM(A.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_NM, A.SELL_PMNT_MTD_CD, FN_CM05M01_CD_TO_NM(A.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_NM, A.SELL_VAT_CD, FN_CM05M01_CD_TO_NM(A.SELL_VAT_CD) AS SELL_VAT_NM, NVL((SELECT CODE_RPRC FROM TB_CM05M01 WHERE CODE_ID = A.SELL_VAT_CD), 10) AS SELL_VAT, (SELECT CODE_ID FROM TB_CM05M01 WHERE CODE_KIND = 'MAKR' AND CODE_ETC = A.CLNT_CD ) AS MAKR_CD, A.CLNT_RMK, A.USE_YN, A.ETC_FIELD1, A.ETC_FIELD2, A.ETC_FIELD3, A.CREAT_ID, (SELECT NAME FROM TB_CM06M01 WHERE ID = A.CREAT_ID) AS CREAT_NM, A.CREAT_PGM, A.UDT_ID, (SELECT NAME FROM TB_CM06M01 WHERE ID = A.UDT_ID) AS UDT_NM, A.UDT_PGM, A.PCHS_CLMN_DAY, A.PCHS_CLMN_RMK, A.SELL_CLMN_DAY, A.SELL_CLMN_RMK, (SELECT SALES_EMP_ID FROM TB_BM02D01 BMD01 WHERE USE_YN = 'Y' AND BMD01.CLNT_CD = A.CLNT_CD AND ROWNUM =1) AS SALES_EMP_ID , (SELECT NAME FROM TB_CM06M01 WHERE ID = (SELECT SALES_EMP_ID FROM TB_BM02D01 BMD01 WHERE USE_YN = 'Y' AND BMD01.CLNT_CD = A.CLNT_CD AND ROWNUM =1)) AS SALES_EMP_NM
FROM TB_BM02M01 A
USE_YN = #{useYn}
AND ${searchType} LIKE '%'||#{searchValue}||'%'
AND A.CLNT_CD = #{searchValue}
AND A.CLNT_DIV_CD = #{clntDivCd}
AND A.CLNT_CD = #{clntCd}
AND A.CLNT_NM LIKE '%'||#{clntNm}||'%'
AND A.CRN LIKE '%'|| #{crn} ||'%'
AND A.CLNT_DIV_CD NOT IN (#{pchsClntCd})
ORDER BY NVL(A.UDT_DTTM, A.CREAT_DTTM) DESC, A.CLNT_CD
ORDER BY A.CLNT_CD
) T WHERE 1=1
AND SALES_EMP_ID = #{salesEmpId}
AND SALES_EMP_NM LIKE '%'||#{salesEmpNm}||'%'
) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

mapper.update:
UPDATE TB_BM02D01
   SET 
       BIZDEPT_NM      = #{bizdeptNm,    		jdbcType=VARCHAR}
     , BIZDEPT_DIV_CD  = #{bizdeptDivCd,    	jdbcType=VARCHAR}
     , MNG_NM          = #{mngNm,    			jdbcType=VARCHAR}
     , MNG_DEPT_NM     = #{mngDeptNm,    		jdbcType=VARCHAR}
     , MNG_PST_CD      = #{mngPstCd,    		jdbcType=VARCHAR}
     , MNG_TEL_NO      = #{mngTelNo,    		jdbcType=VARCHAR}
     , MNG_MOBLPHON_NO = #{mngMoblphonNo,    	jdbcType=VARCHAR}
     , MNG_EMAIL       = #{mngEmail,    		jdbcType=VARCHAR}
     , SALES_CO_CD     = #{salesCoCd,    		jdbcType=VARCHAR}
     , SALES_EMP_ID    = #{salesEmpId,    		jdbcType=VARCHAR}
     , USE_YN          = #{useYn,    		jdbcType=VARCHAR}
     , UDT_ID          = #{udtId,    		jdbcType=VARCHAR}
     , UDT_PGM         = #{udtPgm,    		jdbcType=VARCHAR}
     , UDT_DTTM        = SYSDATE
 WHERE CLNT_CD         = #{clntCd_P}
   AND BIZDEPT_SN      = #{bizdeptSn}
   AND (BIZDEPT_NM    != #{bizdeptNm}
    OR NVL(BIZDEPT_DIV_CD,'@')  != NVL(#{bizdeptDivCd,  jdbcType=VARCHAR},'@')
    OR NVL(MNG_NM,'@')          != NVL(#{mngNm,  jdbcType=VARCHAR},'@')
    OR NVL(MNG_DEPT_NM,'@')     != NVL(#{mngDeptNm,  jdbcType=VARCHAR},'@')
    OR NVL(MNG_PST_CD,'@')      != NVL(#{mngPstCd,  jdbcType=VARCHAR},'@')
    OR NVL(MNG_TEL_NO,'@')      != NVL(#{mngTelNo,  jdbcType=VARCHAR},'@')
    OR NVL(MNG_MOBLPHON_NO,'@') != NVL(#{mngMoblphonNo,  jdbcType=VARCHAR},'@')
    OR NVL(MNG_EMAIL,'@')       != NVL(#{mngEmail,  jdbcType=VARCHAR},'@')
    OR NVL(SALES_CO_CD,'@')     != NVL(#{salesCoCd,  jdbcType=VARCHAR},'@')
    OR NVL(SALES_EMP_ID,'@')    != NVL(#{salesEmpId,  jdbcType=VARCHAR},'@')
    OR USE_YN        		    != #{useYn}
   	)

UPDATE TB_BM02D02
   SET 
       CO_CD           	   = #{coCd}
     , SELPCH_CD           = #{selpchCd}
     , PLDG_DIV_CD         = #{pldgDivCd}
     , PLDG_CHG_CD     	   = #{pldgChgCd}
     , PLDG_AMT        	   = #{pldgAmt}
     , PLDG_RCOGN_RATE 	   = #{pldgRcognRate}
     , EXPRTN_DT		   = #{exprtnDt}
     , SETUP_DT    		   = #{setupDt}
     , PLDG_RMK     	   = #{pldgRmk}
	 , GRNTY_INSTT_CD      = #{grntyInsttCd}
     , USE_YN       	   = #{useYn}
	 , PTON_NM       	   = #{ptonNm}
     , UDT_ID        	   = #{udtId}
     , UDT_PGM       	   = #{udtPgm}
     , PRDT_GRP       	   = #{prdtGrp}
     , UDT_DTTM      	   = SYSDATE
 WHERE PLDG_SN        	   = #{pldgSn}
   AND (PLDG_AMT      	  != #{pldgAmt}
    OR PLDG_RCOGN_RATE 	  != #{pldgRcognRate}
    OR NVL(EXPRTN_DT,'X') != NVL(#{exprtnDt},'X')
    OR SETUP_DT    	   	  != #{setupDt}
    OR NVL(PLDG_RMK,'X')  != NVL(#{pldgRmk},'X')
    OR USE_YN        	  != #{useYn}
    OR SELPCH_CD    	  != #{selpchCd}
    OR CO_CD         	  != #{coCd}
    OR PRDT_GRP           != #{prdtGrp}
    OR PLDG_CHG_CD    	  != #{pldgChgCd}
    OR PLDG_DIV_CD        != #{pldgDivCd}
	OR NVL(PTON_NM ,'X')  != NVL(#{ptonNm},'X')			
	OR NVL(GRNTY_INSTT_CD,'@') != NVL(#{grntyInsttCd},'@')
    )

mapper.select:
SELECT count(*) FROM (
SELECT ROWNUM AS RNUM, T.* FROM ( SELECT DISTINCT A.CLNT_CD, A.CLNT_NM, A.CRN, A.CRN_TYP, A.REPST_NM, A.BIZCON_NM, FN_CM05M01_CD_TO_NM(A.CRN_TYP) AS CRN_TYP_NM, A.COPRT_NO, A.CLNT_DIV_CD, FN_CM05M01_CD_TO_NM(A.CLNT_DIV_CD) CLNT_DIV_NM, A.BIZCON_NM, A.BSTY_NM, A.BIZ_TEL_NO, TO_CHAR(A.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM, TO_CHAR(A.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM FROM TB_BM02M01 A JOIN TB_CR02M01 B ON B.ORDRS_CLNT_CD = A.CLNT_CD WHERE 1=1
AND B.MNG_ID = #{searchValue}
AND A.CLNT_NM LIKE '%'||#{searchClntNm}||'%'
) T
)

/* selectUserClntList */ SELECT * FROM (
SELECT ROWNUM AS RNUM, T.* FROM ( SELECT DISTINCT A.CLNT_CD, A.CLNT_NM, A.CRN, A.CRN_TYP, A.REPST_NM, A.BIZCON_NM, FN_CM05M01_CD_TO_NM(A.CRN_TYP) AS CRN_TYP_NM, A.COPRT_NO, A.CLNT_DIV_CD, FN_CM05M01_CD_TO_NM(A.CLNT_DIV_CD) CLNT_DIV_NM, A.BIZCON_NM, A.BSTY_NM, A.BIZ_TEL_NO, TO_CHAR(A.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM, TO_CHAR(A.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM FROM TB_BM02M01 A JOIN TB_BM06M01 B ON B.CLNT_CD = A.CLNT_CD WHERE 1=1
AND B.MNG_ID = #{searchValue}
AND A.CLNT_NM LIKE '%'||#{searchClntNm}||'%'
AND B.CO_CD LIKE '%'||#{coCd}||'%'
AND TO_CHAR(B.ORDRS_PLAN_DT,'yyyy') = #{salesPlanYy}
) T
) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}
