mapper.select:
WITH MNG AS ( SELECT DISTINCT CLNT_CD, SALES_EMP_ID, SALES_EMP_NM FROM ( SELECT T.*, C.NAME AS SALES_EMP_NM, ROW_NUMBER() OVER (PARTITION BY T.CLNT_CD ORDER BY rownum ASC) AS RN FROM TB_BM02D01 T LEFT OUTER JOIN TB_CM06M01 C ON C.ID = T.SALES_EMP_ID WHERE 1 = 1
AND T.SALES_EMP_ID = #{salesEmpId}
AND C.NAME LIKE '%'||#{salesEmpNm}||'%'
) RANK_DATA WHERE rn = 1 ) SELECT COUNT(*) FROM ( SELECT ROWNUM AS RNUM, T.* FROM ( SELECT A.CLNT_CD, A.CLNT_NM, A.CRN, A.CRN_TYP, FN_CM05M01_CD_TO_NM(A.CRN_TYP) AS CRN_TYP_NM, A.COPRT_NO, A.CLNT_DIV_CD, FN_CM05M01_CD_TO_NM(A.CLNT_DIV_CD) CLNT_DIV_NM, A.BIZCON_NM, A.BSTY_NM, A.BIZ_TEL_NO, TO_CHAR(A.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM, TO_CHAR(A.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM, A.REPST_NM, A.BIZ_ZIP, A.BIZ_ADDR, A.BIZ_ADDR_DTL, A.BIZ_FAX_NO, A.SUB_RMK_URL, A.BANK_CD, A.BKAC_NO, A.BKAC_OWNER, A.PCHS_PAY_DIV_CD, FN_CM05M01_CD_TO_NM(A.PCHS_PAY_DIV_CD) AS PCHS_PAY_DIV_NM, A.PCHS_CLMN_DIV_CD, FN_CM05M01_CD_TO_NM(A.PCHS_CLMN_DIV_CD) AS PCHS_CLMN_DIV_NM, A.PCHS_PMNT_MTD_CD, FN_CM05M01_CD_TO_NM(A.PCHS_PMNT_MTD_CD) AS PCHS_PMNT_MTD_NM, A.PCHS_VAT_CD, FN_CM05M01_CD_TO_NM(A.PCHS_VAT_CD) AS PCHS_VAT_NM, A.SELL_PAY_DIV_CD, FN_CM05M01_CD_TO_NM(A.SELL_PAY_DIV_CD) AS SELL_PAY_DIV_NM, A.SELL_CLMN_DIV_CD, FN_CM05M01_CD_TO_NM(A.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_NM, A.SELL_PMNT_MTD_CD, FN_CM05M01_CD_TO_NM(A.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_NM, A.SELL_VAT_CD, FN_CM05M01_CD_TO_NM(A.SELL_VAT_CD) AS SELL_VAT_NM, NVL((SELECT CODE_RPRC FROM TB_CM05M01 WHERE CODE_ID = A.SELL_VAT_CD), 10) AS SELL_VAT, (SELECT CODE_ID FROM TB_CM05M01 WHERE CODE_KIND = 'MAKR' AND CODE_ETC = A.CLNT_CD ) AS MAKR_CD, A.CLNT_RMK, A.USE_YN, A.ETC_FIELD1, A.ETC_FIELD2, A.ETC_FIELD3, A.CREAT_ID, (SELECT NAME FROM TB_CM06M01 WHERE ID = A.CREAT_ID) AS CREAT_NM, A.CREAT_PGM, A.UDT_ID, (SELECT NAME FROM TB_CM06M01 WHERE ID = A.UDT_ID) AS UDT_NM, A.UDT_PGM, A.PCHS_CLMN_DAY, A.PCHS_CLMN_RMK, A.SELL_CLMN_DAY, A.SELL_CLMN_RMK, MNG.SALES_EMP_ID, MNG.SALES_EMP_NM, NVL(COALESCE(B.CO_CD_ALL, 'GUN,TRN'), 'GUN,TRN') AS CO_CD_ALL FROM TB_BM02M01 A LEFT OUTER JOIN ( SELECT CLNT_CD, LISTAGG(SALES_CO_CD, ',') WITHIN GROUP (ORDER BY SALES_CO_CD) AS CO_CD_ALL FROM ( SELECT DISTINCT CLNT_CD, SALES_CO_CD FROM TB_BM02D01 ) GROUP BY CLNT_CD ) B ON A.CLNT_CD = B.CLNT_CD LEFT OUTER JOIN MNG ON MNG.CLNT_CD = A.CLNT_CD
USE_YN = #{useYn}
AND REPLACE(REPLACE(${searchType}, '-', ''), ' ', '') LIKE '%'|| REPLACE(REPLACE(UPPER(#{searchValue}), '-', ''), ' ', '')||'%'
AND A.CLNT_CD = #{searchValue}
AND A.CLNT_DIV_CD = #{clntDivCd}
AND A.CLNT_CD = #{clntCd}
AND REPLACE(REPLACE(A.CLNT_NM, '-', ''), ' ', '') LIKE '%'|| REPLACE(REPLACE(UPPER(#{clntNm}), '-', ''), ' ', '')||'%'
AND A.CRN LIKE '%'|| #{crn} ||'%'
AND A.CLNT_DIV_CD NOT IN (#{pchsClntCd})
ORDER BY NVL(A.UDT_DTTM, A.CREAT_DTTM) DESC, A.CLNT_CD ) T WHERE 1=1
AND CO_CD_ALL LIKE '%'|| #{coCd} ||'%'
) T

/* selectClntList */ WITH MNG AS ( SELECT DISTINCT CLNT_CD, SALES_EMP_ID, SALES_EMP_NM FROM ( SELECT T.*, C.NAME AS SALES_EMP_NM, ROW_NUMBER() OVER (PARTITION BY T.CLNT_CD ORDER BY rownum ASC) AS RN FROM TB_BM02D01 T LEFT OUTER JOIN TB_CM06M01 C ON C.ID = T.SALES_EMP_ID WHERE 1 = 1
AND T.SALES_EMP_ID = #{salesEmpId}
AND C.NAME LIKE '%'||#{salesEmpNm}||'%'
) RANK_DATA WHERE rn = 1 ) SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT A.CLNT_CD, A.CLNT_NM, A.CRN, A.CRN_TYP, FN_CM05M01_CD_TO_NM(A.CRN_TYP) AS CRN_TYP_NM, A.COPRT_NO, A.CLNT_DIV_CD, FN_CM05M01_CD_TO_NM(A.CLNT_DIV_CD) CLNT_DIV_NM, A.BIZCON_NM, A.BSTY_NM, A.BIZ_TEL_NO, TO_CHAR(A.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM, TO_CHAR(A.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM, A.REPST_NM, A.BIZ_ZIP, A.BIZ_ADDR, A.BIZ_ADDR_DTL, A.BIZ_FAX_NO, A.SUB_RMK_URL, A.BANK_CD, A.BKAC_NO, A.BKAC_OWNER, A.PCHS_PAY_DIV_CD, FN_CM05M01_CD_TO_NM(A.PCHS_PAY_DIV_CD) AS PCHS_PAY_DIV_NM, A.PCHS_CLMN_DIV_CD, FN_CM05M01_CD_TO_NM(A.PCHS_CLMN_DIV_CD) AS PCHS_CLMN_DIV_NM, A.PCHS_PMNT_MTD_CD, FN_CM05M01_CD_TO_NM(A.PCHS_PMNT_MTD_CD) AS PCHS_PMNT_MTD_NM, A.PCHS_VAT_CD, FN_CM05M01_CD_TO_NM(A.PCHS_VAT_CD) AS PCHS_VAT_NM, A.SELL_PAY_DIV_CD, FN_CM05M01_CD_TO_NM(A.SELL_PAY_DIV_CD) AS SELL_PAY_DIV_NM, A.SELL_CLMN_DIV_CD, FN_CM05M01_CD_TO_NM(A.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_NM, A.SELL_PMNT_MTD_CD, FN_CM05M01_CD_TO_NM(A.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_NM, A.SELL_VAT_CD, FN_CM05M01_CD_TO_NM(A.SELL_VAT_CD) AS SELL_VAT_NM, NVL((SELECT CODE_RPRC FROM TB_CM05M01 WHERE CODE_ID = A.SELL_VAT_CD), 10) AS SELL_VAT, (SELECT CODE_ID FROM TB_CM05M01 WHERE CODE_KIND = 'MAKR' AND CODE_ETC = A.CLNT_CD ) AS MAKR_CD, A.CLNT_RMK, A.USE_YN, A.ETC_FIELD1, A.ETC_FIELD2, A.ETC_FIELD3, A.CREAT_ID, (SELECT NAME FROM TB_CM06M01 WHERE ID = A.CREAT_ID) AS CREAT_NM, A.CREAT_PGM, A.UDT_ID, (SELECT NAME FROM TB_CM06M01 WHERE ID = A.UDT_ID) AS UDT_NM, A.UDT_PGM, A.PCHS_CLMN_DAY, A.PCHS_CLMN_RMK, A.SELL_CLMN_DAY, A.SELL_CLMN_RMK, MNG.SALES_EMP_ID, MNG.SALES_EMP_NM, NVL(COALESCE(B.CO_CD_ALL, 'GUN,TRN'), 'GUN,TRN') AS CO_CD_ALL FROM TB_BM02M01 A LEFT OUTER JOIN ( SELECT CLNT_CD, LISTAGG(SALES_CO_CD, ',') WITHIN GROUP (ORDER BY SALES_CO_CD) AS CO_CD_ALL FROM ( SELECT DISTINCT CLNT_CD, SALES_CO_CD FROM TB_BM02D01 ) GROUP BY CLNT_CD ) B ON A.CLNT_CD = B.CLNT_CD LEFT OUTER JOIN MNG ON MNG.CLNT_CD = A.CLNT_CD
USE_YN = #{useYn}
AND REPLACE(REPLACE(${searchType}, '-', ''), ' ', '') LIKE '%'|| REPLACE(REPLACE(UPPER(#{searchValue}), '-', ''), ' ', '')||'%'
AND A.CLNT_CD = #{searchValue}
AND A.CLNT_DIV_CD = #{clntDivCd}
AND A.CLNT_CD = #{clntCd}
AND REPLACE(REPLACE(A.CLNT_NM, '-', ''), ' ', '') LIKE '%'|| REPLACE(REPLACE(UPPER(#{clntNm}), '-', ''), ' ', '')||'%'
AND A.CRN LIKE '%'|| #{crn} ||'%'
AND A.CLNT_DIV_CD NOT IN (#{pchsClntCd})
ORDER BY NVL(A.UDT_DTTM, A.CREAT_DTTM) DESC, A.CLNT_CD ) A WHERE 1=1
AND A.CO_CD_ALL LIKE '%'|| #{coCd} ||'%'
ORDER BY NVL(A.UDT_DTTM, A.CREAT_DTTM) DESC, A.CLNT_CD
ORDER BY A.CLNT_CD
) T WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

mapper.update:
UPDATE TB_BM02D01
   SET 
       BIZDEPT_NM      = #{bizdeptNm,    		jdbcType=VARCHAR}
     , BIZDEPT_DIV_CD  = #{bizdeptDivCd,    	jdbcType=VARCHAR}
     , MNG_NM          = #{mngNm,    			jdbcType=VARCHAR}
     , MNG_DEPT_NM     = #{mngDeptNm,    		jdbcType=VARCHAR}
     , MNG_PST_CD      = #{mngPstCd,    		jdbcType=VARCHAR}
     , MNG_TEL_NO      = #{mngTelNo,    		jdbcType=VARCHAR}
     , MNG_MOBLPHON_NO = #{mngMoblphonNo,    	jdbcType=VARCHAR}
     , MNG_EMAIL       = #{mngEmail,    		jdbcType=VARCHAR}
     , SALES_CO_CD     = #{salesCoCd,    		jdbcType=VARCHAR}
     , SALES_EMP_ID    = #{salesEmpId,    		jdbcType=VARCHAR}
     , USE_YN          = #{useYn,    		jdbcType=VARCHAR}
     , UDT_ID          = #{udtId,    		jdbcType=VARCHAR}
     , UDT_PGM         = #{udtPgm,    		jdbcType=VARCHAR}
     , UDT_DTTM        = SYSDATE
 WHERE CLNT_CD         = #{clntCd_P}
   AND BIZDEPT_SN      = #{bizdeptSn}
   AND (BIZDEPT_NM    != #{bizdeptNm}
    OR NVL(BIZDEPT_DIV_CD,'@')  != NVL(#{bizdeptDivCd,  jdbcType=VARCHAR},'@')
    OR NVL(MNG_NM,'@')          != NVL(#{mngNm,  jdbcType=VARCHAR},'@')
    OR NVL(MNG_DEPT_NM,'@')     != NVL(#{mngDeptNm,  jdbcType=VARCHAR},'@')
    OR NVL(MNG_PST_CD,'@')      != NVL(#{mngPstCd,  jdbcType=VARCHAR},'@')
    OR NVL(MNG_TEL_NO,'@')      != NVL(#{mngTelNo,  jdbcType=VARCHAR},'@')
    OR NVL(MNG_MOBLPHON_NO,'@') != NVL(#{mngMoblphonNo,  jdbcType=VARCHAR},'@')
    OR NVL(MNG_EMAIL,'@')       != NVL(#{mngEmail,  jdbcType=VARCHAR},'@')
    OR NVL(SALES_CO_CD,'@')     != NVL(#{salesCoCd,  jdbcType=VARCHAR},'@')
    OR NVL(SALES_EMP_ID,'@')    != NVL(#{salesEmpId,  jdbcType=VARCHAR},'@')
    OR USE_YN        		    != #{useYn}
   	)

UPDATE TB_BM02D02
   SET 
