mapper.select:
SELECT * FROM( SELECT ROWNUM AS RNUM, A.*
FROM ( SELECT T.PRJCT_SEQ , T.CO_CD , FN_CM05M01_CD_TO_NM(T.CO_CD) CO_NM , T.YYYYMM , T.INPEXP_CD , FN_CM05M01_CD_TO_NM(T.INPEXP_CD) INPEXP_NM , T.NEW_PRDT_CD , FN_CM05M01_CD_TO_NM(T.NEW_PRDT_CD) NEW_PRDT_NM , T.PRJCT_CD , BE.CODE_NM PRJCT_CD_NM , T.PRJCT_NM , T.EQP_NM , T.CAR_NM , T.EQP_QTY , T.CLNT_CD , A.CLNT_NM , T.CLNT_MNG_NM , T.ODR_CD , FN_CM05M01_CD_TO_NM(T.ODR_CD) ODR_NM , T.ODR_RMK , T.EPCT_AMT , T.ORDRS_PLAN_AMT , T.WINBD_CD , T.WINBD_RMK , TO_CHAR(T.ORDRS_PLAN_DT, 'YYYY-MM-DD') AS ORDRS_PLAN_DT , T.ORDRS_AMT , TO_CHAR(T.ORDRS_DT, 'YYYY-MM-DD') AS ORDRS_DT , TO_CHAR(T.DUDT_PLAN_DT, 'YYYY-MM-DD') AS DUDT_PLAN_DT , T.MNG_ID , B.NAME as MNG_NM , T.PRJCT_RMK , T.ORDRS_PCT , T.CREAT_ID , T.CREAT_PGM , T.CREAT_DTTM , T.UDT_ID , T.UDT_PGM , T.UDT_DTTM , T.MAKR_CD , T.USE_YN , T.ORDRS_PCT , BD.ISS_CNTS , BD.ISS_STS , FN_CM05M01_CD_TO_NM(BD.ISS_STS) ISS_STS_NM , BC.CODE_RPRC AS ISS_YN FROM TB_BM06M01 T LEFT OUTER JOIN TB_BM02M01 A ON T.CLNT_CD = A.CLNT_CD LEFT OUTER JOIN TB_CM06M01 B ON T.MNG_ID = B.ID LEFT OUTER JOIN TB_BM06D03 BD ON T.PRJCT_SEQ = BD.PRJCT_SEQ LEFT OUTER JOIN TB_CM05M01 BC ON BD.ISS_STS = BC.CODE_ID LEFT OUTER JOIN TB_CM05M01 BE ON T.PRJCT_CD = BE.CODE_ID WHERE 1=1
ORDER BY T.CO_CD, T.YYYYMM, A.CLNT_NM, T.INPEXP_CD, T.PRJCT_SEQ ) AS a ) A WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT COUNT(PRJCT_SEQ) CNT
  FROM TB_BM06M01
 WHERE PRJCT_SEQ = #{prjctSeq}

mapper.update:
    UPDATE TB_BM06M01
          SET
          CO_CD                                         = #{coCd}
        , YYYYMM                                        = #{yyyymm}
        , PRJCT_CD                                      = #{prjctCd}
        , PRJCT_NM                                      = #{prjctNm}
        , INPEXP_CD                                     = #{inpexpCd}
        , NEW_PRDT_CD                                   = #{newPrdtCd}
        , EQP_NM                                        = #{eqpNm}
        , EQP_QTY                                       = #{eqpQty}
		, CAR_NM=''
        , CLNT_CD                                       = #{clntCd}
        , CLNT_MNG_NM                                   = #{clntMngNm}
        , ODR_CD                                        = #{odrCd}
        , ODR_RMK                                       = #{odrRmk, jdbcType=VARCHAR}
        , EPCT_AMT                                      = #{epctAmt}
        , ORDRS_PLAN_AMT                                = #{ordrsPlanAmt}
        , WINBD_CD                                      = #{winbdCd}
        , WINBD_RMK                                     = #{winbdRmk}
        , ORDRS_PLAN_DT                                 = #{ordrsPlanDt}
        , ORDRS_AMT                                     = #{ordrsAmt}
        , ORDRS_DT                                      = #{ordrsDt}
        , DUDT_PLAN_DT                                  = #{dudtPlanDt}
        , MNG_ID                                        = #{mngId}
        , PRJCT_RMK                                     = #{prjctRmk, jdbcType=VARCHAR}
		, USE_YN=''        
        , ORDRS_PCT                                     = #{ordrsPct}
		, MAKR_CD=''
        , UDT_ID                                        = #{userId}
        , UDT_PGM                                       = #{pgmId}
        , UDT_DTTM                                      = SYSDATE
    WHERE PRJCT_SEQ                                     = #{prjctSeq}

