INSERT INTO TB_BM06D01
(
       PRJCT_SEQ
     , PRJCT_PRDT_CD
     , PRJCT_PRDT_QTY
     , PRJCT_INPEXP_CD
     , PRJCT_PRDT_NEW_CD
     , PRJCT_PRDT_ORDRS_DT
     , PRJCT_PRDT_PLAN_DT
     , PRJCT_PRDT_RMK
  )
VALUES
