mapper.select:
SELECT count(*) FROM TB_BM06M01 WHERE 1=1
AND CO_CD = #{coCd}
AND ORDRS_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD') +1
AND ${searchType} LIKE '%${searchValue}%'
AND ORD_TYP = #{ordTyp}

SELECT * FROM( SELECT ROWNUM AS RNUM, A.*
FROM ( SELECT PRJCT_SEQ , CO_CD , FN_CM05M01_CD_TO_NM(CO_CD) CO_NM , YYYYMM , INPEXP_CD , FN_CM05M01_CD_TO_NM(INPEXP_CD) INPEXP_NM , NEW_PRDT_CD , FN_CM05M01_CD_TO_NM(NEW_PRDT_CD) NEW_PRDT_NM , PRJCT_CD , PRJCT_NM , EQP_NM , CAR_NM , EQP_QTY , TB_BM06M01.CLNT_CD , TB_BM02M01.CLNT_NM , CLNT_MNG_NM , ODR_CD , FN_CM05M01_CD_TO_NM(ODR_CD) ODR_NM , ODR_RMK , EPCT_AMT , ORDRS_PLAN_AMT , WINBD_CD , WINBD_RMK , ORDRS_PLAN_DT , ORDRS_AMT , ORDRS_DT , DUDT_PLAN_DT , MNG_ID , PRJCT_RMK , ORDRS_PCT
FROM TB_BM06M01 INNER JOIN TB_BM02M01 ON TB_BM06M01.CLNT_CD = TB_BM02M01.CLNT_CD WHERE 1=1
AND CO_CD = #{coCd}
AND ORDRS_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD') +1
ORDER BY YYYYMM, INPEXP_CD ) AS a ) A WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

mapper.update:
UPDATE TB_BM06M01
      SET
      PRJCT_SEQ                                     = #{prjctSeq}
    , CO_CD                                         = #{coCd}
    , INPEXP_CD                                     = #{inpexpCd}
    , NEW_PRDT_CD                                   = #{newPrdtCd}
    , CLNT_CD                                       = #{clntCd}
    , CLNT_MNG_NM                                   = #{clntMngNm}
    , PRJCT_NM                                      = #{prjctNm}
    , EQP_NM                                        = #{eqpNm}
    , EQP_QTY                                       = #{eqpQty}
    , ODR_CD                                        = #{odrCd}
    , ODR_RMK                                       = #{odrRmk}
    , EPCT_AMT                                      = #{epctAmt}
    , ORDRS_PLAN_AMT                                = #{ordrsPlanAmt}
    , WINBD_RMK                                     = #{winbdRmk}
    , ORDRS_PLAN_DT                                 = #{ordrsPlanDt}
    , WINBD_CD                                      = #{winbdCd}
    , ORDRS_DT                                      = #{ordrsDt}
    , DUDT_PLAN_DT                                  = #{dudtPlanDt}
    , MNG_ID                                        = #{mngId}
    , YYYYMM                                        = #{yyyymm}
    , PRJCT_CD                                      = #{prjctCd}
    , PRJCT_RMK                                     = #{prjctRmk}
    , ORDRS_AMT                                     = #{ordrsAmt}
    , ORDRS_PCT                                     = #{ordrsPct}
    , UDT_ID                                        = #{userId}
    , UDT_PGM                                       = #{pgmId}
    , UDT_DTTM                                      = SYSDATE
WHERE PRJCT_SEQ                                     = #{prjctSeq}

UPDATE TB_BM06D01
   SET
       PRJCT_SEQ           = #{prjctSeq}
     , PRJCT_PRDT_CD       = #{prjctPrdtCd}
     , PRJCT_ITEM_CD       = #{codeId}
     , PRJCT_ITEM_RMK      = #{prjctItemRmk, jdbcType=VARCHAR}
     , PRJCT_ITEM_SORT     = 1



 WHERE PRJCT_SEQ           = #{prjctSeq}
       AND PRJCT_PRDT_CD   = #{prjctPrdtCd}
       AND PRJCT_ITEM_CD   = #{codeId}

