mapper.delete:
DELETE FROM TB_BM06D01
 WHERE PRJCT_SEQ     = #{prjctSeq}
   AND PRJCT_ITEM_CD = #{codeId}
   AND PRJCT_PRDT_CD = #{prjctPrdtCd}

mapper.select:
SELECT COUNT(PRJCT_SEQ) CNT
  FROM TB_BM06M01
 WHERE PRJCT_SEQ = #{prjctSeq}

mapper.update:
