mapper.select:
SELECT PRJCT_SEQ , T.CO_CD , T.INPEXP_CD , FN_CM05M01_CD_TO_NM(T.INPEXP_CD) INPEXP_NM , T.NEW_PRDT_CD , FN_CM05M01_CD_TO_NM(T.NEW_PRDT_CD) NEW_PRDT_NM , T.CLNT_CD , A.CLNT_NM , T.CLNT_MNG_NM , T.PRJCT_NM , T.EQP_NM , T.EQP_QTY , T.ODR_CD , T.ODR_RMK , T.EPCT_AMT , T.ORDRS_PLAN_AMT , T.WINBD_RMK , TO_CHAR(T.ORDRS_PLAN_DT, 'YYYY-MM-DD') AS ORDRS_PLAN_DT , T.ORDRS_AMT , T.WINBD_CD , TO_CHAR(T.ORDRS_DT, 'YYYY-MM-DD') AS ORDRS_DT
, TO_CHAR(T.DSGN_DT, 'YYYY-MM-DD') AS DSGN_DT , TO_CHAR(T.PURCHS_DT, 'YYYY-MM-DD') AS PURCHS_DT , TO_CHAR(T.PRDCTN_DT, 'YYYY-MM-DD') AS PRDCTN_DT , TO_CHAR(T.ACPTNC_DT, 'YYYY-MM-DD') AS ACPTNC_DT , TO_CHAR(T.DLIVY_DT, 'YYYY-MM-DD') AS DLIVY_DT
, TO_CHAR(T.DUDT_PLAN_DT, 'YYYY-MM-DD') AS DUDT_PLAN_DT , T.MNG_ID , B.NAME as MNG_NM , FN_CM05M01_CD_TO_NM(T.CO_CD) CO_NM , SUBSTR(T.YYYYMM,1,4) || '-' || SUBSTR(T.YYYYMM,5,2) AS YYYYMM , T.PRJCT_CD , T.PRJCT_RMK , T.CREAT_ID , T.CREAT_PGM , TO_CHAR(T.CREAT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS CREAT_DTTM , T.UDT_ID , T.UDT_PGM , TO_CHAR(T.UDT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS UDT_DTTM , ORDRS_PCT FROM TB_BM06M01 T LEFT OUTER JOIN TB_BM02M01 A ON T.CLNT_CD = A.CLNT_CD LEFT OUTER JOIN TB_CM06M01 B ON T.MNG_ID = B.ID WHERE PRJCT_SEQ = #{prjctSeq}

SELECT COUNT(PRJCT_SEQ) CNT
  FROM TB_BM06M01
 WHERE PRJCT_SEQ = #{prjctSeq}

