mapper.select:
        SELECT *
        FROM TB_CM08M01 T
	            INNER JOIN  TB_CM15M01 G ON T.COMON_CD = G.COMON_CD
       							   AND G.FILE_LIST = 'Y'         
        WHERE FILE_TRGT_TYP = #{fileTrgtType}
          AND FILE_TRGT_KEY = #{fileTrgtKey}
       	  AND G.USER_ID = #{userId}

SELECT COUNT(*) FROM TB_CM08M01 T LEFT OUTER JOIN TB_BM02M01 A ON T.CLNT_CD = A.CLNT_CD LEFT OUTER JOIN TB_BM06M01 C ON T.PRJCT_CD = C.PRJCT_SEQ LEFT OUTER JOIN TB_CM06M01 D ON T.CREAT_ID = D.ID LEFT OUTER JOIN TB_BM01M01 E ON T.PRDT_CD = E.PRDT_CD LEFT OUTER JOIN TB_CM05M01 F ON T.ITEM_CD = F.CODE_ID INNER JOIN TB_CM15M01 G ON T.COMON_CD = G.COMON_CD AND G.USER_ID = #{userId} AND G.FILE_LIST = 'Y' WHERE 1=1
AND T.CO_CD = #{coCd}
AND T.COMON_CD IN ( SELECT CODE_ID ID FROM TB_CM05M01 START WITH CODE_ID = #{comonCd} CONNECT BY PRIOR CODE_ID = CODE_KIND )
AND T.FILE_TRGT_TYP= #{fileTrgtType}
AND T.FILE_TRGT_KEY= #{fileTrgtKey}
AND T.CREAT_ID= #{creatId}
AND D.NAME LIKE '%${creatNm}%'
AND T.CREAT_DTTM BETWEEN to_date(REPLACE(#{reqDtFrom},'-','')) AND to_date(REPLACE(#{reqDtTo},'-',''))+1
AND T.PRDT_CD = #{prdtCd}
AND E.PRDT_NM LIKE '%${prdtNm}%'
AND ${searchValue} LIKE '%${searchName}%'
AND A.CLNT_NM LIKE '%${clntNm}%'

WITH b1 AS ( SELECT CODE_ID ID, CODE_NM TEXT, CODE_KIND PARENT, CODE_DESC PARENT_NM, SORT_NO, USE_YN, LPAD(' ', 3*(LEVEL-1)) || LEVEL lvl, REGEXP_REPLACE(SYS_CONNECT_BY_PATH(CODE_NM, '\'), '^\s+\-\>\s+', '') as LPATH, CONNECT_BY_ISLEAF AS IS_LEAF FROM TB_CM05M01 START WITH CODE_ID ='FILETREE' CONNECT BY PRIOR CODE_ID = CODE_KIND ORDER SIBLINGS BY CODE_ID ASC)
SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT T.CO_CD, T.FILE_KEY, T.FILE_SIZE, T.FILE_TYPE, T.FILE_NAME, T.FILE_PATH, T.FILE_TRGT_TYP, T.FILE_TRGT_KEY, T.USE_YN, T.CREAT_ID, D.NAME CREAT_NM, T.CREAT_PGM, TO_CHAR(T.CREAT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS CREAT_DTTM, T.CLNT_CD, A.CLNT_NM, T.PRDT_CD, E.PRDT_NM, T.ITEM_CD, F.CODE_NM AS ITEM_NM, T.SALES_CD, T.PRJCT_CD, C.PRJCT_NM PRJCT_NM, T.COMON_CD, B1.TEXT AS COMON_NM, B1.LPATH, G.FILE_UP, G.FILE_DOWN, G.FILE_UPDATE, G.FILE_DELETE FROM TB_CM08M01 T left outer join TB_BM02M01 A on T.CLNT_CD = A.CLNT_CD LEFT OUTER JOIN B1 ON T.COMON_CD = B1.ID LEFT OUTER JOIN TB_BM06M01 C ON T.PRJCT_CD = C.PRJCT_SEQ LEFT OUTER JOIN TB_CM06M01 D ON T.CREAT_ID = D.ID LEFT OUTER JOIN TB_BM01M01 E ON T.PRDT_CD = E.PRDT_CD LEFT OUTER JOIN TB_CM05M01 F ON T.ITEM_CD = F.CODE_ID LEFT OUTER JOIN TB_CM15M01 G ON T.COMON_CD = G.COMON_CD AND G.USER_ID = #{userId} WHERE 1=1
AND T.CO_CD = #{coCd}
AND (T.COMON_CD IS NULL OR T.COMON_CD = '')
AND G.FILE_LIST = 'Y' AND T.COMON_CD IN ( SELECT CODE_ID ID FROM TB_CM05M01 START WITH CODE_ID = #{comonCd} CONNECT BY PRIOR CODE_ID = CODE_KIND )
AND T.FILE_TRGT_KEY= #{fileTrgtKey}
AND T.FILE_TRGT_TYP= #{fileTrgtType}
AND T.CREAT_ID= #{creatId}
AND D.NAME LIKE '%${creatNm}%'
AND T.CREAT_DTTM BETWEEN to_date(REPLACE(#{reqDtFrom},'-','')) AND to_date(REPLACE(#{reqDtTo},'-',''))+1
AND T.PRDT_CD = #{prdtCd}
AND E.PRDT_NM LIKE '%${prdtNm}%'
AND ${searchValue} LIKE '%${searchName}%'
AND A.CLNT_NM LIKE '%${clntNm}%'
) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

WITH b1 AS (SELECT CODE_ID ID, CODE_NM TEXT, CODE_KIND PARENT, CODE_DESC PARENT_NM, SORT_NO, USE_YN, LPAD(' ', 3*(LEVEL-1)) || LEVEL lvl, REGEXP_REPLACE(SYS_CONNECT_BY_PATH(CODE_NM, '\'), '^\s+\-\>\s+', '') as LPATH, CONNECT_BY_ISLEAF AS IS_LEAF FROM TB_CM05M01 START WITH CODE_ID ='FILETREE' CONNECT BY PRIOR CODE_ID = CODE_KIND ORDER SIBLINGS BY CODE_ID ASC)
SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT T.CO_CD, T.FILE_KEY, T.FILE_SIZE, T.FILE_TYPE, T.FILE_NAME, T.FILE_PATH, T.FILE_TRGT_TYP, T.FILE_TRGT_KEY, T.USE_YN, T.CREAT_ID, D.NAME CREAT_NM, T.CREAT_PGM, TO_CHAR(T.CREAT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS CREAT_DTTM, T.CLNT_CD, A.CLNT_NM, T.PRDT_CD, E.PRDT_NM, T.ITEM_CD, F.CODE_NM AS ITEM_NM, T.SALES_CD, T.PRJCT_CD, C.PRJCT_NM PRJCT_NM, T.COMON_CD, B1.TEXT AS COMON_NM, B1.LPATH, G.FILE_UP, G.FILE_DOWN, G.FILE_UPDATE, G.FILE_DELETE FROM TB_CM08M01 T left outer join TB_BM02M01 A on T.CLNT_CD = A.CLNT_CD LEFT OUTER JOIN B1 ON T.COMON_CD = B1.ID LEFT OUTER JOIN TB_BM06M01 C ON T.PRJCT_CD = C.PRJCT_SEQ LEFT OUTER JOIN TB_CM06M01 D ON T.CREAT_ID = D.ID LEFT OUTER JOIN TB_BM01M01 E ON T.PRDT_CD = E.PRDT_CD LEFT OUTER JOIN TB_CM05M01 F ON T.ITEM_CD = F.CODE_ID LEFT OUTER JOIN TB_CM15M01 G ON T.COMON_CD = G.COMON_CD AND G.USER_ID = #{userId} WHERE 1=1 AND T.FILE_TRGT_KEY= #{fileTrgtKey}
AND (T.COMON_CD IS NULL OR T.COMON_CD = '')
AND G.FILE_LIST = 'Y' AND T.COMON_CD IN ( SELECT CODE_ID ID FROM TB_CM05M01 START WITH CODE_ID = #{comonCd} CONNECT BY PRIOR CODE_ID = CODE_KIND )
AND T.CO_CD LIKE #{coCd}
AND T.FILE_TRGT_TYP= #{fileTrgtType}
AND T.CREAT_ID= #{creatId}
AND D.NAME LIKE '%${creatNm}%'
AND T.CREAT_DTTM BETWEEN to_date(REPLACE(#{reqDtFrom},'-','')) AND to_date(REPLACE(#{reqDtTo},'-',''))+1
AND T.PRDT_CD = #{prdtCd}
AND E.PRDT_NM LIKE '%${prdtNm}%'
AND ${searchValue} LIKE '%${searchName}%'
AND A.CLNT_NM LIKE '%${clntNm}%'
) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT COUNT(*) CNT
FROM TB_CM08M01 A
    LEFT INNER JOIN TB_CM15M01 B
ON A.COMON_CD = B.COMON_CD
    AND B.USER_ID = #{userId, jdbcType=VARCHAR}
WHERE FILE_KEY = #{fileKey}

mapper.update:
UPDATE
    TB_CM08M01
SET COMON_CD = #{comonCd}
WHERE FILE_KEY = #{fileKey}
