mapper.select:
SELECT * FROM ( SELECT X.* , ROWNUM AS RN FROM ( SELECT X.FILE_TRGT_KEY , TO_CHAR(X.REQ_DTTM, 'YYYY-MM-DD HH24:MI') AS REQ_DTTM , X.REQ_DEPT , C.DEPT_NM AS REQ_DEPT_NM , X.REQ_ID , (SELECT FN_CM06M01_ID_TO_NM(X.REQ_ID) FROM DUAL) AS REQ_ID_NM , X.REQ_TYPE , X.ISSUE , X.ACT_CD , TO_CHAR(X.ACT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS ACT_DTTM , X.ACT_ID , X.ACT_PROC_TM , X.ACT_DESC , X.RMK , X.CREAT_ID , (SELECT FN_CM06M01_ID_TO_NM(X.CREAT_ID) FROM DUAL) AS CREAT_ID_NM , X.CREAT_PGM , TO_CHAR(X.CREAT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS CREAT_DTTM , X.UDT_ID AS UDT_ID -- , (SELECT FN_CM06M01_ID_TO_NM(X.UDT_ID) FROM DUAL) AS UDT_ID_NM , X.UDT_PGM AS UDT_PGM -- , TO_CHAR(X.UDT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS UDT_DTT -- FROM TB_CM16M01 X LEFT OUTER JOIN TB_CM04M01 C ON X.REQ_DEPT = C.DEPT_ID WHERE 1 = 1
AND REQ_DTTM BETWEEN to_date(#{reqDtFrom},'YYYY-MM-DD') AND to_date(#{reqDtTo} ,'YYYY-MM-DD')
) X ) WHERE RN BETWEEN ${firstIndex} AND ${lastIndex}

SELECT COUNT(FILE_TRGT_KEY) CNT
FROM TB_PM01M01
WHERE FILE_TRGT_KEY = #{fileTrgtKey}
