mapper.select:
SELECT COUNT(*)
	FROM TB_CM16M01
	WHERE 1 = 1

		AND CO_CD = #{coCd}


	AND REQ_DTTM BETWEEN to_date(#{reqDtFrom},'YYYY-MM-DD') AND to_date(#{reqDtTo} ,'YYYY-MM-DD')


	AND REQ_ID IN ( ${reqId} )


	AND C6.NAME LIKE '%'||#{reqIdNm}||'%'


	AND ACT_ID = #{actId}


	AND NAME = #{actIdNm}


	AND ACT_CD = #{actCd}


	AND REQ_TYPE = #{reqTypeNm}


	AND REQ_TYPE2 = #{reqType2Nm}

SELECT * FROM ( SELECT X.* , ROWNUM AS RN FROM ( SELECT X.FILE_TRGT_KEY , X.CO_CD , FN_CM05M01_CD_TO_NM(X.CO_CD) CO_NM , TO_CHAR(X.REQ_DTTM, 'YYYY-MM-DD HH24:MI') AS REQ_DTTM , C4.DEPT_NM AS REQ_DEPT , X.REQ_ID , C6.NAME AS REQ_ID_NM , X.REQ_TYPE , A.CODE_NM AS REQ_TYPE_NM , X.REQ_TYPE2 , D.CODE_NM AS REQ_TYPE2_NM , X.ISSUE , X.ACT_CD , B.CODE_NM AS ACT_CD_NM , TO_CHAR(X.ACT_DTTM, 'YYYY-MM-DD HH24:MI') AS ACT_DTTM , X.ACT_ID , (SELECT FN_CM06M01_ID_TO_NM(X.ACT_ID) FROM DUAL) AS ACT_ID_NM , X.ACT_PROC_TM , X.ACT_DESC , X.RMK , X.CREAT_ID , (SELECT FN_CM06M01_ID_TO_NM(X.CREAT_ID) FROM DUAL) AS CREAT_ID_NM , X.CREAT_PGM , TO_CHAR(X.CREAT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS CREAT_DTTM , X.UDT_ID AS UDT_ID , (SELECT FN_CM06M01_ID_TO_NM(X.UDT_ID) FROM DUAL) AS UDT_ID_NM , X.UDT_PGM AS UDT_PGM , TO_CHAR(X.UDT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS UDT_DTT FROM TB_CM16M01 X LEFT OUTER JOIN TB_CM05M01 A ON X.REQ_TYPE = A.CODE_ID LEFT OUTER JOIN TB_CM05M01 D ON X.REQ_TYPE2 = D.CODE_ID LEFT OUTER JOIN TB_CM06M01 C6 ON X.REQ_ID = C6.ID LEFT OUTER JOIN TB_CM05M01 B ON X.ACT_CD = B.CODE_ID LEFT OUTER JOIN TB_CM06M01 E ON X.ACT_ID = E.ID LEFT OUTER JOIN TB_CM04M01 C4 ON C6.DEPT_ID = C4.DEPT_ID WHERE 1 = 1
AND X.CO_CD = #{coCd}
AND REQ_DTTM BETWEEN to_date(#{reqDtFrom},'YYYY-MM-DD') AND to_date(#{reqDtTo} ,'YYYY-MM-DD')
AND X.REQ_ID IN ( ${reqId} )
AND C6.NAME LIKE '%'||#{reqIdNm}||'%'
AND X.ACT_ID = #{actId}
AND E.NAME = #{actIdNm}
AND X.ACT_CD = #{actCd}
AND X.REQ_TYPE = #{reqTypeNm}
AND X.REQ_TYPE2 = #{reqType2Nm}
ORDER BY CASE WHEN X.ACT_CD = 'ISSSTS02' THEN -1 WHEN X.ACT_CD = 'ISSSTS03' THEN 1 ELSE 0 END, X.REQ_DTTM DESC, C4.DEPT_NM, X.REQ_ID ) X ) WHERE RN BETWEEN ${firstIndex} AND ${lastIndex}

SELECT COUNT(FILE_TRGT_KEY) CNT
FROM TB_CM16M01
WHERE FILE_TRGT_KEY = #{fileTrgtKey}

mapper.insert:
INSERT INTO TB_CM16M01
	(
		FILE_TRGT_KEY
		, CO_CD
		, REQ_DTTM
		, REQ_ID
		, REQ_TYPE
		, REQ_TYPE2
		, ISSUE
		, ACT_CD
		, ACT_DTTM
		, ACT_ID
		, ACT_PROC_TM
		, ACT_DESC
		, RMK
		, CREAT_ID
		, CREAT_PGM
		, CREAT_DTTM
	)
VALUES
	(
		#{fileTrgtKey}
		, #{coCd}
		, TO_DATE(REPLACE(#{reqDttm}, 'T', ' '), 'YYYY-MM-DD HH24:MI')
		, #{reqId}
		, #{reqType, jdbcType=VARCHAR}
		, #{reqType2, jdbcType=VARCHAR}
		, #{issue, jdbcType=VARCHAR}
		, #{actCd, jdbcType=VARCHAR}
		, TO_DATE(REPLACE(#{actDttm, jdbcType=VARCHAR}, 'T', ' '), 'YYYY-MM-DD HH24:MI')
		, #{actId}
		, #{actProcTm}
		, #{actDesc, jdbcType=VARCHAR}
		, #{rmk, jdbcType=VARCHAR}
		, #{userId}
		, #{pgmId}
		, SYSDATE
	)

mapper.update:
UPDATE TB_CM16M01
SET   FILE_TRGT_KEY     = #{fileTrgtKey}
	, CO_CD             = #{coCd}
	, REQ_DTTM          = TO_DATE(REPLACE(#{reqDttm}, 'T', ' '), 'YYYY-MM-DD HH24:MI')
	, REQ_ID            = #{reqId}
	, REQ_TYPE          = #{reqType, jdbcType=VARCHAR}
	, REQ_TYPE2         = #{reqType2, jdbcType=VARCHAR}
	, ISSUE             = #{issue}
	, ACT_CD            = #{actCd, jdbcType=VARCHAR}
	, ACT_DTTM          = TO_DATE(REPLACE(#{actDttm}, 'T', ' '), 'YYYY-MM-DD HH24:MI')
	, ACT_ID            = #{actId}
	, ACT_PROC_TM       = #{actProcTm}
	, ACT_DESC          = #{actDesc}
	, RMK               = #{rmk, jdbcType=VARCHAR}
	, UDT_ID            = #{userId}
	, UDT_PGM           = #{pgmId}
	, UDT_DTTM          = SYSDATE
WHERE FILE_TRGT_KEY = #{fileTrgtKey}

UPDATE TB_CM16M01
SET   ACT_CD            = 'ISSSTS02'
	, UDT_ID            = #{userId}
	, UDT_PGM           = #{pgmId}
	, UDT_DTTM          = SYSDATE
WHERE FILE_TRGT_KEY = #{issNo}

mapper.select:
SELECT DISTINCT REQ_SELECT_ID AS CODE , REQ_SELECT_NM AS NAME FROM ( SELECT X.REQ_ID AS REQ_SELECT_ID, C6.NAME AS REQ_SELECT_NM FROM TB_CM16M01 X LEFT OUTER JOIN TB_CM06M01 C6 ON X.REQ_ID = C6.ID WHERE 1 = 1
AND X.CO_CD = #{coCd}
AND X.REQ_DTTM BETWEEN TO_DATE(#{reqDtFrom}, 'YYYY-MM-DD') AND TO_DATE(#{reqDtTo}, 'YYYY-MM-DD')
AND C6.NAME LIKE '%'||#{reqIdNm}||'%'
) A
