SELECT count(*) FROM ( SELECT A.* , ROWNUM AS RN FROM ( SELECT A.FILE_TRGT_KEY --파일대상KEY , A.CO_CD --회사코드 , R.CODE_NM AS CO_NM , A.MATR_CD --자재코드 , A.MATR_NM --자재명 , A.GOODS_DIV --제품/자재구분 , R1.CODE_NM AS GOODS_DIV_NM , A.MATR_CLNT_DIV --거래처분류 , R2.CODE_NM AS MATR_CLNT_DIV_NM , A.MATR_DIV --자재유형 , R3.CODE_NM AS MATR_DIV_NM , A.MATR_GRP --자재분류-자재그룹 , R4.CODE_NM AS MATR_GRP_NM , A.MATR_MAT --소재 , A.MATR_SIZE --크기(사이즈) , A.MATR_SPEC --규격 , A.MATR_MAKR_NM --제조사(MAKER) , A.MATR_MNO --형번(Model No) , A.MATR_WT --중량 , A.MATR_UNIT --단위 , R5.CODE_NM AS MATR_UNIT_NM , A.ORIGIN_NM --원산지 , A.MATR_DRW_NO --대표도면 , A.MATR_TEST_DIV --입고검사구분 , R6.CODE_NM AS MATR_TEST_DIV_NM , A.DLVR_RQM_DAY --리드타임(납품소요일자) , A.MATR_PURC_DIV --구매/외주구분 , R7.CODE_NM AS MATR_PURC_DIV_NM , A.MIN_ORDRG_QTY --최소발주량 , A.MATR_SAFT_QTY --안전재고 , A.MATR_STOCK_CD --재고관리여부 , A.DSGN_2D_MD --2D설계소요일 , A.AVRG_2D_MD --2D평균소요일 , A.DSGN_3D_MD --3D설계소요일 , A.AVRG_3D_MD --3D평균소요일 , A.PRDCTN_RQM_MD --생산소요일 , A.PRDCTN_AVRG_MD --생산평균소요일 , A.USE_YN --사용여부 , A.MATR_PART_NO --파트번호 , A.PCHS_CLNT_CD1 --구매업체1 , C1.CLNT_NM AS PCHS_CLNT_NM1 , A.PCHS_CLNT_PCT1 --구매업체1비율 , A.PCHS_CLNT_CD2 --구매업체2 , C2.CLNT_NM AS PCHS_CLNT_NM2 , A.PCHS_CLNT_PCT2 --구매업체2비율 , A.PCHS_CLNT_CD3 --구매업체3 , C3.CLNT_NM AS PCHS_CLNT_NM3 , A.PCHS_CLNT_PCT3 --구매업체3비율 , A.MATR_UPR --최종구매단가 , A.MATR_AVRG_UPR --평균구매단가 , A.MATR_RMK --비고 , TO_CHAR(A.MATR_LAST_TRST_DT, 'YYYY-MM-DD') AS MATR_LAST_TRST_DT --최종거래일자 , A.MATR_ATNT_CD --대체자재코드 , A.MATR_NO --자재번호 , A.DSGN_TRGT_YN --설계대상구분 , A.ETC_FIELD1 --여분필드(고정) , A.ETC_FIELD2 --여분필드(숫자) , A.ETC_FIELD3 --여분필드(가변) , A.CREAT_ID --생성자 , U1.NAME AS CREAT_NM , A.CREAT_PGM --생성프로그램ID , TO_CHAR(A.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM --생성일 , A.UDT_ID --변경자 , U2.NAME AS UDT_NM , A.UDT_PGM --최종수정프로그램ID , TO_CHAR(A.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM --변경일 FROM TB_BM05M01 AS A LEFT OUTER JOIN TB_CM05M01 AS R ON R.CODE_ID = A.CO_CD --회사 LEFT OUTER JOIN TB_CM05M01 AS R1 ON R1.CODE_ID = A.GOODS_DIV --제품/자재구분 LEFT OUTER JOIN TB_CM05M01 AS R2 ON R2.CODE_ID = A.MATR_CLNT_DIV --거래처분류 LEFT OUTER JOIN TB_CM05M01 AS R3 ON R3.CODE_ID = A.MATR_DIV --자재유형 LEFT OUTER JOIN TB_CM05M01 AS R4 ON R4.CODE_ID = A.MATR_GRP --자재분류-자재그룹 LEFT OUTER JOIN TB_CM05M01 AS R5 ON R5.CODE_ID = A.MATR_UNIT --단위 LEFT OUTER JOIN TB_CM05M01 AS R6 ON R6.CODE_ID = A.MATR_TEST_DIV --입고검사구분 LEFT OUTER JOIN TB_CM05M01 AS R7 ON R7.CODE_ID = A.MATR_PURC_DIV --구매/외주구분 LEFT OUTER JOIN TB_BM02M01 AS C1 ON C1.CLNT_CD = A.PCHS_CLNT_CD1 --구매업체1 LEFT OUTER JOIN TB_BM02M01 AS C2 ON C2.CLNT_CD = A.PCHS_CLNT_CD2 --구매업체2 LEFT OUTER JOIN TB_BM02M01 AS C3 ON C3.CLNT_CD = A.PCHS_CLNT_CD3 --구매업체3 LEFT OUTER JOIN TB_CM06M01 AS U1 ON U1.ID = A.CREAT_ID --생성자 LEFT OUTER JOIN TB_CM06M01 AS U2 ON U2.ID = A.UDT_ID --변경자 WHERE 1 = 1
AND A.CO_CD = 'GUN'
AND A.GOODS_DIV = #{goodsDiv}
AND A.MATR_DIV = #{matrDiv}
AND A.MATR_GRP = #{matrGrp}
AND REPLACE(REPLACE(A.MATR_CD, '-', ''), ' ', '') LIKE '%'|| REPLACE(REPLACE(UPPER(#{matrCd}), '-', ''), ' ', '')||'%'
AND REPLACE(REPLACE(A.MATR_NM, '-', ''), ' ', '') LIKE '%'|| REPLACE(REPLACE(UPPER(#{matrNm}), '-', ''), ' ', '')||'%'
AND REPLACE(REPLACE(A.MATR_MNO, '-', ''), ' ', '') LIKE '%'|| REPLACE(REPLACE(UPPER(#{matrMno}), '-', ''), ' ', '')||'%'
AND REPLACE(REPLACE(A.MATR_SPEC, '-', ''), ' ', '') LIKE '%'|| REPLACE(REPLACE(UPPER(#{matrSpec}), '-', ''), ' ', '')||'%'
) AS A WHERE 1 = 1 ORDER BY GOODS_DIV_NM,MATR_GRP_NM, MATR_DIV_NM,MATR_CLNT_DIV_NM, A.MATR_CD ) AS A WHERE 1 = 1

