mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RN, -- No. -- 자재마스터 TB_BM05M01 AS X -- 공통에서 가져와야하는 컬럼 검색 시 사용 TB_CM05M01 X.FILE_TRGT_KEY, -- 파일대상KEY X.CO_CD, -- 회사코드 (SELECT T.CODE_NM FROM TB_CM05M01 T WHERE T.CODE_ID = X.CO_CD) AS CO_CD_NM, X.GOODS_DIV, -- 품목구분 (SELECT T.CODE_NM FROM TB_CM05M01 T WHERE T.CODE_ID = X.GOODS_DIV) AS GOODS_DIV_NM, X.MATR_DIV, -- 자재유형 (SELECT T.CODE_NM FROM TB_CM05M01 T WHERE T.CODE_ID = X.MATR_DIV) AS MATR_DIV_NM, X.MATR_GRP, -- 자재그룹 (SELECT T.CODE_NM FROM TB_CM05M01 T WHERE T.CODE_ID = X.MATR_GRP) AS MATR_GRP_NM, X.MATR_CD, -- 품번 X.MATR_NM, -- 품명 X.MATR_CLNT_DIV, -- 거래처분류 (SELECT T.CODE_NM FROM TB_CM05M01 T WHERE T.CODE_ID = X.MATR_CLNT_DIV) AS MATR_CLNT_DIV_NM, X.MATR_MAT, -- 재질 X.MATR_MAKR_NM, -- Maker X.MATR_MNO, -- 형번 X.MATR_SPEC, -- 규격 X.MATR_SIZE, -- 크기 X.MATR_TEST_DIV, -- 입고검사구분 (SELECT T.CODE_NM FROM TB_CM05M01 T WHERE T.CODE_ID = X.MATR_TEST_DIV) AS MATR_TEST_DIV_NM, X.DLVR_RQM_DAY, -- 리드타임 -> 19번 납품소요일자 X.MIN_ORDRG_QTY, -- 최소발주량 X.MATR_SAFT_QTY, -- 안전재고 X.MATR_WT, -- 중량 X.MATR_UNIT, -- 관리단위 (SELECT T.CODE_NM FROM TB_CM05M01 T WHERE T.CODE_ID = X.MATR_UNIT) AS MATR_UNIT_NM FROM TB_BM05M01 X ) Y WHERE RN BETWEEN #{firstIndex} AND #{lastIndex}
AND ${searchType} LIKE '%${searchValue}%'
AND Y.MATR_CD = #{searchValue}
AND Y.MATR_CD LIKE '%'||'${matrCd}'||'%' --자재코드 품번
AND A.MATR_NM LIKE '%${matrNm}%'
