mapper.select:
/* No. 내림차순 */ SELECT * FROM ( SELECT X.* , ROWNUM AS RN FROM ( SELECT CO_CD , APP_NO /* 결재선 번호 */ , APP_SEQ /* 결재 순번 */ , ID /* 작성자 ID */ , ( SELECT NAME FROM TB_CM06M01 B WHERE A.ID = B.ID ) AS USER_NAME /* 작성자 이름 */ , A.APP_DIV /* 결재선 구분 */ , FN_CM05M01_CD_TO_NM(A.APP_DIV) AS APP_DIV_NM /* 결재선구분 명 */ , APP_LINE_NM /* 결재자명 */ , APP_ID /* 결재자 id */ , B.APP_USER_NM /* 결재자명 */ , USE_YN /*사용여부 */ , B.DEPT_NM /* 부서명 */ , (SELECT D.LEVEL_NM FROM TB_CM07M01 D WHERE D.LEVEL_CD=B.LEVEL_CD) AS LEVEL_NM /* 직급 */ , B.EMAIL /* E-mail */ , B.TEL_NO /* 전화번호 */ , B.OFF_TEL_NO /* 회사번호 */ , B.FAX_NO /* fax번호 */ , PG_PATH , ETC_FIELD1 , ETC_FIELD2 , ETC_FIELD3 , CREAT_ID , CREAT_PGM , NVL(TO_CHAR(UDT_DTTM, 'YYYY-MM-DD'), TO_CHAR(CREAT_DTTM, 'YYYY-MM-DD')) AS CREAT_DTTM , UDT_ID , UDT_PGM , UDT_DTTM FROM TB_BM13M01 A LEFT OUTER JOIN ( SELECT BB.ID AS USER_ID , BB.NAME AS APP_USER_NM , BB.LEVEL_CD , BB.EMAIL , BB.TEL_NO , BB.OFF_TEL_NO , BB.FAX_NO , CC.DEPT_NM FROM TB_CM06M01 BB INNER JOIN TB_CM04M01 CC ON(BB.DEPT_ID=CC.DEPT_ID) WHERE 1=1 ) B ON(A.APP_ID=B.USER_ID) WHERE 1=1
AND APP_ID = ( SELECT DD.ID FROM TB_CM06M01 DD WHERE DD.NAME = #{appName, jdbcType=VARCHAR} )
ORDER BY CO_CD DESC, APP_NO DESC, APP_DIV DESC, ID DESC, CREAT_DTTM DESC, UDT_DTTM DESC ) X ) WHERE 1=1 AND RN BETWEEN ${firstIndex} AND ${lastIndex}

/* No. 내림차순 */ SELECT * FROM ( SELECT X.* , ROWNUM AS RN FROM ( SELECT CO_CD , APP_NO /* 결재선 번호 */ , APP_SEQ /* 결재 순번 */ , ID /* 작성자 ID */ , ( SELECT NAME FROM TB_CM06M01 B WHERE A.ID = B.ID ) AS USER_NAME /* 작성자 이름 */ , A.APP_DIV /* 결재선 구분 */ , FN_CM05M01_CD_TO_NM(A.APP_DIV) AS APP_DIV_NM /* 결재선구분 명 */ , APP_LINE_NM /* 결재선명 */ , APP_ID /* 결재자 id */ , B.APP_USER_NM /* 결재자명 */ , USE_YN /*사용여부 */ , B.DEPT_NM /* 부서명 */ , (SELECT D.LEVEL_NM FROM TB_CM07M01 D WHERE D.LEVEL_CD=B.LEVEL_CD) AS LEVEL_NM /* 직급 */ , B.EMAIL /* E-mail */ , B.TEL_NO /* 전화번호 */ , B.OFF_TEL_NO /* 회사번호 */ , B.FAX_NO /* fax번호 */ , PG_PATH , ETC_FIELD1 , ETC_FIELD2 , ETC_FIELD3 , CREAT_ID , CREAT_PGM , NVL(TO_CHAR(UDT_DTTM, 'YYYY-MM-DD'), TO_CHAR(CREAT_DTTM, 'YYYY-MM-DD')) AS CREAT_DTTM , UDT_ID , UDT_PGM , UDT_DTTM FROM TB_BM13M01 A LEFT OUTER JOIN ( SELECT BB.ID AS USER_ID , BB.NAME AS APP_USER_NM , BB.LEVEL_CD , BB.EMAIL , BB.TEL_NO , BB.OFF_TEL_NO , BB.FAX_NO , CC.DEPT_NM FROM TB_CM06M01 BB INNER JOIN TB_CM04M01 CC ON(BB.DEPT_ID=CC.DEPT_ID) WHERE 1=1 ) B ON(A.APP_ID=B.USER_ID) WHERE 1=1
AND APP_ID = ( SELECT DD.ID FROM TB_CM06M01 DD WHERE DD.NAME = #{appName, jdbcType=VARCHAR} )
ORDER BY CO_CD DESC, APP_NO DESC, APP_DIV DESC, ID DESC, CREAT_DTTM DESC, UDT_DTTM DESC ) X ) WHERE 1=1 /* 엑셀은 전체 조회 */

mapper.insert:

    INSERT INTO TB_BM13M01
      (
		CO_CD
		, APP_NO
		, APP_SEQ
		, ID
		, APP_DIV
		, APP_LINE_NM
		, APP_ID
		, USE_YN
		, PG_PATH
		, ETC_FIELD1
		, ETC_FIELD2
		, ETC_FIELD3
		, CREAT_ID
		, CREAT_PGM
		, CREAT_DTTM    
      )
    VALUES
    (
		#{coCd}
		, #{selAppNo}
		, #{appSeq}
		, #{userId}
		, #{appDiv}
		, #{appLineNm}
		, #{appId}
		, #{useYn}
		, #{pgPath,  jdbcType=VARCHAR}
		, #{etcField1,  jdbcType=VARCHAR}
		, #{etcField2,  jdbcType=VARCHAR}
		, #{etcField3,  jdbcType=VARCHAR}
		, #{userId}
		, #{pgmId}
		, SYSDATE  
    )

mapper.update:
MERGE INTO TB_BM13M01 A
	USING DUAL
	ON (
	A.CO_CD = #{coCd}
	AND A.APP_NO = #{appNo}
	AND A.APP_SEQ = #{appSeq}
		)
WHEN MATCHED THEN
	UPDATE SET	
		ID						= #{userId}
		, APP_DIV					= #{appDiv}
		, APP_LINE_NM				= #{appLineNm}
		, APP_ID					= #{appId}	
		, USE_YN					= #{useYn}	
		, PG_PATH					= #{pgPath,  jdbcType=VARCHAR}
		, ETC_FIELD1				= #{etcField1,  jdbcType=VARCHAR}
		, ETC_FIELD2				= #{etcField2,  jdbcType=VARCHAR}
		, ETC_FIELD3				= #{etcField3,  jdbcType=VARCHAR}
		, UDT_ID					= #{userId}
		, UDT_PGM					= #{pgmId}	
		, UDT_DTTM					= SYSDATE
WHEN NOT MATCHED THEN
    INSERT 
      (
		CO_CD
		, APP_NO
		, APP_SEQ
		, ID
		, APP_DIV
		, APP_LINE_NM
		, APP_ID
		, USE_YN
		, PG_PATH
		, ETC_FIELD1
		, ETC_FIELD2
		, ETC_FIELD3
		, CREAT_ID
		, CREAT_PGM
		, CREAT_DTTM    
      )
    VALUES
    (
		#{coCd}
		, #{appNo}
		, #{appSeq}
		, #{userId}
		, #{appDiv}
		, #{appLineNm}
		, #{appId}
		, #{useYn}
		, #{pgPath,  jdbcType=VARCHAR}
		, #{etcField1,  jdbcType=VARCHAR}
		, #{etcField2,  jdbcType=VARCHAR}
		, #{etcField3,  jdbcType=VARCHAR}
		, #{userId}
		, #{pgmId}
		, SYSDATE  
    )

mapper.delete:
DELETE 
	FROM TB_BM13M01
WHERE 1=1
	AND CO_CD = #{coCd}
	AND APP_NO = #{appNo}
	AND APP_SEQ = #{appSeq}
