mapper.select:
SELECT * FROM ( SELECT X.* , ROWNUM AS RN FROM ( SELECT A.MESSAGE_ID --수신번호 , A.RCV_ID --사용자아이디 , A.RCV_NM --사용자이름 , A.CLNT_CD --거래처 , C.CLNT_NM --거래처명 , B.NAME AS MNG_NM --담당자명 , A.TMPLAT_DIV --발송구분 , (SELECT FN_CM05M01_CD_TO_NM(A.TMPLAT_DIV) FROM DUAL) AS TMPLAT_DIV_NM --발송구분명 , A.SENDG_STATUS --발송상태 --, (SELECT FN_CM05M01_CD_TO_NM(A.SENDG_STATUS) FROM DUAL) AS SENDG_STATUS_NM --발송상태 , CASE WHEN(A.SENDG_STATUS='OK') THEN '성공' ELSE A.SENDG_STATUS END AS SENDG_STATUS_CHK -- 정상코드면 '성공' 아니면 에러코드 표시 , CASE WHEN(A.SENDG_STATUS='OK') THEN 'Y' ELSE 'N' END AS SENDG_STATUS_YN -- 정상코드면 'Y' 아니면 N , A.TITLE --메시지제목 , A.MESSAGE --알림톡메시지 , A.MOBILE --연락처 , A.NAME_TO --받는사람 , NVL(TO_CHAR(A.SEND_DT, 'YYYY-MM-DD HH24:MI:SS'), TO_CHAR(A.SEND_DT, 'YYYY-MM-DD HH24:MI:SS')) AS SEND_DT --보낸일자 , A.CREAT_ID --생성자 , A.CREAT_PGM --생성프로그램ID , NVL(TO_CHAR(A.CREAT_DTTM, 'YYYY-MM-DD HH24:MI:SS'), TO_CHAR(A.CREAT_DTTM, 'YYYY-MM-DD HH24:MI:SS')) AS CREAT_DTTM --생성일시 , A.UDT_ID --최종변경자 , A.UDT_PGM --최종수정프로그램ID , TO_CHAR(A.UDT_DTTM, 'YYYY-MM-DD') UDT_DTTM --최종변경일시 FROM TB_BM18M01 A LEFT OUTER JOIN TB_BM02M01 AS C ON A.CLNT_CD = C.CLNT_CD --거래처정보 LEFT OUTER JOIN TB_CM06M01 AS B ON A.CREAT_ID = B.ID --발송자 정보 WHERE 1=1 ORDER BY CREAT_DTTM DESC ) X WHERE 1=1
AND X.TMPLAT_DIV = #{tmplatDiv}
AND X.CLNT_NM LIKE '%' || #{clntNm} || '%'
AND X.SENDG_STATUS_YN = #{sendgStatusYn}
AND X.NAME_TO LIKE '%' || #{nameTo} || '%'
AND X.MOBILE LIKE '%' || #{mobile} || '%'
) WHERE 1=1 AND RN BETWEEN ${firstIndex} AND ${lastIndex}

SELECT 'KKO'||TO_CHAR(SYSDATE, 'YY') || LPAD(TB_SM18M01_SQ01.NEXTVAL, 6, 0) MAX_MESSAGE_ID , T.* FROM ( SELECT MSSAGE_DESC AS MESSAGE_DESC , A.CLNT_CD
/* 발주서일 경우 거래처 D 사업부순번 1번의 전화번호를 GET */
, B.MNG_NM , B.MNG_MOBLPHON_NO AS MNG_MOBLPHON_NO
FROM ( SELECT MSSAGE_DESC AS MSSAGE_DESC , #{clntCd} AS CLNT_CD FROM TB_BM17M01 WHERE 1=1
AND TMPLAT_DIV = #{tmplatDiv}
AND FILE_TRGT_KEY = #{fileTrgtKey}
) A
/* 발주서일 경우 거래처 D 사업부순번 1번의 전화번호를 GET */
LEFT OUTER JOIN ( SELECT MNG_NM , MNG_MOBLPHON_NO , CLNT_CD FROM TB_BM02D01 WHERE 1=1 AND CLNT_CD = #{clntCd} AND BIZDEPT_SN = 1 ) B ON(A.CLNT_CD=B.CLNT_CD)
) T

--selectMaxMessageIdTodo SELECT 'KKO'||TO_CHAR(SYSDATE, 'YY') || LPAD(TB_SM18M01_SQ01.NEXTVAL, 6, 0) MAX_MESSAGE_ID , T.* FROM ( SELECT A.TODO_ID , A.NAME , A.TODO_TITL , A.TEL_NO , A.TODO_NO , A.SANCTN_SN /* , OLD 'KAKAO'||TO_NUMBER( SUBSTR(MAX_MESSAGE_ID, -8)+A.SANCTN_SN)||A.DIV_NUM AS MAX_MESSAGE_ID */ /* , 'KKO'||A.DIV_GB||TO_NUMBER( SUBSTR(MAX_MESSAGE_ID, -8)+A.SANCTN_SN) AS MAX_MESSAGE_ID */ /* , 'KKO'||TO_NUMBER( SUBSTR(MAX_MESSAGE_ID, -8)+A.RN) AS MAX_MESSAGE_ID */ , B.MSSAGE_DESC AS MESSAGE_DESC , TO_CHAR(A.SEND_DT, 'YYYY-MM-DD HH24:MI') AS SEND_DT , A.TODO_CF_OPN --2023.12.05 남장섭 수정 FROM ( SELECT B.TODO_ID , A.TEL_NO AS TEL_NO /*수신인전화번호 */ , B.TODO_NO , B.SANCTN_SN , B.TODO_TITL , A.NAME , DECODE(B.TODO_DIV1_CODE_ID, 'TODODIV10', 'S', 'TODODIV20', 'A') AS DIV_GB , B.CREAT_DTTM AS SEND_DT , ROWNUM AS RN , B.TODO_CF_OPN --2023.12.05 남장섭 수정 FROM TB_CM06M01 A LEFT OUTER JOIN TB_WB20M03 B ON(A.ID=B.TODO_ID) WHERE 1=1
AND B.TODO_CO_CD = #{coCd}
AND B.TODO_DIV1_CODE_ID = #{todoDiv1CodeId}
AND B.TODO_DIV2_CODE_ID = #{todoDiv2CodeId}
AND B.TODO_NO = #{todoNo}
AND B.ETC_FIELD2 = #{histNo}
/* 결재완료일 경우 결재 상신자(1번)에게 보냄 */ AND B.SANCTN_SN = #{sanctnSn}
/* 발주서 알림톡재발송 버튼클릭시 */ AND B.SANCTN_STTUS = 'N'
) A LEFT OUTER JOIN ( SELECT MAX(MSSAGE_DESC) AS MSSAGE_DESC , #{todoNo} AS TODO_NO /* 이곳에 관리번호 */ FROM ( SELECT MSSAGE_DESC AS MSSAGE_DESC FROM TB_BM17M01 WHERE 1=1
AND TMPLAT_DIV = #{tmplatDiv}
) ) B ON(A.TODO_NO=B.TODO_NO) ) T

mapper.insert:

