mapper.select:
SELECT count(*) FROM ( SELECT A.* , ROWNUM AS RN FROM ( SELECT A.FILE_TRGT_KEY , A.CO_CD , R.CODE_NM AS CO_NM , A.EST_NO , A.EST_DEG , TO_CHAR(A.PBLS_DT, 'YYYY-MM-DD') AS PBLS_DT , A.EST_CLNT_CD , C.CLNT_NM AS EST_CLNT_NM , A.CLNT_PJT , FN_CM05M01_CD_TO_NM(A.CLNT_PJT) AS CLNT_PJT_NM , TO_CHAR(A.EST_DT, 'YYYY-MM-DD') AS EST_DT , TO_CHAR(A.ESTS_DT, 'YYYY-MM-DD') AS ESTS_DT , TO_CHAR(A.ESTE_DT, 'YYYY-MM-DD') AS ESTE_DT , A.CSTM_MNG_NM , A.CSTM_MNG_HP , A.MNG_ID , U3.NAME AS MNG_ID_NM , A.EST_NM , A.CURR_CD , R1.CODE_NM AS CURR_NM , NVL(A.EXRATE, 1) AS EXRATE , NVL(A.EST_AMT, 0) AS EST_AMT , NVL(A.EST_NEGO_AMT, 0) AS EST_NEGO_AMT , A.EST_EXCEPT , A.EST_STD , A.EST_DLVR_CNDT , A.EST_PMNT_CNDT , A.EST_DUDT , NVL(A.ORDRS_YN, 'N') AS ORDRS_YN , TO_CHAR(A.ORDRS_DT, 'YYYY-MM-DD') AS ORDRS_DT , A.EST_RMK , U1.NAME AS CREAT_ID_NM , TO_CHAR(A.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM , U2.NAME AS UDT_ID_NM , TO_CHAR(A.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM , A.CREAT_ID , A.UDT_ID , A.CREAT_PGM , A.UDT_PGM , A.ETC_FIELD1 , A.ETC_FIELD2 , A.ETC_FIELD3 FROM TB_CR01M01 AS A --고객사 LEFT OUTER JOIN TB_BM02M01 AS C ON C.CLNT_CD = A.EST_CLNT_CD --회사 LEFT OUTER JOIN TB_CM05M01 AS R ON R.CODE_ID = A.CO_CD --통화단위 LEFT OUTER JOIN TB_CM05M01 AS R1 ON R1.CODE_ID = A.CURR_CD --생성자 LEFT OUTER JOIN TB_CM06M01 AS U1 ON U1.ID = A.CREAT_ID --변경자 LEFT OUTER JOIN TB_CM06M01 AS U2 ON U2.ID = A.UDT_ID --담당자 LEFT OUTER JOIN TB_CM06M01 AS U3 ON U3.ID = A.MNG_ID WHERE 1 = 1
AND A.CO_CD = #{coCd}
AND A.PBLS_DT BETWEEN TO_DATE( #{strtDt},'YYYYMMDD') AND TO_DATE( #{endDt},'YYYYMMDD')
AND A.EST_CLNT_CD = #{ordrsClntCd}
AND C.CLNT_NM LIKE '%' || #{ordrsClntNm} || '%'
AND A.EST_NO LIKE '%' || #{estNo} || '%'
AND A.EST_NM LIKE '%' || #{estNm} || '%'
) AS A WHERE 1 = 1 ) AS A WHERE 1 = 1

SELECT *
    FROM (
    SELECT
        FN_CM05M01_CD_TO_NM(T.CO_CD) AS CO_NM,
        TO_CHAR(T.EST_DT, 'YYYY-MM-DD') AS EST_DT,
        TO_CHAR(T.PBLS_DT, 'YYYY-MM-DD') AS PBLS_DT,
        ROWNUM AS RNUM,
        T.*
    FROM (
    SELECT A.*, B.CLNT_NM
    FROM TB_CR01M01 A
LEFT JOIN TB_BM02M01 B ON A.EST_CLNT_CD = B.CLNT_CD

    
        AND A.EST_DT BETWEEN #{strtDt} AND #{endDt}
    
    
        AND A.CO_CD = #{coCd}
    
    
        AND A.EST_NO LIKE '%' || #{searchValue} || '%'
    
    
        AND B.CLNT_NM LIKE '%' || #{searchValue} || '%'
    
    
        AND A.EST_NM LIKE '%' || #{estNm} || '%'
    
    
        
            AND A.EST_NO = #{estNo}
        
    
    
        AND A.EST_DEG = #{estDeg}
    
    
        AND B.CLNT_CD = #{clntCd}
    
    AND A.EST_NO NOT IN (
    SELECT EST_NO FROM TB_CR01M01 WHERE ORDRS_YN = 'Y'
    )

) T
)
WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT A.* FROM ( SELECT A.* , ROWNUM AS RN FROM ( SELECT A.FILE_TRGT_KEY , A.CO_CD , R.CODE_NM AS CO_NM , A.EST_NO , A.EST_DEG , TO_CHAR(A.PBLS_DT, 'YYYY-MM-DD') AS PBLS_DT , A.EST_CLNT_CD , C.CLNT_NM AS EST_CLNT_NM , A.CLNT_PJT , FN_CM05M01_CD_TO_NM(A.CLNT_PJT) AS CLNT_PJT_NM , TO_CHAR(A.EST_DT, 'YYYY-MM-DD') AS EST_DT , TO_CHAR(A.ESTS_DT, 'YYYY-MM-DD') AS ESTS_DT , TO_CHAR(A.ESTE_DT, 'YYYY-MM-DD') AS ESTE_DT , A.CSTM_MNG_NM , A.CSTM_MNG_HP , A.MNG_ID , U3.NAME AS MNG_ID_NM , A.EST_NM , A.CURR_CD , R1.CODE_NM AS CURR_NM , NVL(A.EXRATE, 1) AS EXRATE , NVL(A.EST_AMT, 0) AS EST_AMT , NVL(A.EST_NEGO_AMT, 0) AS EST_NEGO_AMT , A.EST_EXCEPT , A.EST_STD , A.EST_DLVR_CNDT , A.EST_PMNT_CNDT , A.EST_DUDT , NVL(A.ORDRS_YN, 'N') AS ORDRS_YN , TO_CHAR(A.ORDRS_DT, 'YYYY-MM-DD') AS ORDRS_DT , A.EST_RMK , U1.NAME AS CREAT_ID_NM , TO_CHAR(A.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM , U2.NAME AS UDT_ID_NM , TO_CHAR(A.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM , A.CREAT_ID , A.UDT_ID , A.CREAT_PGM , A.UDT_PGM , A.ETC_FIELD1 , A.ETC_FIELD2 , A.ETC_FIELD3 FROM TB_CR01M01 AS A --고객사 LEFT OUTER JOIN TB_BM02M01 AS C ON C.CLNT_CD = A.EST_CLNT_CD --회사 LEFT OUTER JOIN TB_CM05M01 AS R ON R.CODE_ID = A.CO_CD --통화단위 LEFT OUTER JOIN TB_CM05M01 AS R1 ON R1.CODE_ID = A.CURR_CD --생성자 LEFT OUTER JOIN TB_CM06M01 AS U1 ON U1.ID = A.CREAT_ID --변경자 LEFT OUTER JOIN TB_CM06M01 AS U2 ON U2.ID = A.UDT_ID --담당자 LEFT OUTER JOIN TB_CM06M01 AS U3 ON U3.ID = A.MNG_ID WHERE 1 = 1
AND A.CO_CD = #{coCd}
AND A.PBLS_DT BETWEEN TO_DATE( #{strtDt},'YYYYMMDD') AND TO_DATE( #{endDt},'YYYYMMDD')
AND A.EST_CLNT_CD = #{ordrsClntCd}
AND C.CLNT_NM LIKE '%' || #{ordrsClntNm} || '%'
AND A.EST_NO LIKE '%' || #{estNo} || '%'
AND A.EST_NM LIKE '%' || #{estNm} || '%'
AND A.EST_NO LIKE '%' || #{searchValue} || '%'
AND C.CLNT_NM LIKE '%' || #{searchValue} || '%'
ORDER BY A.CO_CD, A.EST_NO DESC, A.EST_DEG DESC ) AS A WHERE 1 = 1 ) AS A WHERE 1 = 1 AND A.RN BETWEEN ${firstIndex} AND ${lastIndex}

SELECT A.* FROM ( SELECT A.* , ROWNUM AS RN FROM ( SELECT A.FILE_TRGT_KEY , A.CO_CD , R.CODE_NM AS CO_NM , A.EST_NO , A.EST_DEG , TO_CHAR(A.PBLS_DT, 'YYYY-MM-DD') AS PBLS_DT , A.EST_CLNT_CD , C.CLNT_NM AS EST_CLNT_NM , A.CLNT_PJT , FN_CM05M01_CD_TO_NM(A.CLNT_PJT) AS CLNT_PJT_NM , TO_CHAR(A.EST_DT, 'YYYY-MM-DD') AS EST_DT , TO_CHAR(A.ESTS_DT, 'YYYY-MM-DD') AS ESTS_DT , TO_CHAR(A.ESTE_DT, 'YYYY-MM-DD') AS ESTE_DT , A.CSTM_MNG_NM , A.CSTM_MNG_HP , A.MNG_ID , U3.NAME AS MNG_ID_NM , A.EST_NM , A.CURR_CD , R1.CODE_NM AS CURR_NM , NVL(A.EXRATE, 1) AS EXRATE , NVL(A.EST_AMT, 0) AS EST_AMT , NVL(A.EST_NEGO_AMT, 0) AS EST_NEGO_AMT , A.EST_EXCEPT , A.EST_STD , A.EST_DLVR_CNDT , A.EST_PMNT_CNDT , A.EST_DUDT , NVL(A.ORDRS_YN, 'N') AS ORDRS_YN , TO_CHAR(A.ORDRS_DT, 'YYYY-MM-DD') AS ORDRS_DT , A.EST_RMK , U1.NAME AS CREAT_ID_NM , TO_CHAR(A.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM , U2.NAME AS UDT_ID_NM , TO_CHAR(A.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM , A.CREAT_ID , A.UDT_ID , A.CREAT_PGM , A.UDT_PGM , A.ETC_FIELD1 , A.ETC_FIELD2 , A.ETC_FIELD3 FROM TB_CR01M01 AS A --고객사 LEFT OUTER JOIN TB_BM02M01 AS C ON C.CLNT_CD = A.EST_CLNT_CD --회사 LEFT OUTER JOIN TB_CM05M01 AS R ON R.CODE_ID = A.CO_CD --통화단위 LEFT OUTER JOIN TB_CM05M01 AS R1 ON R1.CODE_ID = A.CURR_CD --생성자 LEFT OUTER JOIN TB_CM06M01 AS U1 ON U1.ID = A.CREAT_ID --변경자 LEFT OUTER JOIN TB_CM06M01 AS U2 ON U2.ID = A.UDT_ID --담당자 LEFT OUTER JOIN TB_CM06M01 AS U3 ON U3.ID = A.MNG_ID WHERE 1 = 1
AND A.CO_CD = #{coCd}
AND A.EST_NO LIKE '%' || #{searchValue} || '%'
AND C.CLNT_NM LIKE '%' || #{searchValue} || '%'
ORDER BY A.CO_CD, A.EST_NO DESC, A.EST_DEG ) AS A WHERE 1 = 1 ) AS A WHERE 1 = 1 AND A.RN BETWEEN ${firstIndex} AND ${lastIndex}

SELECT COUNT(*)
FROM TB_CR01D01 A
WHERE A.CO_CD = #{coCd}
  AND A.EST_NO = #{estNo}
  AND A.EST_DEG = #{estDeg}

SELECT A.*,
       FN_CM05M01_CD_TO_NM(A.EST_DTL_DIV) AS EST_DTL_DIV_NM,
       FN_CM05M01_CD_TO_NM(A.EST_UNIT) AS EST_UNIT_NM
FROM TB_CR01D01 A
WHERE 1 = 1
  AND A.CO_CD = #{coCd}
  AND A.EST_NO = #{estNo}
  AND A.EST_DEG = #{estDeg}
  ORDER BY EST_DTL_DIV, EST_SEQ ASC

mapper.delete:
DELETE
FROM TB_CR01M01
WHERE 1 = 1
  AND CO_CD = #{coCd}
  AND EST_NO = #{estNo}
  AND EST_DEG = #{estDeg}

DELETE
FROM TB_CR01D01
WHERE 1 = 1
  AND CO_CD = #{coCd}
  AND EST_NO = #{estNo}
  AND EST_DEG = #{estDeg}
  AND EST_SEQ = #{estSeq}

DELETE
FROM TB_CR01D01
WHERE 1 = 1
  AND CO_CD = #{coCd}
  AND EST_NO = #{estNo}
  AND EST_DEG = #{estDeg}

mapper.update:
        UPDATE TB_CR01M01
        SET ORDRS_YN = 'Y',
            ORDRS_DT = #{ordrsDt}

        WHERE CO_CD = #{coCd}
          AND EST_NO = #{estNo}
          AND EST_DEG = #{estDeg}

mapper.select:
SELECT MAX(EST_DEG)
FROM TB_CR01M01

    WHERE CO_CD = #{coCd} AND EST_NO = #{estNo}
