mapper.select:
SELECT *
FROM (
SELECT
    FN_CM05M01_CD_TO_NM(T.CO_CD) AS CO_NM,
    TO_CHAR(T.EST_DT, 'YYYY-MM-DD') AS EST_DT,
    TO_CHAR(T.PBLS_DT, 'YYYY-MM-DD') AS PBLS_DT,
    ROWNUM AS RNUM,
    T.*
FROM (
SELECT A.*, B.CLNT_NM
FROM TB_CR01M01 A
LEFT JOIN TB_BM02M01 B ON A.EST_CLNT_CD = B.CLNT_CD


        AND A.EST_DT BETWEEN #{strtDt} AND #{endDt}


        AND A.CO_CD = #{coCd}


        AND A.EST_NO LIKE '%' || #{searchValue} || '%'


        AND B.CLNT_NM LIKE '%' || #{searchValue} || '%'


        AND A.EST_NM LIKE '%' || #{estNm} || '%'



            AND A.EST_NO = #{estNo}



        AND A.EST_DEG = #{estDeg}


        AND B.CLNT_CD = #{clntCd}

    AND A.EST_NO NOT IN (
    SELECT EST_NO FROM TB_CR01M01 WHERE ORDRS_YN = 'Y'
    )

) T
)
WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT * FROM ( SELECT TO_CHAR(T.EST_DT, 'YYYY-MM-DD') AS EST_DT, TO_CHAR(T.PBLS_DT, 'YYYY-MM-DD') AS PBLS_DT, TO_CHAR(T.ORDRS_DT, 'YYYY-MM-DD') AS ORDRS_DT, TO_CHAR(T.ESTS_DT, 'YYYY-MM-DD') AS ESTS_DT, TO_CHAR(T.ESTE_DT, 'YYYY-MM-DD') AS ESTE_DT, TO_CHAR(T.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM, TO_CHAR(T.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM, -- TO_CHAR(T.EST_STD_DT, 'YYYY-MM-DD') AS EST_STD_DT, ROWNUM AS RNUM, T.*, FN_CM05M01_CD_TO_NM(T.CO_CD) AS CO_NM, T.NAME AS MNG_NM FROM (
SELECT A.*, B.CLNT_NM, C.NAME FROM TB_CR01M01 A LEFT JOIN TB_BM02M01 B ON A.EST_CLNT_CD = B.CLNT_CD LEFT JOIN TB_CM06M01 C ON A.MNG_ID = C.EMP_NO
AND UPPER(A.CO_CD) = #{coCd}
AND UPPER(A.EST_NO) LIKE '%' || UPPER(#{searchValue}) || '%'
AND B.CLNT_NM LIKE '%' || #{searchValue} || '%'
AND A.EST_DT BETWEEN #{strtDt} AND #{endDt}
AND UPPER(A.CO_CD) LIKE '%' || UPPER(#{coCd}) || '%'
AND UPPER( FN_CM05M01_CD_TO_NM(A.CO_CD) ) LIKE '%' || UPPER(#{searchValue}) || '%'
AND UPPER(A.EST_NO) LIKE '%' || UPPER(#{searchValue}) || '%'
AND B.CLNT_NM LIKE '%' || #{searchValue} || '%'
AND UPPER(A.EST_NM) LIKE '%' || UPPER(#{estNm}) || '%'
AND UPPER(A.EST_NO) LIKE '%' || UPPER(#{estNo}) || '%'
AND A.EST_DEG = #{estDeg}
AND B.CLNT_CD = #{clntCd}
AND UPPER(B.CLNT_NM) LIKE '%' || UPPER(#{clntNm}) || '%'
) T ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT COUNT(*)
FROM TB_CR01D01 A
WHERE A.CO_CD = #{coCd}
  AND A.EST_NO = #{estNo}
  AND A.EST_DEG = #{estDeg}

SELECT A.*,
       FN_CM05M01_CD_TO_NM(A.EST_DTL_DIV) AS EST_DTL_DIV_NM,
       FN_CM05M01_CD_TO_NM(A.EST_UNIT) AS EST_UNIT_NM
FROM TB_CR01D01 A
WHERE 1 = 1
  AND A.CO_CD = #{coCd}
  AND A.EST_NO = #{estNo}
  AND A.EST_DEG = #{estDeg}

mapper.delete:
DELETE
FROM TB_CR01M01
WHERE 1 = 1
  AND CO_CD = #{coCd}
  AND EST_NO = #{estNo}
  AND EST_DEG = #{estDeg}

DELETE
FROM TB_CR01D01
WHERE 1 = 1
  AND CO_CD = #{coCd}
  AND EST_NO = #{estNo}
  AND EST_DEG = #{estDeg}
  AND EST_SEQ = #{estSeq}

DELETE
FROM TB_CR01D01
WHERE 1 = 1
  AND CO_CD = #{coCd}
  AND EST_NO = #{estNo}
  AND EST_DEG = #{estDeg}

mapper.update:
UPDATE TB_CR01M01
SET ORDRS_YN = 'Y',
    ORDRS_DT = #{ordrsDt}

WHERE CO_CD = #{coCd}
  AND EST_NO = #{estNo}

mapper.select:
SELECT MAX(EST_DEG)
FROM TB_CR01M01

    WHERE CO_CD = #{coCd} AND EST_NO = #{estNo}
