mapper.select:
WITH SJM AS ( SELECT * FROM ( SELECT ROWNUM AS RRN, AA.* FROM ( SELECT M.*, C.CLNT_NM FROM TB_CR02M01 AS M -- 수주마스터 LEFT OUTER JOIN TB_BM02M01 AS C --거래처 ON M.ORDRS_CLNT_CD = C.CLNT_CD WHERE 1=1
AND M.CO_CD = #{coCd}
AND M.ORDRS_DT BETWEEN TO_DATE(#{strtDt}, 'YYYYMMDD') AND TO_DATE(#{endDt}, 'YYYYMMDD')
AND M.ORDRS_CLNT_CD = #{ordrsClntCd}
AND C.CLNT_NM = '%' || #{ordrsClntNm} || '%'
AND M.ORDRS_NO = #{ordrsNo}
AND M.CLNT_PJT = #{clntPjt}
AND M.CTRT_NM LIKE '%' || #{ctrtNm} || '%'
AND M.ORDRS_DIV = #{ordrsDiv}
ORDER BY CO_CD, CLNT_CD, CLNT_PJT, ORDRS_NO, FILE_TRGT_KEY ) AS AA )
WHERE RRN BETWEEN #{firstIndex} AND #{lastIndex}
) , SJD AS ( SELECT SM.CO_CD AS CO_CD --회사코드 , FN_CM05M01_CD_TO_NM(SM.CO_CD) AS CO_NM --회사명 , SM.ORDRS_NO AS ORDRS_NO --수주번호 , SM.ORDRS_DIV AS ORDRS_DIV --수주구분 , FN_CM05M01_CD_TO_NM(SM.ORDRS_DIV) AS ORDRS_DIV_NM --수주구분명 , TO_CHAR(SM.ORDRS_DT, 'YYYY-MM-DD') AS IO_DT --수주일자 , SM.ORDRS_CLNT_CD AS CLNT_CD --고객사 , SM.CLNT_NM AS CLNT_NM --고객사명 , SM.CLNT_PJT AS CLNT_PJT --고객사PJT , SM.CTRT_NM AS CTRT_NM --계약명 , SM.ORDRS_AMT AS ORDRS_AMT --수주금액 , SM.EST_NO AS EST_NO --견적번호 , SM.EST_DEG AS EST_DEG --견적차수 , SM.CO_CD||'_'||SM.ORDRS_NO AS PID -- , SM.CO_CD||'_'||SM.ORDRS_NO||'_'||SD.ORDRS_SEQ AS ID -- , SD.ORDRS_SEQ AS IO_SEQ -- , NVL(SD.SALES_CD, SM.ORDRS_NO||'-'||NVL2(SD.ORDRS_SEQ,TO_CHAR(SD.ORDRS_SEQ, '00')||FN_CM05M01_CD_TO_NM(SD.ORDRS_DTL_DIV10),'상세없음')) AS SALES_CD --SalesCode , SD.ORDRS_DTL_DIV10 AS ORDRS_DTL_DIV10 --입력구분 , FN_CM05M01_CD_TO_NM(SD.ORDRS_DTL_DIV10) AS ORDRS_DTL_DIV10_NM --입력구분명 , SD.PRDT_CD AS PRDT_CD --제품구분 , P.PRDT_NM AS PRDT_NM --제품구분명 , SD.ITEM_DIV AS ITEM_DIV --아이템구분 , FN_CM05M01_CD_TO_NM(SD.ITEM_DIV) AS ITEM_DIV_NM --아이템구분명 , SD.ORDRS_DTL_DIV20 AS ORDRS_DTL_DIV20 --작업구분 , FN_CM05M01_CD_TO_NM(SD.ORDRS_DTL_DIV20) AS ORDRS_DTL_DIV20_NM --작업구분명 , SD.ORDRS_DTL_DIV30 AS ORDRS_DTL_DIV30 --자체구분 , FN_CM05M01_CD_TO_NM(SD.ORDRS_DTL_DIV30) AS ORDRS_DTL_DIV30_NM --자체구분명 , SD.EQP_NM AS EQP_NM --설비명 , SD.ORDRS_PRC_MACH AS ORDRS_PRC_MACH --수주가-기계파트 , SD.ORDRS_PRC_ELCTY AS ORDRS_PRC_ELCTY --수주가-전기파트 , SD.ORDRS_PRC AS ORDRS_PRC --수주가 , SD.ORDRS_QTY AS ORDRS_QTY --수량 , SD.EST_EPCT_MAT_PRC AS EST_EPCT_MAT_PRC --견적예상재료비 , SD.TRGT_PCHS_PCOST_MACH AS TRGT_PCHS_PCOST_MACH --목표구매원가-기계 , SD.TRGT_PCHS_PCOST_ELCTY AS TRGT_PCHS_PCOST_ELCTY--목표구매원가-전기 , SD.TRGT_PCHS_PCOST_IN_HOUSE_PRCSN AS TRGT_PCHS_PCOST_IN_HOUSE_PRCSN --목표구매원가-기자체가공 , SD.LABOR_COST_MFG_COST AS LABOR_COST_MFG_COST , SD.CREAT_ID AS CREAT_ID --작성자ID , FN_CM06M01_ID_TO_NM(SD.CREAT_ID) AS CREAT_ID_NM --작성자명 , TO_CHAR(SD.CREAT_DTTM,'YYYY-MM-DD') AS CREAT_DTTM --작성일시 , SD.UDT_ID AS UDT_ID --수정자ID , FN_CM06M01_ID_TO_NM(SD.UDT_ID) AS UDT_ID_NM --수정자명 , TO_CHAR(SD.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM --수정일시 , SM.FILE_TRGT_KEY AS FILE_TRGT_KEY , RRN FROM SJM AS SM --수주마스터 LEFT OUTER JOIN TB_CR02D02 AS SD ON SM.CO_CD = SD.CO_CD AND SM.ORDRS_NO = SD.ORDRS_NO LEFT OUTER JOIN TB_BM01M01 AS P ON SD.PRDT_CD = P.PRDT_CD WHERE 1 = 1 ) SELECT COUNT(*) FROM ( SELECT CO_CD --회사코드 , FN_CM05M01_CD_TO_NM(CO_CD) CO_NM --회사명 , ORDRS_NO --수주번호 , ORDRS_DIV --수주구분 , FN_CM05M01_CD_TO_NM(ORDRS_DIV) ORDRS_DIV_NM --수주구분명 , TO_CHAR(ORDRS_DT, 'YYYY-MM-DD') IO_DT --수주일자 , ORDRS_CLNT_CD --고객사 , CLNT_NM --고객사명 , CLNT_PJT AS CLNT_PJT --고객사PJT , FN_CM05M01_CD_TO_NM(CLNT_PJT) AS CLNT_PJT_NM --고객사PJT명 , CTRT_NM --계약명 , ORDRS_AMT --수주금액 , EST_NO --견적번호 , EST_DEG --견적차수 , '' PID --PID , CO_CD||'_'||ORDRS_NO ID --ID , 0 IO_SEQ --IO_SEQ , '' SALES_CD --SalesCode , '' ORDRS_DTL_DIV10 --입력구분 , '' ORDRS_DTL_DIV10_NM --입력구분명 , '' PRDT_CD --제품구분 , '' PRDT_NM --제품구분명 , '' ITEM_DIV --아이템구분 , '' ITEM_DIV_NM --아이템구분명 , '' ORDRS_DTL_DIV20 --작업구분 , '' ORDRS_DTL_DIV20_NM --작업구분명 , '' ORDRS_DTL_DIV30 --자체구분 , '' ORDRS_DTL_DIV30_NM --자체구분명 , '' EQP_NM --설비명 , '' ORDRS_PRC_MACH --수주가-기계파트 , '' ORDRS_PRC_ELCTY --수주가-전기파트 , '' ORDRS_PRC --수주가 , '' ORDRS_QTY --수량 , '' EST_EPCT_MAT_PRC --견적예상재료비 , '' TRGT_PCHS_PCOST_MACH --목표구매원가-기계 , '' TRGT_PCHS_PCOST_ELCTY--목표구매원가-전기 , '' TRGT_PCHS_PCOST_IN_HOUSE_PRCSN --목표구매원가-기자체가공 , '' LABOR_COST_MFG_COST --노무비및제조경비 , CREAT_ID --작성자ID , FN_CM06M01_ID_TO_NM(CREAT_ID) CREAT_ID_NM --작성자명 , TO_CHAR(CREAT_DTTM,'YYYY-MM-DD') CREAT_DTTM --작성일시 , UDT_ID --수정자ID , FN_CM06M01_ID_TO_NM(UDT_ID) UDT_ID_NM --수정자명 , TO_CHAR(UDT_DTTM, 'YYYY-MM-DD') UDT_DTTM --수정일시 , FILE_TRGT_KEY , RRN FROM SJM )

