AND X.SALES_CD like '%${salesCd}%'
CROSS JOIN ( SELECT CASE WHEN CODE_KIND = 'ROOT' THEN '' ELSE CODE_KIND END CODE_KIND, CODE_ID, CODE_RPRC, CODE_NM, CASE WHEN LEVEL = '1' THEN CODE_NM ELSE '' END LVL1, CASE WHEN LEVEL = '2' THEN CODE_NM ELSE '' END LVL2, CASE WHEN LEVEL = '3' THEN CODE_NM ELSE '' END LVL3, LPAD('', 3*(LEVEL-1)) || LEVEL LVL, REGEXP_REPLACE(SYS_CONNECT_BY_PATH(CODE_ID, ' -> '), '^\s+\-\>\s+', '') as PATH, CONNECT_BY_ISLEAF AS IS_LEAF, CASE WHEN CONNECT_BY_ISLEAF = 0 THEN 'unit' ELSE 'leaf' END TYPE FROM TB_CM05M01 START WITH CODE_ID = 'WBSDIV' CONNECT BY PRIOR CODE_ID = CODE_KIND ) CD LEFT OUTER JOIN TB_WB01M01 W ON X.CO_CD = W.CO_CD AND X.SALES_CD = w.SALES_CD AND CD.CODE_ID = W.WBS_PLAN_CODE_ID LEFT OUTER JOIN TB_WB02M01 Z ON X.CO_CD = Z.CO_CD AND X.SALES_CD = Z.SALES_CD AND CD.CODE_ID = Z.WBS_PLAN_CODE_ID LEFT OUTER JOIN TB_BM02M01 A ON Y.ORDRS_CLNT_CD = A.CLNT_CD --거래처마스터 WHERE X.ORDRS_DTL_DIV10 = 'ORDRSDTLDIV1010' GROUP BY X.ORDRS_NO, X.SALES_CD, X.CO_CD,Y.ORGN_SALES_CD,Y.ORDRS_CLNT_CD, Y.CLNT_PJT
