mapper.select:
SELECT
    TO_CHAR(T.ORDRS_DT, 'YYYY-MM-DD') AS ORDRS_DT,
    TO_CHAR(T.FWD_EXCH_JOIN_DT, 'YYYY-MM-DD') AS FWD_EXCH_JOIN_DT,
    ROWNUM AS RNUM,
    T.*
FROM (
         SELECT * FROM TB_CR02M01 A
         LEFT JOIN TB_BM02M01 B ON A.ORDRS_CLNT_CD = B.CLNT_CD
     ) T
WHERE 1=1


    AND T.ORDRS_DT BETWEEN #{strtDt} AND #{endDt}


    AND T.CO_CD = #{coCd}


AND T.ORDRS_CLNT_CD = #{clntCd}


    AND T.CTRT_NM = #{ctrtNm}


    AND T.ORDRS_NO LIKE '%' || #{searchValue} || '%'


    AND T.ORDRS_NO = #{ordrsNo}


    AND T.CLNT_PJT = #{clntPjt}

SELECT
    TO_CHAR(T.ORDRS_DT, 'YYYY-MM-DD')         AS ORDRS_DT,
    TO_CHAR(T.FWD_EXCH_JOIN_DT, 'YYYY-MM-DD') AS FWD_EXCH_JOIN_DT,
    T.*
FROM (
        SELECT *
        FROM TB_CR02M01
        WHERE CO_CD = #{coCd}
        AND ORDRS_NO = #{ordrsNo}
) T

SELECT *
FROM TB_CR02D02
WHERE CO_CD = #{coCd}
  AND ORDRS_NO = #{ordrsNo}

mapper.update:
UPDATE TB_CR02D01
SET CLMN_PLAN_DIV      = #{clmnPlanDiv},
    CLMN_DIV_SEQ       = #{clmnDivSeq},
    CLMN_RATE          = #{clmnRate},
    CLMN_AMT           = #{clmnAmt},
    BILL_PBLS_DT       = #{billPblsDt},
    CLMN_DT            = #{clmnDt},
    PMNT_DT_AFTER_BILL = #{pmntDtAfterBill},
    CURR_CD          = #{currCd},
    CLMN_PLAN_RMK                = #{clmnPlanRmk}
WHERE CO_CD = #{coCd}
  AND ORDRS_NO = #{ordrsNo}
  AND CLMN_PLAN_SEQ = #{clmnPlanSeq}

UPDATE TB_CR02D02 SET ORDRS_DTL_DIV10 = #{ordrsDtlDiv10, jdbcType=VARCHAR},
PRDT_CD = #{prdtCd}, ITEM_DIV = #{itemDiv}, SALES_CD = #{salesCd}, EQP_NM = #{eqpNm},
ORDRS_PRC_MACH = #{ordrsPrcMach, jdbcType=NUMERIC}, ORDRS_PRC_ELCTY = #{ordrsPrcElcty, jdbcType=NUMERIC}, ORDRS_PRC = #{ordrsPrc, jdbcType=NUMERIC}, ORDRS_QTY = #{ordrsQty, jdbcType=NUMERIC}, EST_EPCT_MAT_PRC = #{estEpctMatPrc, jdbcType=NUMERIC}, TRGT_PCHS_PCOST_MACH = #{trgtPchsPcostMach, jdbcType=NUMERIC}, TRGT_PCHS_PCOST_ELCTY = #{trgtPchsPcostElcty, jdbcType=NUMERIC}, TRGT_PCHS_PCOST_IN_HOUSE_PRCSN = #{trgtPchsPcostInHousePrcsn, jdbcType=NUMERIC}, LABOR_COST_MFG_COST = #{laborCostMfgCost, jdbcType=NUMERIC} WHERE CO_CD = #{coCd} AND ORDRS_NO = #{ordrsNo} AND ORDRS_SEQ = #{ordrsSeq}

mapper.delete:
DELETE
FROM TB_CR02M01
WHERE 1 = 1
  AND CO_CD = #{coCd}
  AND ORDRS_NO = #{ordrsNo}

DELETE
FROM TB_CR02D01
WHERE 1 = 1
  AND CO_CD = #{coCd}
  AND ORDRS_NO = #{ordrsNo}

DELETE
FROM TB_CR02D02
WHERE 1 = 1
  AND CO_CD = #{coCd}
  AND ORDRS_NO = #{ordrsNo}
