mapper.select:
SELECT count(*) FROM ( SELECT A.* , ROWNUM AS RN FROM ( SELECT DISTINCT A.FILE_TRGT_KEY AS ORDRS_FILE_TRGT_KEY , A.CO_CD , R.CODE_NM AS CO_NM , A.ORDRS_CLNT_CD , S.CLNT_NM AS ORDRS_CLNT_NM , A.CLNT_PJT , A.ORDRS_NO , TO_CHAR(A.ORDRS_DT, 'YYYY-MM-DD') AS ORDRS_DT , A.ORDRS_DIV , R1.CODE_NM AS ORDRS_DIV_NM , A.CTRT_NM , A.CURR_CD , R2.CODE_NM AS CURR_NM , NVL(A.EXRATE, 1) AS EXRATE , NVL(A.ORDRS_AMT, 0) AS ORDRS_AMT , P.CLMN_PLAN_SEQ , P.CLMN_PLAN_DIV , R3.CODE_NM AS CLMN_PLAN_DIV_NM , P.CLMN_DIV_SEQ , TO_CHAR(NVL(P.CLMN_DIV_SEQ, 1)) || ' 차' AS CLMN_DIV_SEQ_NM , NVL(P.CLMN_RATE, 0) AS CLMN_RATE , TO_CHAR(NVL(P.CLMN_RATE, 0)) || ' %' AS CLMN_RATE_NM , NVL(P.CLMN_AMT, 0) AS PLAN_CLMN_AMT , TO_CHAR(P.BILL_PBLS_DT, 'YYYY-MM-DD') AS BILL_PBLS_DT , TO_CHAR(P.CLMN_DT, 'YYYY-MM-DD') AS PLAN_CLMN_DT , NVL(B.FILE_TRGT_KEY, 0) AS FILE_TRGT_KEY , B.CLMN_NO AS CLMN_NO , B.CLMN_DT AS CLMN_DT , NVL(B.CLMN_AMT, 0) AS CLMN_AMT , NVL(B.CLMN_DTL_AMT, 0) AS CLMN_DTL_AMT , NVL(CS.SELL_DCSN_DTL_AMT, 0) AS SELL_DCSN_AMT_SUM , NVL(BS.SELL_BILL_DTL_AMT, 0) AS SELL_BILL_AMT_SUM , NVL(SS.CLMN_DTL_AMT, 0) AS CLMN_DTL_AMT_SUM , CASE WHEN (NVL(P.CLMN_AMT, 0) - NVL(SS.CLMN_DTL_AMT, 0)) > 0 THEN 'N' ELSE 'Y' END AS CLMN_YN , NVL(P.CLMN_AMT, 0) - NVL(SS.CLMN_DTL_AMT, 0) AS MI_AMT FROM TB_CR02M01 AS A --수주 --수금계획 LEFT OUTER JOIN TB_CR02D01 AS P ON P.CO_CD = A.CO_CD AND P.ORDRS_NO = A.ORDRS_NO --매출확정집계 LEFT OUTER JOIN ( SELECT DISTINCT CM.CO_CD , CD.ORDRS_NO , CD.CLMN_PLAN_SEQ , SUM(NVL(CD.SELL_DCSN_DTL_AMT, 0)) AS SELL_DCSN_DTL_AMT FROM TB_CR03M01 AS CM --매출확정 --매출확정상세 LEFT OUTER JOIN TB_CR03D01 AS CD ON CD.CO_CD = CM.CO_CD AND CD.SELL_DCSN_NO = CM.SELL_DCSN_NO GROUP BY CM.CO_CD, CD.ORDRS_NO, CD.CLMN_PLAN_SEQ ) AS CS ON CS.CO_CD = P.CO_CD AND CS.ORDRS_NO = P.ORDRS_NO AND CS.CLMN_PLAN_SEQ = P.CLMN_PLAN_SEQ --매출계산서 집계 LEFT OUTER JOIN ( SELECT DISTINCT BM.CO_CD , BD.ORDRS_NO , BD.CLMN_PLAN_SEQ , SUM(NVL(BD.SELL_BILL_DTL_AMT, 0)) AS SELL_BILL_DTL_AMT FROM TB_CR04M01 AS BM --매출계산서 --매출계산서 상세 LEFT OUTER JOIN TB_CR04D01 AS BD ON BD.CO_CD = BM.CO_CD AND BD.SELL_BILL_NO = BM.SELL_BILL_NO GROUP BY BM.CO_CD, BD.ORDRS_NO, BD.CLMN_PLAN_SEQ ) AS BS ON BS.CO_CD = P.CO_CD AND BS.ORDRS_NO = P.ORDRS_NO AND BS.CLMN_PLAN_SEQ = P.CLMN_PLAN_SEQ --수금 집계 LEFT OUTER JOIN ( SELECT DISTINCT SM.CO_CD , SD.ORDRS_NO , SD.CLMN_PLAN_SEQ , SUM(NVL(SD.CLMN_DTL_AMT, 0)) AS CLMN_DTL_AMT FROM TB_CR05M01 AS SM --수금 --수금 상세 LEFT OUTER JOIN TB_CR05D01 AS SD ON SD.CO_CD = SM.CO_CD AND SD.CLMN_NO = SM.CLMN_NO GROUP BY SM.CO_CD, SD.ORDRS_NO, SD.CLMN_PLAN_SEQ ) AS SS ON SS.CO_CD = P.CO_CD AND SS.ORDRS_NO = P.ORDRS_NO AND SS.CLMN_PLAN_SEQ = P.CLMN_PLAN_SEQ --수금 LEFT OUTER JOIN ( SELECT DISTINCT BM.FILE_TRGT_KEY , BM.CO_CD , BM.CLMN_NO , MAX(TO_CHAR(BM.CLMN_DT, 'YYYY-MM-DD')) AS CLMN_DT , MAX(NVL(BM.CLMN_AMT, 0)) AS CLMN_AMT , BD.ORDRS_NO , BD.CLMN_PLAN_SEQ , SUM(NVL(BD.CLMN_DTL_AMT, 0)) AS CLMN_DTL_AMT FROM TB_CR05M01 AS BM --매출계산서 --매출계산서 상세 LEFT OUTER JOIN TB_CR05D01 AS BD ON BD.CO_CD = BM.CO_CD AND BD.CLMN_NO = BM.CLMN_NO GROUP BY BM.FILE_TRGT_KEY, BM.CO_CD, BD.ORDRS_NO, BD.CLMN_PLAN_SEQ, BM.CLMN_NO ) AS B ON B.CO_CD = P.CO_CD AND B.ORDRS_NO = P.ORDRS_NO AND B.CLMN_PLAN_SEQ = P.CLMN_PLAN_SEQ --회사 LEFT OUTER JOIN TB_CM05M01 AS R ON R.CODE_ID = A.CO_CD --수주구분 LEFT OUTER JOIN TB_CM05M01 AS R1 ON R1.CODE_ID = A.ORDRS_DIV --통화단위 LEFT OUTER JOIN TB_CM05M01 AS R2 ON R2.CODE_ID = A.CURR_CD --수금계획구분 LEFT OUTER JOIN TB_CM05M01 AS R3 ON R3.CODE_ID = P.CLMN_PLAN_DIV --고객사 LEFT OUTER JOIN TB_BM02M01 AS S ON S.CLNT_CD = A.ORDRS_CLNT_CD WHERE 1 = 1
AND A.CO_CD = #{coCd}
AND A.ORDRS_DT BETWEEN TO_DATE( #{strtDt},'YYYYMMDD') AND TO_DATE( #{endDt},'YYYYMMDD')
AND A.ORDRS_CLNT_CD = #{ordrsClntCd}
AND S.CLNT_NM LIKE '%' || #{ordrsClntNm} || '%'
AND A.ORDRS_NO LIKE '%' || #{ordrsNo} || '%'
AND A.CLNT_PJT LIKE '%' || #{clntPjt} || '%'
AND A.CTRT_NM LIKE '%' || #{ctrtNm} || '%'
AND A.ORDRS_DIV = #{ordrsDiv}
ORDER BY A.CO_CD, A.ORDRS_CLNT_CD, A.ORDRS_NO, P.CLMN_PLAN_SEQ, B.CLMN_NO ) AS A WHERE 1 = 1
AND A.CLMN_YN = #{clmnYn}
) AS A WHERE 1 = 1

SELECT A.* FROM ( SELECT A.* , ROWNUM AS RN FROM ( SELECT DISTINCT A.FILE_TRGT_KEY AS ORDRS_FILE_TRGT_KEY , A.CO_CD , R.CODE_NM AS CO_NM , A.ORDRS_CLNT_CD , S.CLNT_NM AS ORDRS_CLNT_NM , A.CLNT_PJT , R4.CODE_NM AS CLNT_PJT_NM , A.ORDRS_NO , TO_CHAR(A.ORDRS_DT, 'YYYY-MM-DD') AS ORDRS_DT , A.ORDRS_DIV , R1.CODE_NM AS ORDRS_DIV_NM , A.CTRT_NM , A.CURR_CD , R2.CODE_NM AS CURR_NM , NVL(A.EXRATE, 1) AS EXRATE , NVL(A.ORDRS_AMT, 0) AS ORDRS_AMT , P.CLMN_PLAN_SEQ , P.CLMN_PLAN_DIV , R3.CODE_NM AS CLMN_PLAN_DIV_NM , P.CLMN_DIV_SEQ , TO_CHAR(NVL(P.CLMN_DIV_SEQ, 1)) || ' 차' AS CLMN_DIV_SEQ_NM , NVL(P.CLMN_RATE, 0) AS CLMN_RATE , TO_CHAR(NVL(P.CLMN_RATE, 0)) || ' %' AS CLMN_RATE_NM , NVL(P.CLMN_AMT, 0) AS PLAN_CLMN_AMT , TO_CHAR(P.BILL_PBLS_DT, 'YYYY-MM-DD') AS BILL_PBLS_DT , TO_CHAR(P.CLMN_DT, 'YYYY-MM-DD') AS PLAN_CLMN_DT , NVL(B.FILE_TRGT_KEY, 0) AS FILE_TRGT_KEY , B.CLMN_NO AS CLMN_NO , B.CLMN_DT AS CLMN_DT , NVL(B.CLMN_AMT, 0) AS CLMN_AMT , NVL(B.CLMN_DTL_AMT, 0) AS CLMN_DTL_AMT , NVL(CS.SELL_DCSN_DTL_AMT, 0) AS SELL_DCSN_AMT_SUM , NVL(BS.SELL_BILL_DTL_AMT, 0) AS SELL_BILL_AMT_SUM , NVL(SS.CLMN_DTL_AMT, 0) AS CLMN_DTL_AMT_SUM , CASE WHEN (NVL(P.CLMN_AMT, 0) - NVL(SS.CLMN_DTL_AMT, 0)) > 0 THEN 'N' ELSE 'Y' END AS CLMN_YN , NVL(P.CLMN_AMT, 0) - NVL(SS.CLMN_DTL_AMT, 0) AS MI_AMT , A.MNG_ID , U.NAME AS MNG_NM FROM TB_CR02M01 AS A --수주 --수금계획 LEFT OUTER JOIN TB_CR02D01 AS P ON P.CO_CD = A.CO_CD AND P.ORDRS_NO = A.ORDRS_NO --매출확정집계 LEFT OUTER JOIN ( SELECT DISTINCT CM.CO_CD , CD.ORDRS_NO , CD.CLMN_PLAN_SEQ , SUM(NVL(CD.SELL_DCSN_DTL_AMT, 0)) AS SELL_DCSN_DTL_AMT FROM TB_CR03M01 AS CM --매출확정 --매출확정상세 LEFT OUTER JOIN TB_CR03D01 AS CD ON CD.CO_CD = CM.CO_CD AND CD.SELL_DCSN_NO = CM.SELL_DCSN_NO GROUP BY CM.CO_CD, CD.ORDRS_NO, CD.CLMN_PLAN_SEQ ) AS CS ON CS.CO_CD = P.CO_CD AND CS.ORDRS_NO = P.ORDRS_NO AND CS.CLMN_PLAN_SEQ = P.CLMN_PLAN_SEQ --매출계산서 집계 LEFT OUTER JOIN ( SELECT DISTINCT BM.CO_CD , BD.ORDRS_NO , BD.CLMN_PLAN_SEQ , SUM(NVL(BD.SELL_BILL_DTL_AMT, 0)) AS SELL_BILL_DTL_AMT FROM TB_CR04M01 AS BM --매출계산서 --매출계산서 상세 LEFT OUTER JOIN TB_CR04D01 AS BD ON BD.CO_CD = BM.CO_CD AND BD.SELL_BILL_NO = BM.SELL_BILL_NO GROUP BY BM.CO_CD, BD.ORDRS_NO, BD.CLMN_PLAN_SEQ ) AS BS ON BS.CO_CD = P.CO_CD AND BS.ORDRS_NO = P.ORDRS_NO AND BS.CLMN_PLAN_SEQ = P.CLMN_PLAN_SEQ --수금 집계 LEFT OUTER JOIN ( SELECT DISTINCT SM.CO_CD , SD.ORDRS_NO , SD.CLMN_PLAN_SEQ , SUM(NVL(SD.CLMN_DTL_AMT, 0)) AS CLMN_DTL_AMT FROM TB_CR05M01 AS SM --수금 --수금 상세 LEFT OUTER JOIN TB_CR05D01 AS SD ON SD.CO_CD = SM.CO_CD AND SD.CLMN_NO = SM.CLMN_NO GROUP BY SM.CO_CD, SD.ORDRS_NO, SD.CLMN_PLAN_SEQ ) AS SS ON SS.CO_CD = P.CO_CD AND SS.ORDRS_NO = P.ORDRS_NO AND SS.CLMN_PLAN_SEQ = P.CLMN_PLAN_SEQ --수금 LEFT OUTER JOIN ( SELECT DISTINCT BM.FILE_TRGT_KEY , BM.CO_CD , BM.CLMN_NO , MAX(TO_CHAR(BM.CLMN_DT, 'YYYY-MM-DD')) AS CLMN_DT , MAX(NVL(BM.CLMN_AMT, 0)) AS CLMN_AMT , BD.ORDRS_NO , BD.CLMN_PLAN_SEQ , SUM(NVL(BD.CLMN_DTL_AMT, 0)) AS CLMN_DTL_AMT FROM TB_CR05M01 AS BM --매출계산서 --매출계산서 상세 LEFT OUTER JOIN TB_CR05D01 AS BD ON BD.CO_CD = BM.CO_CD AND BD.CLMN_NO = BM.CLMN_NO GROUP BY BM.FILE_TRGT_KEY, BM.CO_CD, BD.ORDRS_NO, BD.CLMN_PLAN_SEQ, BM.CLMN_NO ) AS B ON B.CO_CD = P.CO_CD AND B.ORDRS_NO = P.ORDRS_NO AND B.CLMN_PLAN_SEQ = P.CLMN_PLAN_SEQ --회사 LEFT OUTER JOIN TB_CM05M01 AS R ON R.CODE_ID = A.CO_CD --수주구분 LEFT OUTER JOIN TB_CM05M01 AS R1 ON R1.CODE_ID = A.ORDRS_DIV --통화단위 LEFT OUTER JOIN TB_CM05M01 AS R2 ON R2.CODE_ID = A.CURR_CD --수금계획구분 LEFT OUTER JOIN TB_CM05M01 AS R3 ON R3.CODE_ID = P.CLMN_PLAN_DIV --고객사PJT LEFT OUTER JOIN TB_CM05M01 AS R4 ON R4.CODE_ID = A.CLNT_PJT --고객사 LEFT OUTER JOIN TB_BM02M01 AS S ON S.CLNT_CD = A.ORDRS_CLNT_CD --담당자 LEFT OUTER JOIN TB_CM06M01 AS U ON U.ID = A.MNG_ID WHERE 1 = 1
AND A.CO_CD = #{coCd}
AND A.ORDRS_DT BETWEEN TO_DATE( #{strtDt},'YYYYMMDD') AND TO_DATE( #{endDt},'YYYYMMDD')
AND A.ORDRS_CLNT_CD = #{ordrsClntCd}
AND S.CLNT_NM LIKE '%' || #{ordrsClntNm} || '%'
AND A.ORDRS_NO LIKE '%' || #{ordrsNo} || '%'
AND A.CLNT_PJT LIKE '%' || #{clntPjt} || '%'
AND A.CTRT_NM LIKE '%' || #{ctrtNm} || '%'
AND A.ORDRS_DIV = #{ordrsDiv}
ORDER BY A.CO_CD, A.ORDRS_CLNT_CD, A.ORDRS_NO, P.CLMN_PLAN_SEQ, B.CLMN_NO ) AS A WHERE 1 = 1
AND A.CLMN_YN = #{clmnYn}
) AS A WHERE 1 = 1 AND A.RN BETWEEN ${firstIndex} AND ${lastIndex}

mapper.insert:
        INSERT INTO TB_CR05M01
		(
			  FILE_TRGT_KEY
			, CO_CD
			, CLMN_NO
			, ORDRS_CLNT_CD
			, CLMN_DT
			, CURR_CD
			, EXRATE
			, PMNTMTD_CD
			, BKAC_CD
			, CLMN_AMT
			, CLMN_RMK
			, ETC_FIELD1
			, ETC_FIELD2
			, ETC_FIELD3
			, CREAT_ID
			, CREAT_PGM
			, CREAT_DTTM
		)
		VALUES
		(
			  #{fileTrgtKey}
			, #{coCd}
			, #{clmnNo}
			, #{ordrsClntCd}
			, #{clmnDt}
			, #{currCd}
			, #{exrate, jdbcType=VARCHAR}
			, #{pmntmtdCd}
			, #{bkacCd}
			, #{clmnAmt}
			, #{clmnRmk}
			, #{etcField1, jdbcType=VARCHAR}
			, #{etcField2, jdbcType=VARCHAR}
			, #{etcField3, jdbcType=VARCHAR}
			, #{userId}
			, #{pgmId}
			, SYSDATE
		)

INSERT INTO TB_CR05D01
(
	  CO_CD
	, CLMN_NO
	, ORDRS_NO
	, CLMN_PLAN_SEQ
	, CURR_CD
	, EXRATE
	, CLMN_DTL_AMT
	, CLMN_DTL_RMK
	, ETC_FIELD1
	, ETC_FIELD2
	, ETC_FIELD3
	, CREAT_ID
	, CREAT_PGM
	, CREAT_DTTM
)
VALUES
(
	  #{coCd}
	, #{clmnNo}
	, #{ordrsNo}
	, #{clmnPlanSeq}
	, #{currCdM}
	, #{exrateM, jdbcType=VARCHAR}
	, #{clmnDtlAmt}
	, #{clmnDtlRmk, jdbcType=VARCHAR}
	, #{etcField1, jdbcType=VARCHAR}
	, #{etcField2, jdbcType=VARCHAR}
	, #{etcField3, jdbcType=VARCHAR}
	, #{userId}
	, #{pgmId}
	, SYSDATE
)

mapper.update:
UPDATE TB_CR05M01
SET    PMNTMTD_CD = #{pmntmtdCd}
     , CLMN_DT    = #{clmnDt}
	 , BKAC_CD    = #{bkacCd}
     , CLMN_AMT   = #{clmnAmt}
     , CLMN_RMK   = #{clmnRmk}
	 , UDT_ID     = #{userId}
	 , UDT_PGM    = #{pgmId}
	 , UDT_DTTM   = SYSDATE
WHERE  FILE_TRGT_KEY = #{fileTrgtKey}

UPDATE TB_CR05D01
SET    CLMN_DTL_AMT = #{inputClmnDtlAmt}
     , UDT_ID       = #{userId}
	 , UDT_PGM      = #{pgmId}
	 , UDT_DTTM     = SYSDATE
WHERE  CO_CD         = #{coCd}
AND    CLMN_NO       = #{clmnNo}
AND    ORDRS_NO      = #{ordrsNo}
AND    CLMN_PLAN_SEQ = #{clmnPlanSeq}

UPDATE TB_CR05M01
SET    CLMN_AMT = CLMN_AMT - #{clmnDtlAmt}
WHERE  FILE_TRGT_KEY = #{fileTrgtKey}

mapper.select:
		SELECT CASE WHEN A.DEL_CNT = 1 THEN 'Y' ELSE 'N' END DEL_ALL
 		FROM   (
 		        SELECT COUNT(*) AS DEL_CNT
 		        FROM   TB_CR05D01
 		        WHERE  CO_CD   = #{coCd}
 		        AND    CLMN_NO = #{clmnNo}
 		       ) AS A
 		WHERE  1 = 1

mapper.delete:
DELETE TB_CR05M01
WHERE  CO_CD   = #{coCd}
AND    CLMN_NO = #{clmnNo}

DELETE TB_CR05D01
WHERE  CO_CD         = #{coCd}
AND    CLMN_NO       = #{clmnNo}
AND    ORDRS_NO      = #{ordrsNo}
AND    CLMN_PLAN_SEQ = #{clmnPlanSeq}

DELETE TB_CR05D01
WHERE  CO_CD   = #{coCd}
AND    CLMN_NO = #{clmnNo}
