/* selectPfuCopyTargetList */ SELECT * FROM ( SELECT ROWNUM AS RN, X.* FROM ( WITH SJD AS --수주상세 ( SELECT M.CO_CD AS CO_CD --회사코드 , M.ORDRS_NO AS ORDRS_NO --수주번호 , X.SALES_CD AS SALES_CD --SALES Code , M.ORDRS_CLNT_CD AS CLNT_CD --고객코드 , C.CLNT_NM AS CLNT_NM --고객명 , M.CLNT_PJT AS CLNT_PJT --고객사프로젝트 , (SELECT FN_CM05M01_CD_TO_NM(M.CLNT_PJT) FROM DUAL) AS CLNT_PJT_NM --고객사프로젝트명 , X.PRDT_CD AS PRDT_CD --제품코드 , (SELECT FN_CM05M01_CD_TO_NM(X.PRDT_CD) FROM DUAL) AS PRDT_NM --제품명 , X.ITEM_DIV AS ITEM_DIV --아이템구분 , (SELECT FN_CM05M01_CD_TO_NM(X.ITEM_DIV) FROM DUAL) AS ITEM_DIV_NM --아이템구분명 , X.EQP_NM AS EQP_NM --설비명 , X.MCTYPE AS MCTYPE --설비유형 , (SELECT FN_CM05M01_CD_TO_NM(X.MCTYPE) FROM DUAL) AS MCTYPE_NM --설비유형명 , X.ORDRS_DTL_DIV20 , (SELECT FN_CM05M01_CD_TO_NM(X.ORDRS_DTL_DIV20) FROM DUAL) AS ORDRS_DTL_DIV20_NM , M.ORDRS_DT FROM TB_CR02M01 M --수주마스터 INNER JOIN TB_CR02D02 AS X ON X.CO_CD = M.CO_CD AND X.ORDRS_NO = M.ORDRS_NO--수주상세 INNER JOIN TB_BM02M01 AS C ON M.ORDRS_CLNT_CD = C.CLNT_CD --거래처정보 WHERE 1=1 AND M.CO_CD = #{coCd}
AND X.SALES_CD LIKE '%'||#{salesCd}||'%' --salesCd로 검색시 날자와 상관없이 표시되게
AND X.SALES_CD IS NOT NULL --비용제외 AND EXISTS (SELECT 'X' --과제등록된 SALES_CD 만 FROM TB_WB21M01 P WHERE P.CO_CD = #{coCd} AND P.CO_CD = X.CO_CD AND P.SALES_CD = X.SALES_CD ) ) , SUBJ AS --과제정보 ( SELECT X.FILE_TRGT_KEY , X.CO_CD AS CO_CD --회사코드 , (SELECT FN_CM05M01_CD_TO_NM(X.CO_CD) FROM DUAL) AS CO_NM --회사명 , X.SJ_NO AS SJ_NO --과제번호 , X.SJ_NM AS SJ_NM --과제명 , TO_NUMBER(X.VER_NO) AS VER_NO --버젼 , MAX(TO_NUMBER(X.VER_NO)) OVER(PARTITION BY X.CO_CD, X.SJ_NO) AS MAX_VER_NO --최근버젼 , X.SALES_CD AS SALES_CD --SALES Code , X.MKER_DIV AS MKER_DIV --제작처구분 , (SELECT FN_CM05M01_CD_TO_NM(X.MKER_DIV) FROM DUAL) AS MKER_DIV_NM --제작처구분명 , X.MKER_CD AS MKER_CD --제작처코드 , BM.CLNT_NM AS MKER_NM --고객명 , X.SMRIZE_ID AS SMRIZE_ID --총괄PM ID , (SELECT FN_CM06M01_ID_TO_NM(X.SMRIZE_ID) FROM DUAL) AS SMRIZE_NM --총괄PM명 , TO_CHAR(X.BEGIN_DT, 'YYYY-MM-DD') AS BEGIN_DT --시작일 , TO_CHAR(X.DE_DT, 'YYYY-MM-DD') AS DE_DT --출고일 , TO_CHAR(X.ACPTNC_DT, 'YYYY-MM-DD') AS ACPTNC_DT --완료검수일 , X.REQRE_DAYCNT AS REQRE_DAYCNT --소요일수 , X.DSGN_DIF_CODE_ID AS DSGN_DIF_CODE_ID --설계난이도 , (SELECT FN_CM05M01_CD_TO_NM(X.DSGN_DIF_CODE_ID) FROM DUAL) AS DSGN_DIF_CODE_NM --설계난이도 , X.DSGN_PLAN_HOUR AS DSGN_PLAN_HOUR --설계계획공수 , X.PRDCTN_DIF_CODE_ID AS PRDCTN_DIF_CODE_ID --생산난이도 , (SELECT FN_CM05M01_CD_TO_NM(X.PRDCTN_DIF_CODE_ID) FROM DUAL) AS PRDCTN_DIF_CODE_NM --생산난이도 , X.PRDCTN_PLAN_HOUR AS PRDCTN_PLAN_HOUR --생산계획공수 , X.SJ_RMK AS SJ_RMK --비고 , X.CLOSE_YN AS CLOSE_YN --확정유무 , X.CREAT_ID AS CREAT_ID --작성자ID , X.CREAT_PGM AS CREAT_PGM -- , TO_CHAR(X.CREAT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS CREAT_DTTM -- , X.UDT_ID AS UDT_ID -- , X.UDT_PGM AS UDT_PGM -- , TO_CHAR(X.UDT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS UDT_DTT -- , X.PLAN_EXCEPT , DECODE(X.PLAN_EXCEPT, 'PLANEXCEPT01', '', (SELECT FN_CM05M01_CD_TO_NM(X.PLAN_EXCEPT) FROM DUAL)) AS PLAN_EXCEPT_NM --일정예외상황 , CR.FILE_TRGT_KEY AS PFU_NO , CR.PFU_PRDT_DIV , CR.PRDT_TYP , CR.PRDT_CAV , CR.PRDT_STEP , CR.VER_NO AS PFU_VER_NO FROM ( SELECT * FROM ( SELECT X.*, ROW_NUMBER() OVER (PARTITION BY