mapper.select:
SELECT PRDT_DIV , AREA_DIV , (SELECT FN_CM05M01_CD_TO_NM(C.AREA_DIV) FROM DUAL) AS AREA_DIV_NM , PART_DIV , (SELECT FN_CM05M01_CD_TO_NM(C.PART_DIV) FROM DUAL) AS PART_DIV_NM , PART_ID , PART_NM , PART_STD , PART_CUST , PART_CONF , PART_ETC , SORT_NO , USE_YN , CLNT_CD FROM TB_CM50M01 C WHERE 1 = 1
AND C.PRDT_DIV = #{prdtDiv}
AND C.AREA_DIV = #{areaDiv}

SELECT C.FILE_TRGT_KEY , C.PFU_SEQ , C.PRDT_DIV , (SELECT FN_CM05M01_CD_TO_NM(C.PRDT_DIV) FROM DUAL) AS AREA_DIV_NM , C.PART_ID , (SELECT FN_CM05M01_CD_TO_NM(C.PART_ID) FROM DUAL) AS PART_ID_NM , C.AREA_DIV , (SELECT FN_CM05M01_CD_TO_NM(C.AREA_DIV) FROM DUAL) AS AREA_DIV_NM , C.PART_DIV , (SELECT FN_CM05M01_CD_TO_NM(C.PART_DIV) FROM DUAL) AS PART_DIV_NM , C.PART_NM , C.PART_STD , C.PART_CUST , C.PART_CONF , C.PART_ETC , C.SORT_NO , C.CREAT_ID , C.CREAT_DTTM , TO_CHAR(C.CREAT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS CREAT_DTTM , C.CREAT_PGM , C.UDT_ID , TO_CHAR(C.UDT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS UDT_DTTM , C.UDT_PGM FROM TB_CR50D01 C WHERE 1 = 1 AND C.FILE_TRGT_KEY = #{fileTrgtKey}
AND C.PRDT_DIV = #{prdtDiv}
AND C.AREA_DIV = #{areaDiv}

