mapper.select:
WITH BASE AS ( SELECT T.DOC_ID AS DOC_ID , T.DOC_NO AS DOC_NO , SUBSTR(T.FILE_NAME, INSTR(T.FILE_NAME, '\', -1) + 1) AS FILE_NAME , T.EXT AS FILE_TYPE , REPLACE(T.PATH_BASE,'\','/') AS LPATH , TO_CHAR(T.CREATED_AT,'YYYY-MM-DD HH24:MI:SS') AS WORK_DTTM , T.CREATED_BY AS WORK_NM , T.ACTIVE_VER AS VER_NO , T.SALES_CD AS SALES_CD , T.DOC_ST , A.FILE_SIZE , A.VER_NO FROM TB_DRAWING_DOC T LEFT OUTER JOIN TB_DRAWING_VERSION AS A ON T.DOC_ID = A.DOC_ID AND T.ACTIVE_VER = A.VER_NO WHERE 1 = 1
AND T.DOC_ST = 'A'
AND REPLACE(T.PATH_BASE,'\','/') = #{comonCd}
AND T.CREATED_AT BETWEEN to_date(REPLACE(#{reqDtFrom},'-','')) AND to_date(REPLACE(#{reqDtTo},'-',''))+1
AND T.SALES_CD LIKE '%'||#{salesCd}||'%'
) SELECT COUNT(*) FROM ( SELECT B.*, ROW_NUMBER() OVER (ORDER BY B.FILE_NAME) AS RN FROM BASE B )

WITH BASE AS ( SELECT T.DOC_ID AS DOC_ID , T.DOC_NO AS DOC_NO , SUBSTR(T.FILE_NAME, INSTR(T.FILE_NAME, '\', -1) + 1) AS FILE_NAME , T.EXT AS FILE_TYPE , REPLACE(T.PATH_BASE,'\','/') AS LPATH , TO_CHAR(T.CREATED_AT,'YYYY-MM-DD HH24:MI:SS') AS WORK_DTTM , T.CREATED_BY AS WORK_NM , T.ACTIVE_VER AS VER_NO , T.SALES_CD AS SALES_CD , T.DOC_ST , A.FILE_SIZE , A.VER_NO FROM TB_DRAWING_DOC T LEFT OUTER JOIN TB_DRAWING_VERSION AS A ON T.DOC_ID = A.DOC_ID AND T.ACTIVE_VER = A.VER_NO WHERE 1 = 1
AND T.DOC_ST = 'A'
AND REPLACE(T.PATH_BASE,'\','/') = #{comonCd}
AND T.CREATED_AT BETWEEN to_date(REPLACE(#{reqDtFrom},'-','')) AND to_date(REPLACE(#{reqDtTo},'-',''))+1
AND T.SALES_CD LIKE '%'||#{salesCd}||'%'
) SELECT * FROM ( SELECT B.*, ROW_NUMBER() OVER (ORDER BY B.FILE_NAME) AS RN FROM BASE B ) WHERE RN BETWEEN #{firstIndex} AND #{lastIndex} ORDER BY FILE_NAME

/* SALES_CD / PART_NO / UNIT_NO 기준 파일 카운트 */
WITH BASE AS (
	SELECT 1
	FROM TB_DRAWING_DOC T
	WHERE 1=1

		AND T.DOC_ST = 'A'


		AND T.CREATED_AT BETWEEN TO_DATE(#{reqDtFrom}, 'YYYY-MM-DD')
							AND TO_DATE(#{reqDtTo},   'YYYY-MM-DD') + 1

		AND T.SALES_CD = #{salesCd,jdbcType = VARCHAR}
		AND T.PART_NO = #{partNo,jdbcType = VARCHAR}
		AND T.UNIT_NO = #{unitNo,jdbcType = VARCHAR}
)
SELECT COUNT(1) FROM BASE
