mapper.select:
WITH BASE AS ( SELECT T.DOC_ID AS FILE_KEY , T.DOC_NO AS DOC_NO , SUBSTR(T.FILE_NAME, INSTR(T.FILE_NAME, '\', -1) + 1) AS FILE_NAME , T.EXT AS FILE_TYPE , REPLACE(T.PATH_BASE,'\','/') AS LPATH , TO_CHAR(T.CREATED_AT,'YYYY-MM-DD') AS CREAT_DTTM , T.CREATED_BY AS CREAT_NM , T.ACTIVE_VER AS VER_NO , T.SALES_CD AS SALES_CD , T.DOC_ST , A.FILE_SIZE , A.VER_NO FROM TB_DRAWING_DOC T LEFT OUTER JOIN TB_DRAWING_VERSION AS A ON T.DOC_ID = A.DOC_ID AND T.ACTIVE_VER = A.VER_NO WHERE 1 = 1
AND T.DOC_ST = 'A'
AND REPLACE(T.PATH_BASE,'\','/') = #{comonCd}
AND T.CREATED_AT BETWEEN to_date(REPLACE(#{reqDtFrom},'-','')) AND to_date(REPLACE(#{reqDtTo},'-',''))+1
AND T.SALES_CD LIKE '%'||#{salesCd}||'%'
) SELECT * FROM ( SELECT B.*, ROW_NUMBER() OVER (ORDER BY B.FILE_NAME) AS RN FROM BASE B ) WHERE RN BETWEEN #{firstIndex} AND #{lastIndex} ORDER BY FILE_NAME
