mapper.select:
SELECT COUNT(*) FROM ( SELECT ROWNUM AS RNUM , A.* FROM ( SELECT IMPRVM_NO FROM TB_IM01M01 T LEFT OUTER JOIN TB_CM04M01 AS C ON C.DEPT_ID = T.EXEC_ACT_DEPT WHERE 1 = 1
AND T.CO_CD = #{coCd}
AND T.IMPRVM_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD')
AND T.EXEC_ACT_DEPT = #{actDeptId}
AND C.DEPT_NM = %'||#{actDeptIdNm}||'%'
AND T.IMPRVM_ID = #{imprvmId}
AND T.SALES_CD LIKE '%'||#{salesCd}||'%'
AND T.PRDT_CD = #{prdtCd}
AND T.ITEM_Div = #{itemDiv}
AND C.PRDT_NM LIKE '%'||#{prdtNm}||'%'
ORDER BY T.CO_CD, T.IMPRVM_DT DESC, T.IMPRVM_ID, T.IMPRVM_TYPE ) AS A WHERE 1 = 1 ) AS A WHERE 1 = 1

SELECT A.* FROM ( SELECT ROWNUM AS RNUM , A.* FROM ( SELECT T.IMPRVM_NO , T.CO_CD , T.IMPRVM_ID , FN_CM06M01_ID_TO_NM(T.IMPRVM_ID) AS IMPRVM_ID_NM , T.IMPRVM_DEPT_CD , (SELECT DEPT_NM FROM TB_CM04M01 WHERE DEPT_ID = T.IMPRVM_DEPT_CD) AS IMPRVM_DEPT_CD_NM , TO_CHAR(T.IMPRVM_DT,'YYYY-MM-DD') AS IMPRVM_DT , T.IMPRVM_TYPE , get_code_names(T.IMPRVM_TYPE) AS IMPRVM_TYPE_NM , T.UNIT_NO , T.SALES_CD , T.PRDT_CD , FN_CM05M01_CD_TO_NM(T.PRDT_CD) AS PRDT_CD_NM , T.PRDT_TYP , FN_CM05M01_CD_TO_NM(T.PRDT_TYP) AS PRDT_TYP_NM , T.ITEM_DIV , FN_CM05M01_CD_TO_NM(T.ITEM_DIV) AS ITEM_DIV_NM , T.MC_PART_CD , FN_CM05M01_CD_TO_NM(T.MC_PART_CD) AS MC_PART_CD_NM , T.IMPORTANT_CD , FN_CM05M01_CD_TO_NM(T.IMPORTANT_CD) AS IMPORTANT_CD_NM , T.IMPACT_CD , FN_CM05M01_CD_TO_NM(T.IMPACT_CD) AS IMPACT_CD_NM , T.IMPRVM_TXT , T.IMPRVM_RMK , T.IMPRVM_STS_CD , FN_CM05M01_CD_TO_NM(T.IMPRVM_STS_CD) AS IMPRVM_STS_CD_NM , T.IMPRVM_REQ_TXT , T.IMPRVM_REQ_ID , FN_CM06M01_ID_TO_NM(T.IMPRVM_REQ_ID) AS IMPRVM_REQ_ID_NM , TO_CHAR(T.IMPRVM_REQ_DTTM,'YYYY-MM-DD') AS IMPRVM_REQ_DTTM , T.EXEC_ACT_DEPT , C.DEPT_NM AS EXEC_ACT_DEPT_NM , T.EXEC_ACT_TXT , T.EXEC_TEAM_TXT , T.EXEC_TEAM_ID , FN_CM06M01_ID_TO_NM(T.EXEC_TEAM_ID) AS EXEC_TEAM_ID_NM , TO_CHAR(T.EXEC_TEAM_DTTM,'YYYY-MM-DD') AS EXEC_TEAM_DTTM , T.IMPRVM_APPR_GRADE , T.IMPRVM_APPR_POINT , TO_CHAR(T.IMPRVM_APPR_DTTM,'YYYY-MM-DD') AS IMPRVM_APPR_DTTM , T.IMPRVM_APPR_TXT , T.CREAT_ID , FN_CM06M01_ID_TO_NM(T.CREAT_ID) AS CREAT_ID_NM , TO_CHAR(T.CREAT_DTTM,'YYYY-MM-DD') AS CREAT_DTTM , T.CREAT_PGM , T.UDT_ID , FN_CM06M01_ID_TO_NM(T.UDT_ID) AS UDT_ID_NM , TO_CHAR(T.UDT_DTTM,'YYYY-MM-DD') AS UDT_DTTM , T.UDT_PGM , ( SELECT COUNT(*) AS APP_CNT FROM TB_WB20M03 W WHERE W.TODO_NO = T.IMPRVM_NO AND (W.TODO_DIV2_CODE_ID = 'TODODIV2150' OR W.TODO_DIV2_CODE_ID = 'TODODIV2160') AND W.SANCTN_STTUS = 'Y' ) AS APPROVAL_CNT FROM TB_IM01M01 T LEFT OUTER JOIN TB_CM04M01 AS C ON C.DEPT_ID = T.EXEC_ACT_DEPT WHERE 1 = 1
AND T.CO_CD = #{coCd}
AND T.IMPRVM_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD')
AND T.EXEC_ACT_DEPT = #{actDeptId}
AND C.DEPT_NM = %'||#{actDeptIdNm}||'%'
AND T.IMPRVM_ID = #{imprvmId}
AND T.SALES_CD LIKE '%'||#{salesCd}||'%'
AND T.PRDT_CD = #{prdtCd}
AND T.ITEM_Div = #{itemDiv}
AND C.PRDT_NM LIKE '%'||#{prdtNm}||'%'
ORDER BY T.CO_CD, T.IMPRVM_DT DESC, T.IMPRVM_ID, T.IMPRVM_TYPE ) AS A WHERE 1 = 1 ) AS A WHERE A.RNUM BETWEEN #{firstIndex} AND #{lastIndex}

mapper.update:
        UPDATE TB_IM01M01
        SET 
			  CO_CD              = #{coCd}
			, IMPRVM_ID          = #{imprvmId}
			, IMPRVM_DEPT_CD     = #{imprvmDeptCd}
			, IMPRVM_DT          = #{imprvmDt}
			, IMPRVM_TYPE        = #{imprvmType}
			, UNIT_NO            = #{unitNo}
			, SALES_CD           = #{salesCd, jdbcType=VARCHAR}
			, PRDT_CD            = #{prdtCd, jdbcType=VARCHAR}
			, PRDT_TYP           = #{prdtTyp, jdbcType=VARCHAR}
			, ITEM_DIV           = #{itemDiv, jdbcType=VARCHAR}
			, MC_PART_CD         = #{mcPartCd}
			, IMPORTANT_CD       = #{importantCd}
			, IMPACT_CD          = #{impactCd}
			, IMPRVM_TXT         = #{imprvmTxt}
			, IMPRVM_RMK         = #{imprvmRmk, jdbcType=VARCHAR}
			, IMPRVM_STS_CD      = #{imprvmStsCd, jdbcType=VARCHAR}
			, IMPRVM_REQ_TXT     = #{imprvmReqTxt, jdbcType=VARCHAR}
			, IMPRVM_REQ_ID      = #{imprvmReqId, jdbcType=VARCHAR}
			, IMPRVM_REQ_DTTM    = #{imprvmReqDttm, jdbcType=VARCHAR}
			, EXEC_ACT_DEPT      = #{execActDept, jdbcType=VARCHAR}
			, EXEC_ACT_TXT       = #{execActTxt, jdbcType=VARCHAR}
			, EXEC_TEAM_TXT      = #{execTeamTxt, jdbcType=VARCHAR}
			, EXEC_TEAM_ID       = #{execTeamId, jdbcType=VARCHAR}
			, EXEC_TEAM_DTTM     = #{execTeamDttm, jdbcType=VARCHAR}
			, IMPRVM_APPR_GRADE  = #{imprvmApprGrade, jdbcType=VARCHAR}
			, IMPRVM_APPR_POINT  = #{imprvmApprPoint, jdbcType=VARCHAR}
			, IMPRVM_APPR_DTTM   = #{imprvmApprDttm, jdbcType=VARCHAR}
			, IMPRVM_APPR_TXT    = #{imprvmApprTxt, jdbcType=VARCHAR}
			, UDT_ID           	 = #{userId}
			, UDT_DTTM           = SYSDATE
			, UDT_PGM            = #{pgmId}
        WHERE IMPRVM_NO = #{imprvmNo}

		UPDATE TB_IM01M01
           SET  IMPRVM_STS_CD = DECODE(IMPRVM_STS_CD,'ISSSTS03','ISSSTS03','ISSSTS02')
		 WHERE  IMPRVM_NO =  #{todoNo}

		UPDATE TB_IM01M01
           SET  IMPRVM_STS_CD = 'ISSSTS03'
		 WHERE  IMPRVM_NO =  #{todoNo}

		UPDATE TB_IM01M01
           SET  IMPRVM_REQ_TXT 	= #{todoCfOpn, jdbcType=VARCHAR}
           	  , IMPRVM_REQ_DTTM = SYSDATE
           	  , IMPRVM_STS_CD = DECODE(IMPRVM_STS_CD,'ISSSTS03','ISSSTS03','ISSSTS02')
		 WHERE  IMPRVM_NO =  #{todoNo}
		   AND  IMPRVM_REQ_ID = #{todoId}

		UPDATE TB_IM01M01
           SET  EXEC_TEAM_TXT 	= #{todoCfOpn, jdbcType=VARCHAR}
           	  , EXEC_TEAM_DTTM  = SYSDATE
           	  , IMPRVM_STS_CD = 'ISSSTS03'
		 WHERE  IMPRVM_NO =  #{todoNo}
		   AND  EXEC_TEAM_ID = #{todoId}

UPDATE TB_WB20M03
SET    ETC_FIELD3 = 'DEL'
WHERE  TODO_FILE_TRGT_KEY = #{reqNo}
AND    TODO_NO = #{reqNo}
AND    (TODO_DIV2_CODE_ID = 'TODODIV1150' OR TODO_DIV2_CODE_ID = 'TODODIV2150' OR
        TODO_DIV2_CODE_ID = 'TODODIV1160' OR TODO_DIV2_CODE_ID = 'TODODIV2160')
