mapper.select:
	SELECT 
 		COUNT(*) 
	  FROM TB_CM10M01 T


	 	AND CO_CD = #{coCd}


	 	AND CREAT_ID = #{creatId}

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT MAIL_KEY, MAIL_FROM, MAIL_TO, MAIL_CC, MAIL_BCC, MAIL_TITLE, MAIL_CNTS, MAIL_ATTACH, SEND_DTTM, ERROR_YN, ERROR_TEXT, CO_CD, SEND_TYPE, SEND_KEY, CREAT_ID, CREAT_PGM, CREAT_DTTM, UDT_ID, UDT_PGM, UDT_DTTM, MAIL_CODE FROM TB_CM10M01 T
AND CO_CD = #{coCd}
AND CREAT_ID = #{creatId}
ORDER BY CO_CD, CREAT_ID, SEND_DTTM, MAIL_FROM ) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}
