AND T.CO_CD = #{coCd}
AND T.WORK_RPT_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD')
AND T.WORK_RPT_ID = #{workRptId}
AND T.SALES_CD LIKE '%'||#{salesCd}||'%'
AND T.PRDT_CD = #{prdtCd}
AND T.ITEM_DIV = #{itemDiv}
AND T.WORK_RPT_L = #{workRptL}
AND T.WORK_RPT_M = #{workRptM}
AND T.WORK_RPT_S = #{workRptS}
AND B.NAME LIKE '%'||#{workRptIdNm}||'%'
AND C.PRDT_NM LIKE '%'||#{prdtNm}||'%'
ORDER BY T.CO_CD, T.WORK_RPT_ID, T.WORK_RPT_DT, T.WORK_RPT_L, T.WORK_RPT_M, T.WORK_RPT_S, T.WORK_RPT_NO DESC, T.FILE_TRGT_KEY DESC
ORDER BY T.CO_CD, T.WORK_RPT_L, T.WORK_RPT_M, T.WORK_RPT_S, T.WORK_RPT_NO DESC, T.FILE_TRGT_KEY DESC
) AS A WHERE 1 = 1 ) AS A WHERE A.RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT COUNT(FILE_TRGT_KEY) CNT
FROM TB_PM01M01
WHERE FILE_TRGT_KEY = #{fileTrgtKey}

SELECT CO_CD, DEPT_ID, MAX(DEPT_NM) DEPT_NM, LEVEL_CD, (SELECT LEVEL_NM FROM TB_CM07M01 i WHERE T.LEVEL_CD = i.LEVEL_CD) LEVEL_NM, ID, NAME, SUM(CASE WHEN DD = '01' THEN WORK_TIME ELSE '' END) AS "1", SUM(CASE WHEN DD = '02' THEN WORK_TIME ELSE '' END) AS "2", SUM(CASE WHEN DD = '03' THEN WORK_TIME ELSE '' END) AS "3", SUM(CASE WHEN DD = '04' THEN WORK_TIME ELSE '' END) AS "4", SUM(CASE WHEN DD = '05' THEN WORK_TIME ELSE '' END) AS "5", SUM(CASE WHEN DD = '06' THEN WORK_TIME ELSE '' END) AS "6", SUM(CASE WHEN DD = '07' THEN WORK_TIME ELSE '' END) AS "7", SUM(CASE WHEN DD = '08' THEN WORK_TIME ELSE '' END) AS "8", SUM(CASE WHEN DD = '09' THEN WORK_TIME ELSE '' END) AS "9", SUM(CASE WHEN DD = '10' THEN WORK_TIME ELSE '' END) AS "10", SUM(CASE WHEN DD = '11' THEN WORK_TIME ELSE '' END) AS "11", SUM(CASE WHEN DD = '12' THEN WORK_TIME ELSE '' END) AS "12", SUM(CASE WHEN DD = '14' THEN WORK_TIME ELSE '' END) AS "14", SUM(CASE WHEN DD = '13' THEN WORK_TIME ELSE '' END) AS "13", SUM(CASE WHEN DD = '15' THEN WORK_TIME ELSE '' END) AS "15", SUM(CASE WHEN DD = '16' THEN WORK_TIME ELSE '' END) AS "16", SUM(CASE WHEN DD = '17' THEN WORK_TIME ELSE '' END) AS "17", SUM(CASE WHEN DD = '18' THEN WORK_TIME ELSE '' END) AS "18", SUM(CASE WHEN DD = '19' THEN WORK_TIME ELSE '' END) AS "19", SUM(CASE WHEN DD = '20' THEN WORK_TIME ELSE '' END) AS "20", SUM(CASE WHEN DD = '21' THEN WORK_TIME ELSE '' END) AS "21", SUM(CASE WHEN DD = '22' THEN WORK_TIME ELSE '' END) AS "22", SUM(CASE WHEN DD = '23' THEN WORK_TIME ELSE '' END) AS "23", SUM(CASE WHEN DD = '24' THEN WORK_TIME ELSE '' END) AS "24", SUM(CASE WHEN DD = '25' THEN WORK_TIME ELSE '' END) AS "25", SUM(CASE WHEN DD = '26' THEN WORK_TIME ELSE '' END) AS "26", SUM(CASE WHEN DD = '27' THEN WORK_TIME ELSE '' END) AS "27", SUM(CASE WHEN DD = '28' THEN WORK_TIME ELSE '' END) AS "28", SUM(CASE WHEN DD = '29' THEN WORK_TIME ELSE '' END) AS "29", SUM(CASE WHEN DD = '30' THEN WORK_TIME ELSE '' END) AS "30", SUM(CASE WHEN DD = '31' THEN WORK_TIME ELSE '' END) AS "31", SUM(HOLY_TIME) AS HOLY_TIME, nvl(SUM(WORK_TIME),0) AS tot FROM ( SELECT A.CO_CD, A.DEPT_ID, A.LEVEL_CD, A.ID, A.NAME, DD, WORK_TIME, HOLY_TIME, C.DEPT_NM, CODE FROM TB_CM06M01 A LEFT OUTER JOIN ( SELECT CO_CD, WORK_RPT_ID, WORK_RPT_DT, SUBSTRING(WORK_RPT_DT,9,2) AS DD, CASE WHEN SUBSTRING(WORK_RPT_S, LENGTH(WORK_RPT_S) - 3, 4) = '9901' THEN 0 ELSE WORK_RPT_HOUR END AS WORK_TIME, --휴가인경우에는 근무시간을 빼야하는데??? CASE WHEN SUBSTRING(WORK_RPT_S, LENGTH(WORK_RPT_S) - 3, 4) = '9901' THEN WORK_RPT_HOUR ELSE 0 END AS HOLY_TIME, --휴가인경우에는 근무시간을 빼야하는데??? WORK_RPT_S, -- 근무코드 SUBSTRING(WORK_RPT_S, LENGTH(WORK_RPT_S) - 3, 4) AS CODE --근무코드 뒤에4자리 9901은 휴가 임 FROM TB_PM01M01 P WHERE 1=1 AND P.WORK_RPT_DT BETWEEN #{workYm} || '-01' AND LAST_DAY(TO_DATE(#{workYm} || '-01', 'YYYY-MM-DD')) ) B ON A.ID = B.WORK_RPT_ID -- AND A.CO_CD = B.CO_CD LEFT OUTER JOIN TB_CM04M01 C ON A.DEPT_ID = C.DEPT_ID WHERE 1=1
AND A.DEPT_ID BETWEEN substring(#{jwtDept},0,5) AND substring(#{jwtDept},0,5) || 'ZZ'
AND A.ID = #{userId}
AND A.DEPT_ID BETWEEN #{deptId} AND #{deptId} || 'ZZ'
AND C.DEPT_NM LIKE '%'||#{deptNm}||'%'
) T GROUP BY CO_CD, DEPT_ID, LEVEL_CD, ID, NAME ORDER BY LEVEL_CD DESC, ID, NAME

