AND T.WORK_RPT_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD')
AND T.WORK_RPT_ID = #{workRptId}
AND T.SALES_CD LIKE '%'||#{salesCd}||'%'
AND CR.PRDT_CD = #{prdtCd}
AND CR.ITEM_DIV = #{itemDiv}
AND T.WORK_RPT_L = #{workRptL}
AND T.WORK_RPT_M = #{workRptM}
AND T.WORK_RPT_S = #{workRptS}
AND B.NAME LIKE '%'||#{workRptIdNm}||'%'
AND C.PRDT_NM LIKE '%'||#{prdtNm}||'%'
ORDER BY T.CO_CD, T.WORK_RPT_ID, T.WORK_RPT_DT, T.WORK_RPT_L, T.WORK_RPT_M, T.WORK_RPT_S, T.WORK_RPT_NO DESC, T.FILE_TRGT_KEY DESC
ORDER BY T.CO_CD, T.WORK_RPT_L, T.WORK_RPT_M, T.WORK_RPT_S, T.WORK_RPT_NO DESC, T.FILE_TRGT_KEY DESC
) AS A WHERE 1 = 1 ) AS A WHERE 1 = 1

SELECT A.* FROM ( SELECT ROWNUM AS RNUM , A.* FROM ( SELECT T.FILE_TRGT_KEY , T.CO_CD , FN_CM05M01_CD_TO_NM(T.CO_CD) CO_NM , T.WORK_RPT_NO , T.WORK_RPT_ID , B.NAME AS WORK_RPT_ID_NM , TO_CHAR(T.WORK_RPT_DT,'YYYY-MM-DD') AS WORK_RPT_DT , T.WORK_RPT_L , T.WORK_RPT_M , T.WORK_RPT_S , FN_CM05M01_CD_TO_NM(T.WORK_RPT_L) AS WORK_RPT_L_NM , FN_CM05M01_CD_TO_NM(T.WORK_RPT_M) AS WORK_RPT_M_NM , FN_CM05M01_CD_TO_NM(T.WORK_RPT_S) AS WORK_RPT_S_NM , T.ORDRS_NO , T.WORK_RPT_HOUR , T.SALES_CD , T.PRDT_CD , C.PRDT_NM AS PRDT_CD_NM , T.ITEM_DIV , FN_CM05M01_CD_TO_NM(T.ITEM_DIV) AS ITEM_DIV_NM , T.WORK_RPT_RMK , T.WORK_RPT_RMK , T.ETC_FIELD1 , T.ETC_FIELD2 , T.ETC_FIELD3 , T.CREAT_ID , FN_CM06M01_ID_TO_NM(T.CREAT_ID) AS CREAT_NM , TO_CHAR(T.CREAT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS CREAT_DTTM , T.UDT_ID , FN_CM06M01_ID_TO_NM(T.UDT_ID) AS UDT_NM , TO_CHAR(T.UDT_DTTM, 'YYYY-MM-DD HH24:MI:SS') AS UDT_DTTM , T.ISSUE_TITLE FROM TB_PM01M01 AS T LEFT OUTER JOIN TB_CR02D02 AS CR ON T.CO_CD = CR.CO_CD AND T.SALES_CD = CR.SALES_CD LEFT OUTER JOIN TB_CR02M01 AS Y ON T.CO_CD = Y.CO_CD AND T.ORDRS_NO = Y.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 AS A ON T.CLNT_CD = A.CLNT_CD LEFT OUTER JOIN TB_CM06M01 AS B ON T.WORK_RPT_ID = B.ID LEFT OUTER JOIN TB_BM01M01 AS C ON T.PRDT_CD = C.PRDT_CD WHERE T.ISSUE_YN = 'Y'
AND T.CO_CD = #{coCd}
AND T.WORK_RPT_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD')
AND T.WORK_RPT_ID = #{workRptId}
AND T.SALES_CD LIKE '%'||#{salesCd}||'%'
AND T.PRDT_CD = #{prdtCd}
AND T.ITEM_DIV = #{itemDiv}
AND T.WORK_RPT_L = #{workRptL}
AND T.WORK_RPT_M = #{workRptM}
AND T.WORK_RPT_S = #{workRptS}
AND B.NAME LIKE '%'||#{workRptIdNm}||'%'
AND C.PRDT_NM LIKE '%'||#{prdtNm}||'%'
ORDER BY T.CO_CD, T.WORK_RPT_ID, T.WORK_RPT_DT, T.WORK_RPT_L, T.WORK_RPT_M, T.WORK_RPT_S, T.WORK_RPT_NO DESC, T.FILE_TRGT_KEY DESC
ORDER BY T.CO_CD, T.WORK_RPT_L, T.WORK_RPT_M, T.WORK_RPT_S, T.WORK_RPT_NO DESC, T.FILE_TRGT_KEY DESC
) AS A WHERE 1 = 1 ) AS A WHERE A.RNUM BETWEEN #{firstIndex} AND #{lastIndex}
