mapper.select:
        SELECT
        		COUNT(*)
	        FROM TB_PM01M01 T
			        		LEFT OUTER JOIN TB_CR02D02 CR ON T.CO_CD    = CR.CO_CD
			        								     AND T.SALES_CD = CR.SALES_CD
			        		LEFT OUTER JOIN TB_CR02M01 Y  ON CR.CO_CD    = Y.CO_CD
                                                         AND CR.ORDRS_NO = Y.ORDRS_NO
                            LEFT OUTER JOIN TB_BM02M01 A  ON Y.ORDRS_CLNT_CD = A.CLNT_CD    
        WHERE 1=1

            AND T.CO_CD = #{coCd}


            AND T.WORK_RPT_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD')


            AND T.WORK_RPT_ID = #{workRptId}


            AND T.SALES_CD = #{salesCd}


            AND A.PRDT_CD = #{prdtCd}


            AND A.ITEM_DIV = #{itemDiv}


            AND T.WORK_RPT_L = #{workRptL}


            AND T.WORK_RPT_M = #{workRptM}


            AND T.WORK_RPT_S = #{workRptS}

/* 작업일보 내역 분석 */ WITH DPT AS ( SELECT A.CO_CD , A.NAME AS NAME , A.ID AS ID , A.DEPT_ID AS DEPT_ID , B.DEPT_NM AS DEPT_NM , SUBSTR(A.DEPT_ID,1,5) AS UP_DEPT FROM TB_CM06M01 A INNER JOIN TB_CM04M01 AS B ON (A.DEPT_ID = B.DEPT_ID ) WHERE A.CO_CD = #{coCd} AND A.DEPT_ID LIKE (SELECT SUBSTR(DEPT_ID,1,5)||'%' FROM TB_CM06M01 WHERE ID = #{userId} ) ) , PCODE AS ( SELECT LEVEL - 1 AS LVL , T.CODE_KIND , T.CODE_DESC , T.CODE_ID , T.CODE_NM , T.SORT_NO FROM TB_CM05M01 T WHERE USE_YN = 'Y' AND LEVEL = 2 START WITH T.CODE_KIND = (SELECT MIN(CODE_ID) FROM TB_CM05M01 WHERE CODE_RPRC = (SELECT MAX(UP_DEPT) FROM DPT) AND CODE_KIND = 'WORKRPT') CONNECT BY PRIOR T.CODE_ID = T.CODE_KIND ) , SJD AS --수주상세정보 ( SELECT X.CO_CD AS CO_CD , Y.ORDRS_CLNT_CD AS CLNT_CD , A.CLNT_NM AS CLNT_NM , X.SALES_CD AS SALES_CD , (SELECT FN_CM05M01_CD_TO_NM(Y.CLNT_PJT) FROM DUAL) AS PJT_NM , X.PRDT_CD AS PRDT_CD , (SELECT FN_BM01M01_ID_TO_NM(X.PRDT_CD) FROM DUAL) AS PRDT_CD_NM , X.ITEM_DIV AS ITEM_DIV , (SELECT FN_CM05M01_CD_TO_NM(X.ITEM_DIV) FROM DUAL) AS ITEM_DIV_NM , X.EQP_NM AS EQP_NM FROM TB_CR02D02 X --수주상세 INNER JOIN TB_CR02M01 AS Y ON (X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO) INNER JOIN TB_BM02M01 AS A ON (Y.ORDRS_CLNT_CD = A.CLNT_CD) WHERE X.CO_CD = #{coCd} ) , PDS AS --생산일보 ( SELECT T.CO_CD AS CO_CD , T.SALES_CD AS SALES_CD , T.WORK_RPT_L AS WORK_RPT_L , T.WORK_RPT_M AS WORK_RPT_M , T.WORK_RPT_S AS WORK_RPT_S , T.WORK_RPT_HOUR AS WORK_RPT_HOUR , T.WORK_RPT_ID AS WORK_RPT_ID , (SELECT FN_CM06M01_ID_TO_NM(T.CREAT_ID) FROM DUAL) AS WORK_RPT_NM , TO_CHAR(T.WORK_RPT_DT,'YYYY-MM-DD') AS WORK_RPT_DT , CR.CLNT_NM AS CLNT_NM , CR.PJT_NM AS CLNT_PJT , CR.PRDT_CD_NM AS PRDT_CD_NM , CR.ITEM_DIV_NM AS ITEM_DIV_NM , CR.EQP_NM AS EQP_NM FROM TB_PM01M01 T LEFT OUTER JOIN SJD AS CR ON ( T.CO_CD = CR.CO_CD AND T.SALES_CD = CR.SALES_CD ) INNER JOIN DPT AS DP ON ( T.CO_CD = DP.CO_CD AND T.WORK_RPT_ID = DP.ID AND T.WORK_RPT_L = DP.UP_DEPT) WHERE 1=1 AND T.WORK_RPT_DT >= TO_DATE( #{reqDtFrom},'YYYYMMDD') AND T.WORK_RPT_DT <= TO_DATE( #{reqDtTo},'YYYYMMDD') AND T.CO_CD = #{coCd} ) SELECT * FROM ( SELECT C.CODE_KIND --중분류코드 , C.CODE_DESC --중분류명 , C.CODE_ID --소분류코드 , C.CODE_NM --소분류명 , P.SALES_CD , P.CLNT_NM , P.CLNT_PJT , P.PRDT_CD_NM , P.ITEM_DIV_NM , P.EQP_NM , P.WORK_RPT_NM --작성자명 , P.WORK_RPT_S --작업일보소분류 , P.WORK_RPT_HOUR --작업시간 , C.CODE_KIND||C.CODE_ID||C.SORT_NO AS ORD_SEQ , (select count(CODE_KIND) from PCODE a where C.CODE_KIND = a.CODE_KIND) as ROW_CNT FROM PDS P , PCODE C WHERE 1=1 AND C.CODE_ID = P.WORK_RPT_S(+) AND C.CODE_KIND = P.WORK_RPT_M(+) ) PIVOT (SUM(WORK_RPT_HOUR) FOR WORK_RPT_NM IN ( ${test} ) --해당부서 인원 읽어와서 넣어주기 )
AND CODE_KIND = #{workRptm}
AND CODE_ID = #{workRpts}
AND SALES_CD = #{salesCd}
ORDER BY ORD_SEQ
