mapper.select:
		SELECT
            count(*)
		FROM TB_QM01M01 T
		 LEFT OUTER JOIN TB_QM01P01 P ON P.REQ_NO = T.REQ_NO
		 LEFT OUTER JOIN TB_CR02D02 S ON T.SALES_CD = S.SALES_CD  
		 LEFT OUTER JOIN TB_CR02M01 SM ON S.CO_CD =  SM.CO_CD  AND S.ORDRS_NO = SM.ORDRS_NO
		 LEFT OUTER JOIN TB_BM01M01 B ON S.PRDT_CD = B.PRDT_CD  
		 LEFT OUTER JOIN TB_CM05M01 C ON C.CODE_ID  = T.REQ_ST AND C.USE_YN='Y' AND C.CODE_KIND = 'REQST'       
		 LEFT OUTER JOIN TB_CM05M01 C1 ON C1.CODE_ID = T.BUST_CD AND C1.USE_YN='Y' AND C1.CODE_KIND = 'INWKCD' 
		 LEFT OUTER JOIN TB_CM05M01 C2 ON C2.CODE_ID = T.INWK_CD AND C2.USE_YN='Y' AND C2.CODE_KIND = 'INWKCD'		
		 LEFT OUTER JOIN TB_CM05M01 C3 ON C3.CODE_ID = T.DEPT_CD AND C3.USE_YN='Y' AND C3.CODE_KIND = 'COBGB'
		 LEFT OUTER JOIN TB_CM05M01 C4 ON C4.CODE_ID = T.MID_CD AND C4.USE_YN='Y' 
		 LEFT OUTER JOIN TB_CM05M01 C5 ON C5.CODE_ID = T.SUB_CD AND C5.USE_YN='Y' 	
		 LEFT OUTER JOIN TB_CM05M01 C6 ON C6.CODE_ID = T.PART_CD AND C6.USE_YN='Y' AND C6.CODE_KIND = 'COBTP'	 
		 LEFT OUTER JOIN TB_CM06M01 N1 ON N1.ID = T.REG_ID  AND N1.CO_CD =  T.CO_CD
		 LEFT OUTER JOIN TB_CM06M01 N2 ON N2.ID = T.ORDRG_ID  AND N2.CO_CD =  T.CO_CD
		 LEFT OUTER JOIN TB_CM06M01 N3 ON N3.ID = T.CREAT_ID  AND N3.CO_CD =  T.CO_CD
		 LEFT OUTER JOIN TB_BM02M01 CT ON CT.CLNT_CD = T.EST_CLNT_CD AND CT.USE_YN = 'Y' 
		 WHERE 1=1 

		  AND T.CO_CD = #{coCd}


          AND T.ORDRG_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYY-MM-DD') AND TO_DATE( #{reqDtTo},'YYYY-MM-DD') +1


          AND T.ORDRG_ID = #{ordrgId}


          AND T.SALES_CD  LIKE '%${salesCd}%'



          AND B.PRDT_GRP = #{prdtCd}


          AND P.PRDT_NM LIKE '%${prdtNm}%'


          AND S.ITEM_DIV = #{itemCd}


          AND (T.SHAR_ID1 = #{sharId}           
                OR T.SHAR_ID2 = #{sharId} 
                OR T.SHAR_ID3 = #{sharId} 
                OR T.SHAR_ID4 = #{sharId} 
                OR T.SHAR_ID5 = #{sharId} 
                OR T.SHAR_ID6 = #{sharId} 
                OR T.SHAR_ID7 = #{sharId} 
                OR T.SHAR_ID8 = #{sharId} 
                OR T.SHAR_ID9 = #{sharId} 
                OR T.SHAR_ID10 = #{sharId} ) 


          AND T.PROJ_NM LIKE '%${clntPjt}%' 


          AND S.EQP_NM  LIKE '%${eqpNm}%'  


          AND T.EST_CLNT_CD = #{clntCd}


          AND T.PART_CD = #{partCd}

SELECT * FROM( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT T.FILE_TRGT_KEY AS FILE_TRGT_KEY, T.CO_CD AS CO_CD, T.REQ_NO AS REQ_NO, T.INWK_CD AS INWK_CD, T.BUST_CD AS BUST_CD, NVL(T.SALES_CD,' ') AS SALES_CD, T.REQ_ST AS REQ_ST, T.REQ_CD AS REQ_CD, T.REG_ID AS REQ_ID, T.ORDRG_ID AS ORDRG_ID, T.DEPT_CD AS DEPT_CD, T.MID_CD AS MID_CD, T.SUB_CD AS SUB_CD, T.EST_CLNT_CD AS CLNT_CD, P.CREAT_ID AS CREAT_ID, T.PART_CD AS COBTB_CD, S.ITEM_DIV AS ITEM_DIV, FN_CM05M01_CD_TO_NM(S.ITEM_DIV) AS ITEM_DIV_NM, S.PRDT_CD AS PRDT_CD1, B.PRDT_GRP AS PRDT_CD, TO_CHAR(T.ORDRG_DT, 'YYYY-MM-DD') AS ORDRG_DT, T.RSLT_YN AS RSLT_YN, DECODE(T.REQ_CD,'REQCD01','Y',' ') AS REQ_1YN, DECODE(T.REQ_CD,'REQCD02','Y',' ') AS REQ_2YN, CASE WHEN (T.INWK_CD = 'INWK01' OR INWK_CD = 'INWK02') THEN 'Y' ELSE ' ' END AS INWK_YN, NVL(C1.CODE_NM,' ') AS BUST_NM, NVL(C2.CODE_NM,' ') AS INWK_NM, N1.NAME AS REG_NM, N2.NAME AS ORDRG_NM, C3.CODE_NM AS DEPT_NM, C4.CODE_NM AS MID_NM, C5.CODE_NM AS SUB_NM, CT.CLNT_NM AS CLNT_NM, T.PROJ_NM AS CLNT_PJT, T.MATR_DRW_NO AS MATR_DRW_NO, NVL(S.EQP_NM,' ') AS EQP_NM, C6.CODE_NM AS PART_NM, C.CODE_NM AS CODE_NM, T.REQ_RMK AS REQ_RMK, P.CREAT_DTTM AS CREAT_DTTM, N3.NAME AS CREAT_NM, NVL(P.RESLT_RST,' ') AS RESLT_RST FROM TB_QM01M01 T LEFT OUTER JOIN TB_QM01P01 P ON P.REQ_NO = T.REQ_NO LEFT OUTER JOIN TB_CR02D02 S ON T.SALES_CD = S.SALES_CD LEFT OUTER JOIN TB_CR02M01 SM ON S.CO_CD = SM.CO_CD AND S.ORDRS_NO = SM.ORDRS_NO LEFT OUTER JOIN TB_BM01M01 B ON S.PRDT_CD = B.PRDT_CD LEFT OUTER JOIN TB_CM05M01 C ON C.CODE_ID = T.REQ_ST AND C.USE_YN='Y' AND C.CODE_KIND = 'REQST' LEFT OUTER JOIN TB_CM05M01 C1 ON C1.CODE_ID = T.BUST_CD AND C1.USE_YN='Y' AND C1.CODE_KIND = 'INWKCD' LEFT OUTER JOIN TB_CM05M01 C2 ON C2.CODE_ID = T.INWK_CD AND C2.USE_YN='Y' AND C2.CODE_KIND = 'INWKCD' LEFT OUTER JOIN TB_CM05M01 C3 ON C3.CODE_ID = T.DEPT_CD AND C3.USE_YN='Y' AND C3.CODE_KIND = 'COBGB' LEFT OUTER JOIN TB_CM05M01 C4 ON C4.CODE_ID = T.MID_CD AND C4.USE_YN='Y' LEFT OUTER JOIN TB_CM05M01 C5 ON C5.CODE_ID = T.SUB_CD AND C5.USE_YN='Y' LEFT OUTER JOIN TB_CM05M01 C6 ON C6.CODE_ID = T.PART_CD AND C6.USE_YN='Y' AND C6.CODE_KIND = 'COBTP' LEFT OUTER JOIN TB_CM06M01 N1 ON N1.ID = T.REG_ID AND N1.CO_CD = T.CO_CD LEFT OUTER JOIN TB_CM06M01 N2 ON N2.ID = T.ORDRG_ID AND N2.CO_CD = T.CO_CD LEFT OUTER JOIN TB_CM06M01 N3 ON N3.ID = T.CREAT_ID AND N3.CO_CD = T.CO_CD LEFT OUTER JOIN TB_BM02M01 CT ON CT.CLNT_CD = T.EST_CLNT_CD AND CT.USE_YN = 'Y' WHERE 1=1
AND T.CO_CD = #{coCd}
AND T.ORDRG_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYY-MM-DD') AND TO_DATE( #{reqDtTo},'YYYY-MM-DD') +1
AND T.ORDRG_ID = #{ordrgId}
AND T.SALES_CD LIKE '%${salesCd}%'
AND B.PRDT_GRP = #{prdtCd}
AND P.PRDT_NM LIKE '%${prdtNm}%'
AND S.ITEM_DIV = #{itemCd}
AND (T.SHAR_ID1 = #{sharId} OR T.SHAR_ID2 = #{sharId} OR T.SHAR_ID3 = #{sharId} OR T.SHAR_ID4 = #{sharId} OR T.SHAR_ID5 = #{sharId} OR T.SHAR_ID6 = #{sharId} OR T.SHAR_ID7 = #{sharId} OR T.SHAR_ID8 = #{sharId} OR T.SHAR_ID9 = #{sharId} OR T.SHAR_ID10 = #{sharId} )
AND T.PROJ_NM LIKE '%${clntPjt}%'
AND S.EQP_NM LIKE '%${eqpNm}%'
AND T.EST_CLNT_CD = #{clntCd}
AND T.PART_CD = #{partCd}
ORDER BY T.CO_CD, T.REQ_NO ) AS a ) A WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT T.FILE_TRGT_KEY AS FILE_TRGT_KEY, T.CO_CD AS CO_CD, T.REQ_NO AS REQ_NO, T.INWK_CD AS INWK_CD, T.BUST_CD AS BUST_CD, NVL(T.SALES_CD,' ') AS SALES_CD, T.REQ_ST AS REQ_ST, T.REQ_CD AS REQ_CD, T.REG_ID AS REQ_ID, T.ORDRG_ID AS ORDRG_ID, T.DEPT_CD AS DEPT_CD, T.MID_CD AS MID_CD, T.SUB_CD AS SUB_CD, T.EST_CLNT_CD AS CLNT_CD, P.CREAT_ID AS CREAT_ID, T.PART_CD AS COBTB_CD, S.ITEM_DIV AS ITEM_DIV, FN_CM05M01_CD_TO_NM(S.ITEM_DIV) AS ITEM_DIV_NM, S.PRDT_CD AS PRDT_CD1, B.PRDT_GRP AS PRDT_CD, TO_CHAR(T.ORDRG_DT, 'YYYY-MM-DD') AS ORDRG_DT, T.RSLT_YN AS RSLT_YN, DECODE(T.REQ_CD,'REQCD01','Y',' ') AS REQ_1YN, DECODE(T.REQ_CD,'REQCD02','Y',' ') AS REQ_2YN, CASE WHEN (T.INWK_CD = 'INWK01' OR INWK_CD = 'INWK02') THEN 'Y' ELSE ' ' END AS INWK_YN, NVL(C1.CODE_NM,' ') AS BUST_NM, NVL(C2.CODE_NM,' ') AS INWK_NM, N1.NAME AS REG_NM, N2.NAME AS ORDRG_NM, C3.CODE_NM AS DEPT_NM, C4.CODE_NM AS MID_NM, C5.CODE_NM AS SUB_NM, CT.CLNT_NM AS CLNT_NM, T.PROJ_NM AS CLNT_PJT, T.MATR_DRW_NO AS MATR_DRW_NO, NVL(S.EQP_NM,' ') AS EQP_NM, C6.CODE_NM AS PART_NM, C.CODE_NM AS CODE_NM, T.REQ_RMK AS REQ_RMK, P.CREAT_DTTM AS CREAT_DTTM, N3.NAME AS CREAT_NM, NVL(P.RESLT_RST,' ') AS RESLT_RST FROM TB_QM01M01 T LEFT OUTER JOIN TB_QM01P01 P ON P.REQ_NO = T.REQ_NO LEFT OUTER JOIN TB_CR02D02 S ON T.SALES_CD = S.SALES_CD LEFT OUTER JOIN TB_CR02M01 SM ON S.CO_CD = SM.CO_CD AND S.ORDRS_NO = SM.ORDRS_NO LEFT OUTER JOIN TB_BM01M01 B ON S.PRDT_CD = B.PRDT_CD LEFT OUTER JOIN TB_CM05M01 C ON C.CODE_ID = T.REQ_ST AND C.USE_YN='Y' AND C.CODE_KIND = 'REQST' LEFT OUTER JOIN TB_CM05M01 C1 ON C1.CODE_ID = T.BUST_CD AND C1.USE_YN='Y' AND C1.CODE_KIND = 'INWKCD' LEFT OUTER JOIN TB_CM05M01 C2 ON C2.CODE_ID = T.INWK_CD AND C2.USE_YN='Y' AND C2.CODE_KIND = 'INWKCD' LEFT OUTER JOIN TB_CM05M01 C3 ON C3.CODE_ID = T.DEPT_CD AND C3.USE_YN='Y' AND C3.CODE_KIND = 'COBGB' LEFT OUTER JOIN TB_CM05M01 C4 ON C4.CODE_ID = T.MID_CD AND C4.USE_YN='Y' LEFT OUTER JOIN TB_CM05M01 C5 ON C5.CODE_ID = T.SUB_CD AND C5.USE_YN='Y' LEFT OUTER JOIN TB_CM05M01 C6 ON C6.CODE_ID = T.PART_CD AND C6.USE_YN='Y' AND C6.CODE_KIND = 'COBTP' LEFT OUTER JOIN TB_CM06M01 N1 ON N1.ID = T.REG_ID AND N1.CO_CD = T.CO_CD LEFT OUTER JOIN TB_CM06M01 N2 ON N2.ID = T.ORDRG_ID AND N2.CO_CD = T.CO_CD LEFT OUTER JOIN TB_CM06M01 N3 ON N3.ID = T.CREAT_ID AND N3.CO_CD = T.CO_CD LEFT OUTER JOIN TB_BM02M01 CT ON CT.CLNT_CD = T.EST_CLNT_CD AND CT.USE_YN = 'Y' WHERE 1=1
AND T.CO_CD = #{coCd}
AND T.ORDRG_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYY-MM-DD') AND TO_DATE( #{reqDtTo},'YYYY-MM-DD') +1
AND T.ORDRG_ID = #{ordrgId}
AND T.SALES_CD LIKE '%${salesCd}%'
AND B.PRDT_GRP = #{prdtCd}
AND P.PRDT_NM LIKE '%${prdtNm}%'
AND S.ITEM_DIV = #{itemCd}
AND (T.SHAR_ID1 = #{sharId} OR T.SHAR_ID2 = #{sharId} OR T.SHAR_ID3 = #{sharId} OR T.SHAR_ID4 = #{sharId} OR T.SHAR_ID5 = #{sharId} OR T.SHAR_ID6 = #{sharId} OR T.SHAR_ID7 = #{sharId} OR T.SHAR_ID8 = #{sharId} OR T.SHAR_ID9 = #{sharId} OR T.SHAR_ID10 = #{sharId} )
AND T.PROJ_NM LIKE '%${clntPjt}%'
AND S.EQP_NM LIKE '%${eqpNm}%'
AND T.EST_CLNT_CD = #{clntCd}
AND T.PART_CD = #{partCd}

SELECT * FROM( SELECT ROWNUM AS RNUM, A.*
FROM ( SELECT T.FILE_TRGT_KEY, T.CO_CD, FN_CM05M01_CD_TO_NM(T.CO_CD) CO_NM, T.COST_NO, T.SALES_CD, T.PCHS_CLNT_CD, A.CLNT_NM PCHS_CLNT_NM, TO_CHAR(T.COST_DT, 'YYYY-MM-DD') AS COST_DT, T.PCHS_COST_DIV10, FN_CM05M01_CD_TO_NM(T.PCHS_COST_DIV10) PCHS_COST_DIV10_NM, T.PCHS_COST_DIV20, FN_CM05M01_CD_TO_NM(T.PCHS_COST_DIV20) PCHS_COST_DIV20_NM, T.CURR_CD, FN_CM05M01_CD_TO_NM(T.CURR_CD) CURR_NM, T.EXRATE, T.COST_AMT, T.MATR_CD, M.MATR_NM, T.COST_RMK, T.ETC_FIELD1, T.ETC_FIELD2, T.ETC_FIELD3, T.CREAT_ID, B.NAME CREAT_NM, T.CREAT_PGM, TO_CHAR(T.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM, T.UDT_ID, C.NAME UDT_NM, T.UDT_PGM, TO_CHAR(T.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM FROM TB_SM10M01 T LEFT OUTER JOIN TB_BM02M01 A ON T.PCHS_CLNT_CD = A.CLNT_CD LEFT OUTER JOIN TB_CM06M01 B ON T.CREAT_ID = B.ID LEFT OUTER JOIN TB_CM06M01 C ON T.UDT_ID = C.ID
LEFT OUTER JOIN TB_BM05M01 M ON T.MATR_CD = M.MATR_CD --자재마스터 LEFT OUTER JOIN TB_CR02D02 S ON T.SALES_CD = S.SALES_CD --수주상세(Sales Code) LEFT OUTER JOIN TB_CR02M01 SM ON S.CO_CD = SM.CO_CD --수주상세(Sales Code) AND S.ORDRS_NO = SM.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 X ON SM.ORDRS_CLNT_CD = X.CLNT_CD --거래처마스터 LEFT OUTER JOIN TB_BM01M01 P ON S.PRDT_CD = P.PRDT_CD --제품마스터
WHERE 1=1
AND T.CO_CD = #{coCd}
AND T.COST_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD') +1
AND SM.ORDRS_CLNT_CD = #{vendCd}
AND X.CLNT_NM LIKE '%${vendNm}%'
AND T.SALES_CD LIKE '%${salesCd}%'
AND S.PRDT_CD = #{prdtCd}
AND P.PRDT_NM LIKE '%${prdtNm}%'
AND S.ITEM_DIV = #{itemCd}
AND T.PCHS_CLNT_CD = #{clntCd}
AND A.CLNT_NM LIKE '%${clntNm}%'
AND T.PCHS_COST_DIV10 = #{pchsCostDiv10}
AND T.PCHS_COST_DIV20 = #{pchsCostDiv20}
ORDER BY T.CO_CD, T.SALES_CD, T.COST_NO, A.CLNT_NM ) AS a ) A WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT COUNT(FILE_TRGT_KEY) CNT
  FROM TB_SM10M01
 WHERE FILE_TRGT_KEY = #{fileTrgtKey}
