mapper.select:
SELECT count(*) FROM TB_SM10M01 T LEFT OUTER JOIN TB_BM02M01 A ON T.PCHS_CLNT_CD = A.CLNT_CD LEFT OUTER JOIN TB_CM06M01 B ON T.CREAT_ID = B.ID LEFT OUTER JOIN TB_CM06M01 C ON T.UDT_ID = C.ID
LEFT OUTER JOIN TB_BM05M01 M ON T.MATR_CD = M.MATR_CD --자재마스터 LEFT OUTER JOIN TB_CR02D02 S ON T.SALES_CD = S.SALES_CD --수주상세(Sales Code) LEFT OUTER JOIN TB_CR02M01 SM ON S.CO_CD = SM.CO_CD --수주상세(Sales Code) AND S.ORDRS_NO = SM.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 X ON SM.ORDRS_CLNT_CD = X.CLNT_CD --거래처마스터 LEFT OUTER JOIN TB_BM01M01 P ON S.PRDT_CD = P.PRDT_CD --제품마스터 WHERE 1=1
AND T.CO_CD = #{coCd}
AND T.COST_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD') +1
AND SM.ORDRS_CLNT_CD = #{vendCd}
AND X.CLNT_NM LIKE '%${vendNm}%'
AND T.SALES_CD LIKE '%${salesCd}%'
AND S.PRDT_CD = #{prdtCd}
AND P.PRDT_NM LIKE '%${prdtNm}%'
AND S.ITEM_DIV = #{itemCd}
AND T.PCHS_CLNT_CD = #{clntCd}
AND A.CLNT_NM LIKE '%${clntNm}%'
AND T.PCHS_COST_DIV10 = #{pchsCostDiv10}
AND T.PCHS_COST_DIV20 = #{pchsCostDiv20}

SELECT * FROM( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT T.FILE_TRGT_KEY, T.CO_CD, T.REQ_NO, T.INWK_CD, T.BUST_CD, NVL(T.SALES_CD,' ') AS SALES_CD, T.REQ_ST, T.REQ_CD, T.REG_ID, T.ORDRG_ID, T.DEPT_CD, T.MID_CD, T.SUB_CD, T.EST_CLNT_CD AS CLNT_CD, NVL(P.CREAT_ID,' ') AS CREAT_ID, T.ORDRG_DT, T.RSLT_YN, DECODE(T.REQ_CD,'1','Y',' ') AS REQ_1YN, DECODE(T.REQ_CD,'2','Y',' ') AS REQ_2YN, CASE WHEN (T.INWK_CD = '1' OR INWK_CD = '2') THEN 'Y' ELSE ' ' END AS INWK_YN,--사내 NVL(C1.CODE_NM,' ') AS BUST_NM, NVL(C2.CODE_NM,' ') AS INWK_NM, ' ' AS REG_NM, ' ' AS ORDRG_NM, ' ' AS DEPT_NM, ' ' AS MID_NM, ' ' AS SUB_NM, ' ' AS CLNT_NM, T.PROJ_NM AS CLNT_PJT, T.MATR_DRW_NO, NVL(S.EQP_NM,' ') AS EQP_NM, T.PART_CD, C.CODE_NM, T.REQ_RMK, NVL(P.CREAT_DTTM,' ') AS CREAT_DTTM, ' ' AS CREAT_NM, NVL(P.RESLT_RST,' ') AS RESLT_RST FROM TB_QM01M01 T LEFT OUTER JOIN TB_QM01P01 P ON P.REQ_NO = T.REQ_NO LEFT OUTER JOIN TB_CR02D02 S ON T.SALES_CD = S.SALES_CD --수주상세(Sales Code) LEFT OUTER JOIN TB_CR02M01 SM ON S.CO_CD = SM.CO_CD AND S.ORDRS_NO = SM.ORDRS_NO LEFT OUTER JOIN TB_BM01M01 P ON S.PRDT_CD = P.PRDT_CD --제품마스터 LEFT OUTER JOIN TB_CM05M01 C ON C.CODE_ID = T.REQ_ST AND C.USE_YN='Y' AND C.CODE_KIND = 'REQST' --접수상태 LEFT OUTER JOIN TB_CM05M01 C1 ON C1.CODE_ID = T.BUST_CD AND C1.USE_YN='Y' AND C1.CODE_KIND = 'INWKCD' --출장구분 LEFT OUTER JOIN TB_CM05M01 C2 ON C2.CODE_ID = T.INWK_CD AND C2.USE_YN='Y' AND C2.CODE_KIND = 'INWKCD' -- 사내작업구분(1:생산,2:전기) WHERE 1=1
AND T.CO_CD = #{coCd}
AND T.ORDRG_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD') +1
AND T.ORDRG_ID = #{ordrgId}
AND T.SALES_CD LIKE '%${salesCd}%'
AND S.PRDT_CD = #{prdtCd}
AND P.PRDT_NM LIKE '%${prdtNm}%'
AND S.ITEM_DIV = #{itemCd}
AND (SHAR_ID1 = #{sharId} OR SHAR_ID2 = #{sharId} OR SHAR_ID3 = #{sharId} OR SHAR_ID4 = #{sharId} OR SHAR_ID5 = #{sharId} OR SHAR_ID6 = #{sharId} OR SHAR_ID7 = #{sharId} OR SHAR_ID8 = #{sharId} OR SHAR_ID9 = #{sharId} OR SHAR_ID10 = #{sharId} )
AND T.PROJ_NM LIKE '%${clntPjt}%'
AND S.EQP_NM LIKE '%${eqpNm}%'
AND T.PCHS_CLNT_CD = #{clntCd}
AND T.PART_CD = #{partCd}
ORDER BY T.CO_CD, T.REQ_NO ) AS a ) A WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT * FROM( SELECT ROWNUM AS RNUM, A.*
FROM ( SELECT T.FILE_TRGT_KEY, T.CO_CD, FN_CM05M01_CD_TO_NM(T.CO_CD) CO_NM, T.COST_NO, T.SALES_CD, T.PCHS_CLNT_CD, A.CLNT_NM PCHS_CLNT_NM, TO_CHAR(T.COST_DT, 'YYYY-MM-DD') AS COST_DT, T.PCHS_COST_DIV10, FN_CM05M01_CD_TO_NM(T.PCHS_COST_DIV10) PCHS_COST_DIV10_NM, T.PCHS_COST_DIV20, FN_CM05M01_CD_TO_NM(T.PCHS_COST_DIV20) PCHS_COST_DIV20_NM, T.CURR_CD, FN_CM05M01_CD_TO_NM(T.CURR_CD) CURR_NM, T.EXRATE, T.COST_AMT, T.MATR_CD, M.MATR_NM, T.COST_RMK, T.ETC_FIELD1, T.ETC_FIELD2, T.ETC_FIELD3, T.CREAT_ID, B.NAME CREAT_NM, T.CREAT_PGM, TO_CHAR(T.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM, T.UDT_ID, C.NAME UDT_NM, T.UDT_PGM, TO_CHAR(T.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM FROM TB_SM10M01 T LEFT OUTER JOIN TB_BM02M01 A ON T.PCHS_CLNT_CD = A.CLNT_CD LEFT OUTER JOIN TB_CM06M01 B ON T.CREAT_ID = B.ID LEFT OUTER JOIN TB_CM06M01 C ON T.UDT_ID = C.ID
LEFT OUTER JOIN TB_BM05M01 M ON T.MATR_CD = M.MATR_CD --자재마스터 LEFT OUTER JOIN TB_CR02D02 S ON T.SALES_CD = S.SALES_CD --수주상세(Sales Code) LEFT OUTER JOIN TB_CR02M01 SM ON S.CO_CD = SM.CO_CD --수주상세(Sales Code) AND S.ORDRS_NO = SM.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 X ON SM.ORDRS_CLNT_CD = X.CLNT_CD --거래처마스터 LEFT OUTER JOIN TB_BM01M01 P ON S.PRDT_CD = P.PRDT_CD --제품마스터
WHERE 1=1
AND T.CO_CD = #{coCd}
AND T.COST_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD') +1
AND SM.ORDRS_CLNT_CD = #{vendCd}
AND X.CLNT_NM LIKE '%${vendNm}%'
AND T.SALES_CD LIKE '%${salesCd}%'
AND S.PRDT_CD = #{prdtCd}
AND P.PRDT_NM LIKE '%${prdtNm}%'
AND S.ITEM_DIV = #{itemCd}
AND T.PCHS_CLNT_CD = #{clntCd}
AND A.CLNT_NM LIKE '%${clntNm}%'
AND T.PCHS_COST_DIV10 = #{pchsCostDiv10}
AND T.PCHS_COST_DIV20 = #{pchsCostDiv20}
ORDER BY T.CO_CD, T.SALES_CD, T.COST_NO, A.CLNT_NM ) AS a ) A WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT COUNT(FILE_TRGT_KEY) CNT
  FROM TB_SM10M01
 WHERE FILE_TRGT_KEY = #{fileTrgtKey}
