mapper.select:
SELECT count(*) FROM ( SELECT ROWNUM AS RN , A.FILE_TRGT_KEY , A.RES_NO , A.CO_CD , A.ORDRG_ID , C.NAME AS ORDRG_NM , TO_CHAR(A.RES_DT, 'YYYY-MM-DD') AS RES_DT , A.RES_RMK , A.RES_DIV , A.WRITE_DT , A.WRITE_MONTH , A.CREAT_ID , A.CREAT_PGM , A.CREAT_DTTM FROM TB_QM02M01 A LEFT OUTER JOIN TB_CM06M01 C ON C.ID = A.ORDRG_ID AND C.CO_CD = A.CO_CD ) B WHERE 1 = 1
AND B.CO_CD = #{coCd}
AND B.RES_DT BETWEEN TO_DATE( #{strtDt},'YYYYMMDD') AND TO_DATE( #{endDt},'YYYYMMDD')
AND B.ORDRG_ID = #{ordrgId}
AND B.ORDRG_NM LIKE '%' || #{ordrgNm} || '%'

SELECT B.* FROM ( SELECT ROWNUM AS RN , A.FILE_TRGT_KEY , A.RES_NO , A.CO_CD , A.ORDRG_ID , C.NAME AS ORDRG_NM , TO_CHAR(A.RES_DT, 'YYYY-MM-DD') AS RES_DT , A.RES_RMK , A.RES_DIV , A.WRITE_DT , A.WRITE_YEAR , A.WRITE_MONTH , A.CREAT_ID , A.CREAT_PGM , A.CREAT_DTTM FROM TB_QM02M01 A LEFT OUTER JOIN TB_CM06M01 C ON C.ID = A.ORDRG_ID AND C.CO_CD = A.CO_CD ) B WHERE 1 = 1
AND B.CO_CD = #{coCd}
AND B.RES_DT BETWEEN TO_DATE( #{strtDt},'YYYYMMDD') AND TO_DATE( #{endDt},'YYYYMMDD')
AND B.ORDRG_ID = #{ordrgId}
AND B.ORDRG_NM LIKE '%' || #{ordrgNm} || '%'
AND B.RN BETWEEN ${firstIndex} AND ${lastIndex}

SELECT DISTINCT A.FILE_TRGT_KEY , A.CO_CD , A.SALES_CD , O.CLNT_CD , O.CLNT_NM , O.CLNT_PJT , O.PRDT_CD , O.PRDT_NM , O.ITEM_DIV , O.ITEM_DIV_NM , O.EQP_NM , A.IO_NO , A.OUT_WH_CD , A.IN_WH_CD , TO_CHAR(A.IO_DT, 'YYYY-MM-DD') AS IO_DT , A.SOC_YN , A.IO_RMK , B.IO_SEQ , B.DSGN_NO , B.MATR_CD , M.MATR_NM --자재품명 , M.MATR_SPEC --자재규격 , M.MATR_MAT --자재소재 , M.MATR_MAKR_NM --자재메이커 , M.MATR_MNO --자재형번 , B.IO_QTY , B.IO_DTL_RMK , B.OUT_INOUT_KEY , B.IN_INOUT_KEY
, 0 AS STOCK_QTY --재고수량 , '' AS CHK --체크여부 , 0 AS OUT_QTY
, 'U' AS DATA_CHK FROM TB_SM04M01 AS A LEFT OUTER JOIN TB_SM04D01 AS B ON B.CO_CD = A.CO_CD AND B.IO_NO = A.IO_NO LEFT OUTER JOIN ( SELECT DISTINCT SM.CO_CD --회사 , SM.ORDRS_NO --수주번호 , TO_CHAR(SM.ORDRS_CLNT_CD) AS CLNT_CD --고객사 , C.CLNT_NM AS CLNT_NM --고객사명 , SM.CLNT_PJT --고객사PJT , SD.PRDT_CD --제품구분 , P.PRDT_NM , SD.ITEM_DIV --아이템구분 , R.CODE_NM AS ITEM_DIV_NM , SD.SALES_CD --SalesCode , SD.EQP_NM --설비명 FROM TB_CR02M01 AS SM LEFT OUTER JOIN TB_CR02D02 AS SD ON SD.CO_CD = SM.CO_CD AND SD.ORDRS_NO = SM.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 AS C ON C.CLNT_CD = SM.ORDRS_CLNT_CD LEFT OUTER JOIN TB_BM01M01 AS P ON P.PRDT_CD = SD.PRDT_CD LEFT OUTER JOIN TB_CM05M01 AS R ON R.CODE_ID = SD.ITEM_DIV ) AS O ON O.CO_CD = A.CO_CD AND O.SALES_CD = A.SALES_CD LEFT JOIN TB_BM05M01 AS M ON M.CO_CD = B.CO_CD AND M.MATR_CD = B.MATR_CD WHERE 1 = 1 AND A.CO_CD = #{coCd} AND A.IO_NO = #{ioNo}

SELECT count(*)
  FROM TB_QM02M01 
  WHERE WRITE_MONTH = #{writeMonth}
    AND ORDRG_ID = #{ordrgId}

SELECT count(*)
  FROM TB_QM02M01 
  WHERE WRITE_MONTH = #{writeMonth}
    AND ORDRG_ID = #{ordrgId}
    AND RES_DIV = '팀'

mapper.insert:
