mapper.update:
UPDATE TB_QM02M01
SET    RES_RMK  = #{resRmk}
	 , UDT_ID   = #{userId}
	 , UDT_PGM  = #{pgmId}
	 , UDT_DTTM = SYSDATE
WHERE  FILE_TRGT_KEY = #{fileTrgtKey}
