mapper.select:
  SELECT count(*)
	FROM  TB_QM02M01 A
		   LEFT OUTER JOIN TB_CM06M01 C ON C.ID = A.ORDRG_ID  AND C.CO_CD =  A.CO_CD
	WHERE  1 = 1
	  AND SUBSTR(C.DEPT_ID,0,5) IN ( SELECT SUBSTR(C.DEPT_ID,0,5) FROM TB_CM06M01 C WHERE C.ID = #{userId})

	  AND A.CO_CD = #{coCd}


	  AND A.STAT_YM BETWEEN  #{strtDt} AND  #{endDt}


	  AND A.ORDRG_ID = #{ordrgId}


	  AND C.NAME LIKE '%' || #{ordrgNm} || '%'

	ORDER BY A.STAT_YM DESC, C.NAME, A.RES_DIV

SELECT B.* , ROWNUM AS RN FROM ( SELECT A.FILE_TRGT_KEY , A.RES_NO , A.CO_CD , A.ORDRG_ID , C.NAME AS ORDRG_NM , TO_CHAR(A.RES_DT, 'YYYY-MM-DD') AS RES_DT , A.RES_RMK , A.RES_DIV , A.WRITE_DT , A.STAT_YM , A.CREAT_ID , A.CREAT_PGM , A.CREAT_DTTM , SUBSTR(C.DEPT_ID,0,5) AS DEPT_ID FROM TB_QM02M01 A LEFT OUTER JOIN TB_CM06M01 C ON C.ID = A.ORDRG_ID AND C.CO_CD = A.CO_CD WHERE 1 = 1 AND SUBSTR(C.DEPT_ID,0,5) IN ( SELECT SUBSTR(C.DEPT_ID,0,5) FROM TB_CM06M01 C WHERE C.ID = #{userId})
AND A.CO_CD = #{coCd}
AND A.STAT_YM BETWEEN #{strtDt} AND #{endDt}
AND A.ORDRG_ID = #{ordrgId}
AND C.NAME LIKE '%' || #{ordrgNm} || '%'
ORDER BY A.STAT_YM DESC, C.NAME, A.RES_DIV ) B WHERE 1 = 1 AND ROWNUM BETWEEN ${firstIndex} AND ${lastIndex}

SELECT count(*)
  FROM TB_QM02M01 
  WHERE STAT_YM = #{statym}
    AND ORDRG_ID = #{ordrgId}
    AND RES_DIV = '개인'

SELECT count(*)
  FROM TB_QM02M01 
  WHERE STAT_YM = #{statym}
    AND DEPT_CD = #{deptCd}
    AND RES_DIV = '팀'

SELECT count(*)
  FROM TB_QM02M01 
  WHERE 1=1
    AND STAT_YM = #{statym}
    AND DEPT_CD = #{deptCd}
    AND RES_DIV = '팀'

mapper.update:
UPDATE TB_QM01M01
SET    STAT_YM  = #{statym}
	 , UDT_ID   = #{userId}
	 , UDT_PGM  = #{pgmId}
	 , UDT_DTTM = SYSDATE
WHERE ORDRG_ID = #{userId}
  AND STAT_YM IS NULL

