mapper.select:
SELECT COUNT(*) 
  FROM (
		SELECT T.CO_CD, T.ORDRG_ID
		FROM TB_QM01M01 T
		 	LEFT OUTER JOIN TB_CM06M01 N2 ON N2.ID = T.ORDRG_ID
			LEFT OUTER JOIN TB_CR02D02 D1 ON T.SALES_CD = D1.SALES_CD
			LEFT OUTER JOIN TB_CR02M01 M1 ON D1.CO_CD = D1.CO_CD AND D1.ORDRS_NO = M1.ORDRS_NO
		 WHERE 1=1  

			  AND T.CO_CD = #{coCd}


	          AND T.ORDRG_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYY-MM-DD') AND TO_DATE( #{reqDtTo},'YYYY-MM-DD')


	          AND (T.ORDRG_ID = #{ordrgId} OR T.REG_ID = #{ordrgId})


	          AND N2.NAME = #{ordrgNm}


	          AND T.SALES_CD  LIKE '%' || #{salesCd} || '%'


        	  AND substr(N2.DEPT_ID,1,5) = #{deptCd}


        	  AND M1.CLNT_PJT = #{prjctCd}


        	  AND M1.ORDRS_CLNT_CD = #{clntCd}

           AND T.PART_CD = 'COBTP01'
		GROUP BY T.CO_CD, T.ORDRG_ID
)

SELECT ROWNUM AS RNUM, A.* FROM( SELECT T.CO_CD, T.ORDRG_ID, count(T.REQ_NO) AS ERR_CNT , MAX(N2.NAME) AS ORDRG_ID_NM , SUM(DECODE(NVL(T.SCD_STS,'SCDSTS01'), 'SCDSTS01',1,0)) AS NORMAL_CNT , SUM(DECODE(NVL(T.SCD_STS,'SCDSTS01'), 'SCDSTS01',0,1)) AS AFT_ERR_CNT , SUM(COUNT(*)) OVER (ORDER BY T.CO_CD ) AS REQ_TOT , SUM(SUM(DECODE(NVL(T.SCD_STS,'SCDSTS01'), 'SCDSTS01',1,0))) OVER (ORDER BY T.CO_CD ) AS NORMAL_TOT , SUM(SUM(DECODE(NVL(T.SCD_STS,'SCDSTS01'), 'SCDSTS01',0,1))) OVER (ORDER BY T.CO_CD ) AS AFT_ERR_TOT FROM TB_QM01M01 T LEFT OUTER JOIN TB_CM06M01 N2 ON N2.ID = T.ORDRG_ID LEFT OUTER JOIN TB_CR02D02 D1 ON T.SALES_CD = D1.SALES_CD LEFT OUTER JOIN TB_CR02M01 M1 ON D1.CO_CD = D1.CO_CD AND D1.ORDRS_NO = M1.ORDRS_NO WHERE 1=1
AND T.CO_CD = #{coCd}
AND T.ORDRG_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYY-MM-DD') AND TO_DATE( #{reqDtTo},'YYYY-MM-DD')
AND (T.ORDRG_ID = #{ordrgId} OR T.REG_ID = #{ordrgId})
AND N2.NAME = #{ordrgNm}
AND T.SALES_CD LIKE '%' || #{salesCd} || '%'
AND substr(N2.DEPT_ID,1,5) = #{deptCd}
AND M1.CLNT_PJT = #{prjctCd}
AND M1.ORDRS_CLNT_CD = #{clntCd}
GROUP BY T.CO_CD, T.ORDRG_ID ORDER BY count(T.REQ_NO) DESC, MAX(N2.NAME) ) A

SELECT A.*, D1.DEPT_NM FROM( SELECT T.CO_CD, substr(N2.DEPT_ID,1,5) AS DEPT_ID FROM TB_QM01M01 T LEFT OUTER JOIN TB_CM06M01 N2 ON N2.ID = T.ORDRG_ID WHERE 1=1
AND T.CO_CD = #{coCd}
AND T.ORDRG_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYY-MM-DD') AND TO_DATE( #{reqDtTo},'YYYY-MM-DD')
AND (T.ORDRG_ID = #{ordrgId} OR T.REG_ID = #{ordrgId})
AND N2.NAME = #{ordrgNm}
AND T.SALES_CD LIKE '%' || #{salesCd} || '%'
AND T.PART_CD = #{partCd}
GROUP BY T.CO_CD, substr(N2.DEPT_ID,1,5) ) A LEFT OUTER JOIN TB_CM04M01 D1 ON A.DEPT_ID = D1.DEPT_ID ORDER BY DEPT_NM

SELECT A.* FROM( SELECT T.CO_CD, M1.ORDRS_CLNT_CD, C1.CLNT_NM FROM TB_QM01M01 T LEFT OUTER JOIN TB_CR02D02 D1 ON T.SALES_CD = D1.SALES_CD LEFT OUTER JOIN TB_CR02M01 M1 ON D1.CO_CD = D1.CO_CD AND D1.ORDRS_NO = M1.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 C1 ON M1.ORDRS_CLNT_CD = C1.CLNT_CD WHERE 1=1
AND T.CO_CD = #{coCd}
AND T.ORDRG_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYY-MM-DD') AND TO_DATE( #{reqDtTo},'YYYY-MM-DD')
AND (T.ORDRG_ID = #{ordrgId} OR T.REG_ID = #{ordrgId})
AND N2.NAME = #{ordrgNm}
AND T.SALES_CD LIKE '%' || #{salesCd} || '%'
AND T.PART_CD = #{partCd}
GROUP BY T.CO_CD, M1.ORDRS_CLNT_CD, C1.CLNT_NM ) A ORDER BY CLNT_NM

SELECT A.* FROM( SELECT T.CO_CD, M1.CLNT_PJT , ( SELECT FN_CM05M01_CD_TO_NM(M1.CLNT_PJT) FROM DUAL ) AS CLNT_PJT_NM FROM TB_QM01M01 T LEFT OUTER JOIN TB_CR02D02 D1 ON T.SALES_CD = D1.SALES_CD LEFT OUTER JOIN TB_CR02M01 M1 ON D1.CO_CD = D1.CO_CD AND D1.ORDRS_NO = M1.ORDRS_NO WHERE 1=1
AND T.CO_CD = #{coCd}
AND T.ORDRG_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYY-MM-DD') AND TO_DATE( #{reqDtTo},'YYYY-MM-DD')
AND (T.ORDRG_ID = #{ordrgId} OR T.REG_ID = #{ordrgId})
AND N2.NAME = #{ordrgNm}
AND T.SALES_CD LIKE '%' || #{salesCd} || '%'
AND T.PART_CD = #{partCd}
GROUP BY T.CO_CD, M1.CLNT_PJT ) A ORDER BY CLNT_PJT_NM
