sql.if:
AND B.PCHS_CLNT_CD IN
(
SELECT CLNT_CD
	FROM TB_BM02M01 CC
WHERE CC.CLNT_DIV_CD = #{clntDivCd} 
)

mapper.select:
SELECT B.FILE_TRGT_KEY , A.CO_CD , FN_CM05M01_CD_TO_NM(A.CO_CD) AS CO_CD_NM /* 회사명 */ , A.MATR_CD /* 자재코드 */ , FN_CM05M01_CD_TO_NM(A.MATR_CD) AS MATR_CD_NM /* 자재명 */ , B.ORDRG_NO /* 발주번호 */ , B.REQ_NO /* 요인별 요청번호 */ , A.SALES_CD , A.MATR_CD /* 자재코드 */ , D.MATR_NM /* 자재명 */ , D.MATR_SPEC /* 규격 */ , B.PCHS_CLNT_CD /* 구매처cd */ , ( SELECT D.CLNT_NM FROM TB_BM02M01 D WHERE B.PCHS_CLNT_CD=D.CLNT_CD ) AS PCHS_CLNT_NM /* 구매처명 */ , TO_CHAR(B.ORDRG_DT, 'YYYY-MM-DD') ORDRG_DT /* 발주일자 */ , B.ORDRG_DIV10 /* 발주구분 */ , FN_CM05M01_CD_TO_NM(B.ORDRG_DIV10) AS ORDRG_DIV10_NM /* 발주구분명 */ , B.ORDRG_DIV20 /* 용도구분 */ , FN_CM05M01_CD_TO_NM(B.ORDRG_DIV20) AS ORDRG_DIV20_NM /* 용도구분명 */ , B.ORDRG_DIV30 /* 특성구분 */ , FN_CM05M01_CD_TO_NM(B.ORDRG_DIV30) AS ORDRG_DIV30_NM /* 특성구분명 */ , B.CURR_CD /* 통화단위 */ , B.EXRATE , ( SELECT SUM(D.ORDRG_AMT) FROM TB_SM02D01 D WHERE A.CO_CD=D.CO_CD AND A.ORDRG_NO=D.ORDRG_NO AND A.ORDRG_SEQ=D.ORDRG_SEQ AND A.SALES_CD=D.SALES_CD ) AS ORDRG_AMT , SUM(A.ORDRG_QTY) OVER(PARTITION BY A.CO_CD, A.ORDRG_NO, A.ORDRG_SEQ) ORDRG_QTY /* 발주수량 */ , SUM(A.ORDRG_AMT) OVER(PARTITION BY A.CO_CD, A.ORDRG_NO, A.ORDRG_SEQ) ORDRG_AMT /* 발주금액 */ , NVL(SUM(C.IN_QTY) OVER(PARTITION BY A.CO_CD, A.ORDRG_NO, A.ORDRG_SEQ), 0) IN_QTY /* 입고수량 */ , SUM(A.ORDRG_QTY) OVER(PARTITION BY A.CO_CD, A.ORDRG_NO, A.ORDRG_SEQ) - NVL(SUM(IN_QTY) OVER(PARTITION BY A.CO_CD, A.ORDRG_NO, A.ORDRG_SEQ), 0) AS NOT_IN_QTY /* 미입고수량 */ , A.WH_CD AS MATR_WH_CD /* 입고창고 */ , FN_CM05M01_CD_TO_NM(A.WH_CD) AS MATR_WH_NM /* 입고창고 */ , A.MATR_TEST_DIV /*검사구분 */ , FN_CM05M01_CD_TO_NM(A.MATR_TEST_DIV) MATR_TEST_DIV_NM /*검사구분 */ , C.IO_DIV AS IO_DIV_CD /* 수불구분 */ , FN_CM05M01_CD_TO_NM(C.IO_DIV) AS IO_DIV_NM /* 아이템 구분명 */ FROM TB_SM02D01 A INNER JOIN TB_SM02M01 B ON(A.CO_CD=B.CO_CD AND A.ORDRG_NO=B.ORDRG_NO) LEFT OUTER JOIN TB_SM01D01 D ON(A.CO_CD = D.CO_CD AND A.SALES_CD = D.SALES_CD AND A.UPPER_CD = D.UPPER_CD AND A.LOWER_CD = D.LOWER_CD AND A.MATR_SEQ=D.MATR_SEQ AND A.MATR_CD=D.MATR_CD) LEFT OUTER JOIN ( SELECT C.ORDRG_NO , C.ORDRG_SEQ , C.SALES_CD , NVL(IN_QTY, 0) AS IN_QTY , NVL(C.BAD_QTY, 0) AS BAD_QTY , C.IN_DTL_AMT , C.CURR_CD AS CURR_CD_IN /* 통화단위 IN */ , C.EXRATE AS EXRATE_IN /* 환율 IN */ , C.IN_DTL_RMK , C.IO_DIV /* 수불구분 */ FROM TB_SM03D01 C ) C ON( A.ORDRG_NO=C.ORDRG_NO AND A.SALES_CD=C.SALES_CD AND A.ORDRG_SEQ=C.ORDRG_SEQ) WHERE 1=1
AND A.CO_CD = #{coCd}
AND A.ORDRG_NO = #{ordrgNo}
AND A.ORDRG_SEQ = #{ordrgSeq}
AND A.SALES_CD = #{salesCd}

