sql.if:
AND B.PCHS_CLNT_CD IN
(
SELECT CLNT_CD
	FROM TB_BM02M01 CC
WHERE CC.CLNT_DIV_CD = #{clntDivCd} 
)

mapper.select:
SELECT NVL(MAX_IN_NO, 'IN'||TO_CHAR(SYSDATE, 'YY')||'00001') AS MAX_IN_NO FROM ( SELECT DISTINCT DECODE( INSTR(IN_NO, 'IN'||TO_CHAR(SYSDATE, 'YY')), 0, 'IN'||TO_CHAR(SYSDATE, 'YY')||'00001' , 'IN' || MAX( TO_CHAR(SYSDATE, 'YY')|| LPAD( SUBSTRING(IN_NO, -5)+1, 5, '0') ) OVER() ) AS MAX_IN_NO FROM TB_SM03D01 WHERE 1=1
AND CO_CD = #{coCd}
UNION ALL SELECT NULL AS MAX_IN_NO FROM DUAL ) WHERE MAX_IN_NO IS NOT NULL OR ROWNUM = 1

SELECT B.FILE_TRGT_KEY , A.CO_CD , FN_CM05M01_CD_TO_NM(A.CO_CD) AS CO_CD_NM /* 회사명 */ , A.MATR_CD /* 자재코드 */ , FN_CM05M01_CD_TO_NM(A.MATR_CD) AS MATR_CD_NM /* 자재명 */ , A.ORDRG_NO /* 발주번호 */ , A.ORDRG_SEQ /* 발주순번 */ , B.REQ_NO /* 요인별 요청번호 */ , A.SALES_CD , A.MATR_CD /* 자재코드 */ , D.MATR_NM /* 자재명 */ , D.MATR_SPEC /* 규격 */ , B.PCHS_CLNT_CD /* 구매처cd */ , ( SELECT D.CLNT_NM FROM TB_BM02M01 D WHERE B.PCHS_CLNT_CD=D.CLNT_CD ) AS PCHS_CLNT_NM /* 구매처명 */ , TO_CHAR(B.ORDRG_DT, 'YYYY-MM-DD') ORDRG_DT /* 발주일자 */ , B.ORDRG_DIV10 /* 발주구분 */ , FN_CM05M01_CD_TO_NM(B.ORDRG_DIV10) AS ORDRG_DIV10_NM /* 발주구분명 */ , B.ORDRG_DIV20 /* 용도구분 */ , FN_CM05M01_CD_TO_NM(B.ORDRG_DIV20) AS ORDRG_DIV20_NM /* 용도구분명 */ , B.ORDRG_DIV30 /* 특성구분 */ , FN_CM05M01_CD_TO_NM(B.ORDRG_DIV30) AS ORDRG_DIV30_NM /* 특성구분명 */ , B.CURR_CD /* 통화단위 */ , B.EXRATE , ( SELECT SUM(D.ORDRG_AMT) FROM TB_SM02D01 D WHERE A.CO_CD=D.CO_CD AND A.ORDRG_NO=D.ORDRG_NO AND A.ORDRG_SEQ=D.ORDRG_SEQ AND A.SALES_CD=D.SALES_CD ) AS ORDRG_AMT , SUM(A.ORDRG_QTY) OVER(PARTITION BY A.CO_CD, A.ORDRG_NO, A.ORDRG_SEQ) ORDRG_QTY /* 발주수량 */ , SUM(A.ORDRG_AMT) OVER(PARTITION BY A.CO_CD, A.ORDRG_NO, A.ORDRG_SEQ) ORDRG_AMT /* 발주금액 */ , SUM(A.ORDRG_PRC) OVER(PARTITION BY A.CO_CD, A.ORDRG_NO, A.ORDRG_SEQ) ORDRG_PRC /* 발주단가 */ , NVL(SUM(C.IN_QTY) OVER(PARTITION BY A.CO_CD, A.ORDRG_NO, A.ORDRG_SEQ), 0) PRE_IN_QTY /* 기입고수량 */
, C.IN_NO /* 입고번호 */ , C.IN_SEQ /* 입고순번 */ , 'Y' AS USER_CHK /* chk 값 */ , NVL(C.IN_QTY, 0) AS IN_QTY /* 입고(합격)수량 */ , NVL(C.BAD_QTY, 0) AS BAD_QTY /*불량수량 */ , NVL(C.IN_PRC, 0) AS IN_PRC /* 입고단가 */ , NVL(C.IN_DTL_AMT, 0) AS IN_DTL_AMT /* 입고금액 */ , NVL(C.IN_QTY+C.BAD_QTY, 0) AS S_INSPEC_QTY /* 검사수량 */ , NVL(C.IN_QTY, 0) AS S_IN_QTY /* 입고수량 */ , TO_CHAR(C.IN_DT, 'YYYY-MM-DD') IN_DT /* 입고일자 */ , C.IN_DTL_RMK /* 상세비고 */
, SUM(A.ORDRG_QTY) OVER(PARTITION BY A.CO_CD, A.ORDRG_NO, A.ORDRG_SEQ) - NVL(SUM(IN_QTY) OVER(PARTITION BY A.CO_CD, A.ORDRG_NO, A.ORDRG_SEQ), 0) AS NOT_IN_QTY /* 미입고수량 */ , A.WH_CD AS MATR_WH_CD /* 입고창고 */ , FN_CM05M01_CD_TO_NM(A.WH_CD) AS MATR_WH_NM /* 입고창고 */ , A.MATR_TEST_DIV /*검사구분 */ , FN_CM05M01_CD_TO_NM(A.MATR_TEST_DIV) MATR_TEST_DIV_NM /*검사구분 */ FROM TB_SM02D01 A INNER JOIN TB_SM02M01 B ON(A.CO_CD=B.CO_CD AND A.ORDRG_NO=B.ORDRG_NO) LEFT OUTER JOIN TB_SM01D01 D ON(A.CO_CD = D.CO_CD AND A.SALES_CD = D.SALES_CD AND A.UPPER_CD = D.UPPER_CD AND A.LOWER_CD = D.LOWER_CD AND A.MATR_SEQ=D.MATR_SEQ AND A.MATR_CD=D.MATR_CD)
INNER JOIN TB_SM03D01 C ON(A.ORDRG_NO=C.ORDRG_NO AND A.ORDRG_SEQ=C.ORDRG_SEQ)
LEFT OUTER JOIN ( SELECT C.ORDRG_NO , C.ORDRG_SEQ , NVL(SUM(IN_QTY), 0) AS IN_QTY FROM TB_SM03D01 C GROUP BY C.ORDRG_NO, C.ORDRG_SEQ ) C ON( A.ORDRG_NO=C.ORDRG_NO AND A.ORDRG_SEQ=C.ORDRG_SEQ)
WHERE 1=1
AND A.CO_CD = #{coCd}
AND A.ORDRG_NO = #{ordrgNo}
AND A.ORDRG_SEQ = #{ordrgSeq}
AND A.SALES_CD = #{salesCd}

mapper.insert:

	MERGE INTO TB_SM03M01 A
		USING DUAL
		ON (
		A.FILE_TRGT_KEY = #{fileTrgtKey}
		AND A.CO_CD = #{coCd}
		AND A.IN_NO = #{maxInNo}
			)	
	WHEN MATCHED THEN
		UPDATE SET	
			REQ_NO						= #{reqNo,  jdbcType=VARCHAR}
			, SALES_CD					= #{salesCd}
			, ETC_FIELD1				= #{etcField1,  jdbcType=VARCHAR}
			, ETC_FIELD2				= #{etcField2,  jdbcType=VARCHAR}
			, ETC_FIELD3				= #{etcField3,  jdbcType=VARCHAR}
			, UDT_ID					= #{userId}
			, UDT_PGM					= #{pgmId}	
			, UDT_DTTM					= SYSDATE
	WHEN NOT MATCHED THEN											
	INSERT 
      (
		FILE_TRGT_KEY
		 , CO_CD
		 , IN_NO
		 , REQ_NO
		 , SALES_CD
		 , CREAT_ID
		 , CREAT_PGM
		 , CREAT_DTTM
      )
    VALUES
    (
		#{fileTrgtKey}
		, #{coCd}
		, #{maxInNo}
		, #{reqNo,  jdbcType=VARCHAR}
		, #{salesCd}
		, #{userId}
		, #{pgmId}
		, SYSDATE
    )


		SELECT NVL(MAX(IN_SEQ)+1, 1) FROM TB_SM03D01 
			WHERE CO_CD = #{coCd}
					AND IN_NO = #{maxInNo}

	INSERT INTO TB_SM03D01
	  (
		CO_CD
		, IN_NO
		, IN_SEQ
		, ORDRG_NO
		, ORDRG_SEQ
		, REQ_NO
		, SALES_CD
		, MATR_CD
		, MATR_TEST_DIV
		, IN_PRC
		, ARR_QTY
		, IN_QTY
		, BAD_QTY
		, IN_DTL_AMT
		, CURR_CD
		, EXRATE
		, IN_DTL_RMK
		, INOUT_KEY
		, CREAT_ID
		, CREAT_PGM
		, CREAT_DTTM
		, IO_DIV
		, IN_DT
		, WH_CD
	  )
	VALUES
	  (
		 #{coCd}
		, #{maxInNo}
		, #{inSeq}
		, #{ordrgNo}
		, #{ordrgSeq}
		, #{reqNo}
		, #{salesCd}
		, #{matrCd}
		, #{matrTestDiv}
		, #{inPrc}
		, #{arrQty,  jdbcType=VARCHAR}
		, #{inQty}
		, #{badQty}
		, #{inDtlAmt}
		, #{currCd}
		, #{exrate}
		, #{inDtlRmk,  jdbcType=VARCHAR}
		, (SELECT NVL(MAX(INOUT_KEY)+1, 1) FROM TB_SM03D01 )
		, #{userId}
		, #{pgmId}
		, SYSDATE
		, #{ioDiv}
		, #{inDt}
		, #{matrWhCd}
      )

mapper.update:
UPDATE TB_SM02M01
	SET
REQ_NO = #{reqNo} 
, SALES_CD = #{salesCd}
, PCHS_CLNT_CD = #{pchsClntCd}
, ORDRG_DT = #{ordrgDt}
, ORDRG_DIV10 = #{ordrgDiv10}
, ORDRG_DIV20 = #{ordrgDiv20}
, ORDRG_DIV30 = #{ordrgDiv30}
, CURR_CD = #{currCd}
, EXRATE = #{exrate}
, ORDRG_AMT = #{ordrgAmt}
, DUDT_DEQ_DT = #{dudtDeqDt}
, ORDRG_RMK = #{ordrgRmk,			jdbcType=VARCHAR}	 	
, IN_CMPLET_YN = #{inCmpletYn,			jdbcType=VARCHAR}
, UDT_ID = #{userId}
, UDT_PGM = #{pgmId}
, UDT_DTTM = SYSDATE
WHERE FILE_TRGT_KEY = #{fileTrgtKey}
	AND CO_CD = #{coCd}
	AND ORDRG_NO = #{ordrgNo}

UPDATE TB_SM03D01
	SET 
	MATR_CD = #{matrCd}
	, MATR_TEST_DIV = #{matrTestDiv}
	, IN_PRC = #{inPrc}
	, ARR_QTY = #{arrQty,			jdbcType=VARCHAR}
	, IN_QTY = #{inQty}
	, BAD_QTY = #{badQty}
	, IN_DTL_AMT = #{inDtlAmt}
	, CURR_CD = #{currCd}
	, EXRATE = #{exrate}
	, IN_DTL_RMK = #{inDtlRmk,			jdbcType=VARCHAR}	 	
	, UDT_ID = #{userId}
	, UDT_PGM = #{pgmId}
	, UDT_DTTM = SYSDATE
	, IO_DIV = #{ioDiv}
	, IN_DT = #{inDt}
	, WH_CD = #{matrWhCd}
WHERE CO_CD = #{coCd}
	AND IN_NO = #{inNo}
	AND IN_SEQ = #{inSeq}

mapper.delete:
DELETE 
	FROM TB_SM03D01
WHERE 1=1
	AND CO_CD = #{coCd}
	AND IN_NO = #{inNo}
	AND IN_SEQ = #{inSeq}
