SELECT count(*) FROM ( SELECT A.* , ROWNUM AS RN FROM ( SELECT DISTINCT A.FILE_TRGT_KEY , T.PID , T.ID , T.CO_CD , T.CO_NM , T.IO_NO , CASE WHEN T.IO_SEQ IS NULL OR T.IO_SEQ = '' THEN A.FILE_TRGT_KEY ELSE A.FILE_TRGT_KEY||'-'||T.IO_SEQ END AS TREE_ID , T.IO_SEQ , T.SALES_CD , T.CLNT_CD --고객사 , T.CLNT_NM --고객사명 , T.CLNT_PJT --고객사PJT , T.PRDT_CD --제품구분 , T.PRDT_NM --제품구분명 , T.ITEM_DIV --아이템구분 , T.ITEM_DIV_NM --아이템구분명 , T.EQP_NM --설비명 , T.OUT_WH_CD --출고창고코드 , T.OUT_WH_NM , T.IO_DT --폐기일자 , T.IO_RESN --폐기사유 , T.IO_RMK --폐기비고 , T.DSGN_NO , T.MATR_CD , T.MATR_NM --자재품명 , T.MATR_SPEC --자재규격 , T.MATR_MAT --자재소재 , T.MATR_MAKR_NM --자재메이커 , T.MATR_MNO --자재형번 , T.IO_QTY , T.IO_DTL_RMK , T.MATR_CLNT_DIV --자재거래처분류 , T.CREAT_PGM , T.CREAT_ID , T.CREAT_ID_NM , T.CREAT_DTTM , T.UDT_PGM , T.UDT_ID , T.UDT_ID_NM , T.UDT_DTTM , T.LVL FROM ( SELECT DISTINCT A.FILE_TRGT_KEY --파일대상KEY FROM TB_SM05M01 AS A LEFT OUTER JOIN ( SELECT DISTINCT SM.CO_CD --회사 , SM.ORDRS_NO --수주번호 , TO_CHAR(SM.ORDRS_CLNT_CD) AS CLNT_CD --고객사 , C.CLNT_NM AS CLNT_NM --고객사명 , SM.CLNT_PJT --고객사PJT , SD.PRDT_CD --제품구분 , P.PRDT_NM , SD.ITEM_DIV --아이템구분 , SD.SALES_CD --SalesCode , SD.EQP_NM --설비명 FROM TB_CR02M01 AS SM LEFT OUTER JOIN TB_CR02D02 AS SD ON SD.CO_CD = SM.CO_CD AND SD.ORDRS_NO = SM.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 AS C ON C.CLNT_CD = SM.ORDRS_CLNT_CD LEFT OUTER JOIN TB_BM01M01 AS P ON P.PRDT_CD = SD.PRDT_CD ) AS O ON O.CO_CD = A.CO_CD AND O.SALES_CD = A.SALES_CD LEFT OUTER JOIN TB_SM05D01 AS B ON B.CO_CD = A.CO_CD AND B.IO_NO = A.IO_NO LEFT OUTER JOIN TB_BM05M01 AS M ON M.MATR_CD = B.MATR_CD WHERE 1 = 1
AND A.CO_CD = #{coCd}
