AND A.SALES_CD LIKE '%' || #{salesCd} || '%'
AND A.OUT_WH_CD = #{outWhCd}
AND O.CLNT_CD = #{OrdrsClntCd}
AND O.CLNT_NM LIKE '%' || #{clntNm} || '%'
AND O.PRDT_CD = #{prdtCd}
AND O.ITEM_DIV = #{itemDiv}
AND B.MATR_CD LIKE '%' || #{matrCd} || '%'
AND M.MATR_NM LIKE '%' || #{matrNm} || '%'
