	, #{etcField2, jdbcType=VARCHAR}
	, #{etcField3, jdbcType=VARCHAR}
	, #{userId}
	, #{pgmId}
	, SYSDATE
	, #{position, jdbcType=VARCHAR}
)

mapper.update:
UPDATE TB_SM05M01
SET    IO_RESN  = #{ioResn}
     , IO_RMK   = #{ioRmk}
	 , UDT_ID   = #{userId}
	 , UDT_PGM  = #{pgmId}
	 , UDT_DTTM = SYSDATE
WHERE  FILE_TRGT_KEY = #{fileTrgtKey}

UPDATE TB_SM05D01
SET    UDT_ID   = #{userId}
	 , UDT_PGM  = #{pgmId}
	 , UDT_DTTM = SYSDATE
	 , POSITION = #{position}
WHERE  CO_CD  = #{coCd}
AND    IO_NO  = #{ioNo}
AND    IO_SEQ = #{ioSeq}

mapper.delete:
DELETE TB_SM05D01
WHERE  CO_CD  = #{coCd}
AND    IO_NO  = #{ioNo}
AND    IO_SEQ = #{ioSeq}

DELETE TB_SM05D01
WHERE  CO_CD  = #{coCd}
AND    IO_NO  = #{ioNo}

mapper.select:
