	 , RET_DTL_RMK = #{returnSum}
     , UDT_ID   = #{userId}
	 , UDT_PGM  = #{pgmId}
	 , UDT_DTTM = SYSDATE
WHERE  CO_CD = #{coCd}
AND    RET_NO = #{retNo}
AND    RET_SEQ = #{retSeq}

mapper.delete:
DELETE TB_SM06D01
WHERE  CO_CD  = #{coCd}
AND    RET_NO  = #{retNo}
AND    RET_SEQ = #{ioSeq}

DELETE TB_SM06D01
WHERE  CO_CD  = #{coCd}
AND    RET_NO  = #{retNo}

mapper.select:
SELECT * FROM ( SELECT DISTINCT A.CO_CD , R1.CODE_NM AS CO_CD_NM , A.IN_NO , A.SALES_CD , TO_CHAR(A.IN_DT, 'YYYY-MM-DD') AS IN_DT , A.CURR_CD , C.CODE_NM AS CURR_NM , A.EXRATE , Z.PCHS_CLNT_CD , D.CLNT_NM AS PCHS_CLMN_NM , B.ORDRS_CLNT_CD , B.ORDRS_CLNT_NM , B.CLNT_PJT , B.CLNT_PJT_NM , B.PRDT_CD , B.PRDT_CD_NM , B.ITEM_DIV , B.ITEM_DIV_NM , B.EQP_NM , ( COUNT(A.IN_QTY) OVER()) AS IN_CNT , (SELECT COUNT(*) FROM TB_SM06D01 T WHERE T.IN_NO = A.IN_NO) AS CNT FROM TB_SM03D01 AS A LEFT OUTER JOIN ( SELECT X.CO_CD , R1.CODE_NM AS CO_CD_NM , X.ORDRS_NO , X.SALES_CD , Y.ORGN_SALES_CD , Y.ORDRS_CLNT_CD , A.CLNT_NM AS ORDRS_CLNT_NM , Y.CLNT_PJT , R2.CODE_NM AS CLNT_PJT_NM , X.ORDRS_DTL_DIV20 , R3.CODE_NM AS ORDRS_DTL_DIV20_NM , X.PRDT_CD , P.PRDT_NM AS PRDT_CD_NM , X.ITEM_DIV , R4.CODE_NM AS ITEM_DIV_NM , X.EQP_NM FROM TB_CR02D02 AS X INNER JOIN TB_CR02M01 AS Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO LEFT OUTER JOIN TB_BM01M01 AS P ON X.PRDT_CD = P.PRDT_CD LEFT OUTER JOIN TB_BM02M01 AS A ON Y.ORDRS_CLNT_CD = A.CLNT_CD LEFT OUTER JOIN TB_CM05M01 AS R1 ON R1.CODE_ID = X.CO_CD LEFT OUTER JOIN TB_CM05M01 AS R2 ON R2.CODE_ID = Y.CLNT_PJT LEFT OUTER JOIN TB_CM05M01 AS R3 ON R3.CODE_ID = X.ORDRS_DTL_DIV20 LEFT OUTER JOIN TB_CM05M01 AS R4 ON R4.CODE_ID = X.ITEM_DIV WHERE 1 = 1 ) AS B ON B.SALES_CD = A.SALES_CD LEFT OUTER JOIN TB_CM05M01 AS C ON C.CODE_ID = A.CURR_CD LEFT OUTER JOIN TB_SM02M01 AS Z ON Z.ORDRG_NO = A.ORDRG_NO LEFT OUTER JOIN TB_BM02M01 AS D ON D.CLNT_CD = Z.PCHS_CLNT_CD LEFT OUTER JOIN TB_CM05M01 AS R1 ON R1.CODE_ID = A.CO_CD WHERE 1 = 1 ) WHERE 1 = 1 AND IN_CNT > CNT
AND CO_CD = #{coCd}
AND ${searchType} LIKE '%' || #{searchValue} || '%'
