mapper.select:
SELECT count(*) FROM TB_SM10M01 T LEFT OUTER JOIN TB_BM02M01 A ON T.PCHS_CLNT_CD = A.CLNT_CD LEFT OUTER JOIN TB_CM06M01 B ON T.CREAT_ID = B.ID LEFT OUTER JOIN TB_CM06M01 C ON T.UDT_ID = C.ID LEFT OUTER JOIN TB_BM05M01 M ON T.MATR_CD = M.MATR_CD --자재마스터 AND T.CO_CD = M.CO_CD LEFT OUTER JOIN TB_CR02D02 S ON T.SALES_CD = S.SALES_CD --수주상세(Sales Code) AND T.ORDRS_CO_CD = S.CO_CD LEFT OUTER JOIN TB_CR02M01 SM ON T.ORDRS_CO_CD = SM.CO_CD --수주상세(Sales Code) AND T.ORDRS_NO = SM.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 X ON SM.ORDRS_CLNT_CD = X.CLNT_CD --거래처마스터 LEFT OUTER JOIN TB_BM01M01 P ON S.PRDT_CD = P.PRDT_CD --제품마스터 LEFT OUTER JOIN( SELECT ORDRG_NO , SUM(A.PCHS_AMT) AS PCHS_AMT , SUM(A.PCHS_VAT) AS PCHS_VAT , SUM(A.PCHS_QTY) AS PCHS_QTY , PCHS_NO , COUNT(A.PCHS_NO) PCHS_CNT , SUM(DECODE(A.CMPLET_YN, 'Y', 1, 0)) AS CMPLET_SUM , MAX(A.PCHS_DT) AS PCHS_DT , MAX(A.BILL_YN) AS BILL_YN FROM TB_SM12D01 A WHERE 1=1 AND ORDRG_NO LIKE 'COST%' GROUP BY A.CO_CD , A.ORDRG_NO , A.PCHS_NO ) E ON(T.COST_NO = E.ORDRG_NO) /* 매입 DETAIL */ WHERE 1=1
AND T.CO_CD = #{coCd}
AND T.COST_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD') +1
AND SM.ORDRS_CLNT_CD = #{vendCd}
AND X.CLNT_NM LIKE '%'||#{vendNm}||'%'
AND T.ORDRS_NO LIKE '%'||#{ordrsNo}||'%'
AND T.SALES_CD LIKE '%'||#{salesCd}||'%'
AND S.PRDT_CD = #{prdtCd}
AND P.PRDT_NM LIKE '%'||#{prdtNm}||'%'
AND S.ITEM_DIV = #{itemCd}
AND T.PCHS_CLNT_CD = #{clntCd}
AND A.CLNT_NM LIKE '%'||#{clntNm}||'%'
AND T.PCHS_COST_DIV10 = #{pchsCostDiv10}
AND T.PCHS_COST_DIV20 = #{pchsCostDiv20}

/* selectPchsCostList */ SELECT * FROM( SELECT ROWNUM AS RNUM, A.*
FROM ( SELECT T.FILE_TRGT_KEY, T.CO_CD, FN_CM05M01_CD_TO_NM(T.CO_CD) CO_NM, T.COST_NO, T.ORDRS_NO, (SELECT MAX(SALES_CD) || DECODE(COUNT(*), 0, '', 1, '', '외 ' || (COUNT(*)-1) || '건') FROM TB_SM10D01 WHERE COST_NO = T.COST_NO) AS SALES_CD, T.REQ_NO, T.PCHS_CLNT_CD, A.CLNT_NM PCHS_CLNT_NM, TO_CHAR(T.COST_DT, 'YYYY-MM-DD') AS COST_DT, T.PCHS_COST_DIV10, FN_CM05M01_CD_TO_NM(T.PCHS_COST_DIV10) PCHS_COST_DIV10_NM, T.PCHS_COST_DIV20, FN_CM05M01_CD_TO_NM(T.PCHS_COST_DIV20) PCHS_COST_DIV20_NM, T.CURR_CD, FN_CM05M01_CD_TO_NM(T.CURR_CD) CURR_NM, T.EXRATE, T.COST_AMT, T.MATR_CD, M.MATR_NM, T.COST_RMK, T.ETC_FIELD1, T.ETC_FIELD2, T.ETC_FIELD3, T.CREAT_ID, B.NAME CREAT_NM, T.CREAT_PGM, TO_CHAR(T.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM, T.UDT_ID, C.NAME UDT_NM, T.UDT_PGM, TO_CHAR(T.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM, NVL2(E.PCHS_NO,'Y','N') AS CONF_YM, --매입확정 E.BILL_YN AS BILL_YN, --계산서발행 E.PCHS_AMT, --매입확정금액 E.PCHS_VAT, --매입확정VAT E.PCHS_QTY, --매입확정수량 E.PCHS_NO, --매입확정번호 TO_CHAR(E.PCHS_DT, 'YYYY-MM-DD') AS PCHS_DT, --확정일자 SM.ORDRS_CLNT_CD, X.CLNT_NM, SM.CLNT_PJT, FN_CM05M01_CD_TO_NM(SM.CLNT_PJT) AS CLNT_PJT_NM, SM.CTRT_NM, T.PAY_DIV, FN_CM05M01_CD_TO_NM(T.PAY_DIV) AS PAY_DIV_NM, TO_CHAR(T.PAY_DIV_DT, 'YYYY-MM-DD') AS PAY_DIV_DT, T.PCHS_NO, T.PCHS_SEQ, T.ORDRS_CO_CD, FN_CM05M01_CD_TO_NM(T.ORDRS_CO_CD) AS ORDRS_CO_CD_NM, T.MAIL_YN, T.MAIL_DTTM, T.MAIL_SEND_ID, TO_CHAR((SELECT MAX(CREAT_DTTM) FROM TB_SM10D02 WHERE COST_NO = T.COST_NO), 'YYYY-MM-DD') AS REPORT_DT,--발주서발행일자 NVL(T.PCHS_PMNT_MTD_CD, A.PCHS_PMNT_MTD_CD) AS PCHS_PMNT_MTD_CD, --결재방법(현금, 어음) NVL(T.PCHS_CLMN_DIV_CD, A.PCHS_CLMN_DIV_CD) AS PCHS_CLMN_DIV_CD, --결재조건(당월,익월) NVL(T.PCHS_CLMN_DAY, A.PCHS_CLMN_DAY) AS PCHS_CLMN_DAY, --결재일 (10,25) FN_CM05M01_CD_TO_NM(NVL(T.PCHS_PMNT_MTD_CD, A.PCHS_PMNT_MTD_CD)) AS PCHS_PMNT_MTD_CD_NM, FN_CM05M01_CD_TO_NM(NVL(T.PCHS_CLMN_DIV_CD, A.PCHS_CLMN_DIV_CD)) AS PCHS_CLMN_DIV_CD_NM, T.CTRT_NO, T.REQ_NO FROM TB_SM10M01 T LEFT OUTER JOIN TB_BM02M01 A ON T.PCHS_CLNT_CD = A.CLNT_CD LEFT OUTER JOIN TB_CM06M01 B ON T.CREAT_ID = B.ID LEFT OUTER JOIN TB_CM06M01 C ON T.UDT_ID = C.ID LEFT OUTER JOIN TB_BM05M01 M ON T.MATR_CD = M.MATR_CD --자재마스터 AND T.CO_CD = M.CO_CD LEFT OUTER JOIN TB_CR02D02 S ON T.SALES_CD = S.SALES_CD --수주상세(Sales Code) AND T.ORDRS_CO_CD = S.CO_CD LEFT OUTER JOIN TB_CR02M01 SM ON T.ORDRS_CO_CD = SM.CO_CD --수주상세(Sales Code) AND T.ORDRS_NO = SM.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 X ON SM.ORDRS_CLNT_CD = X.CLNT_CD --거래처마스터 LEFT OUTER JOIN TB_BM01M01 P ON S.PRDT_CD = P.PRDT_CD --제품마스터 LEFT OUTER JOIN( SELECT ORDRG_NO , SUM(A.PCHS_AMT) AS PCHS_AMT , SUM(A.PCHS_VAT) AS PCHS_VAT , SUM(A.PCHS_QTY) AS PCHS_QTY , PCHS_NO , COUNT(A.PCHS_NO) PCHS_CNT , SUM(DECODE(A.CMPLET_YN, 'Y', 1, 0)) AS CMPLET_SUM , MAX(A.PCHS_DT) AS PCHS_DT , MAX(A.BILL_YN) AS BILL_YN FROM TB_SM12D01 A WHERE 1=1 AND ORDRG_NO LIKE 'COST%' GROUP BY A.CO_CD , A.ORDRG_NO , A.PCHS_NO ) E ON(T.COST_NO = E.ORDRG_NO) /* 매입 DETAIL */
WHERE 1=1
AND T.CO_CD = #{coCd}
AND T.COST_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD') +1
AND SM.ORDRS_CLNT_CD = #{vendCd}
AND X.CLNT_NM LIKE '%'||#{vendNm}||'%'
AND T.ORDRS_NO LIKE '%'||#{ordrsNo}||'%'
AND T.SALES_CD LIKE '%'||#{salesCd}||'%'
AND S.PRDT_CD = #{prdtCd}
AND P.PRDT_NM LIKE '%'||#{prdtNm}||'%'
AND S.ITEM_DIV = #{itemCd}
AND T.PCHS_CLNT_CD = #{clntCd}
AND A.CLNT_NM LIKE '%'||#{clntNm}||'%'
AND T.PCHS_COST_DIV10 = #{pchsCostDiv10}
AND T.PCHS_COST_DIV20 = #{pchsCostDiv20}
AND B.NAME LIKE '%' || #{userNm} || '%'
AND T.COST_NO LIKE '%'||#{costNo}||'%'
ORDER BY T.CO_CD, T.COST_NO DESC, A.CLNT_NM, T.SALES_CD ) AS a ) A WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT COUNT(FILE_TRGT_KEY) CNT
  FROM TB_SM10M01
 WHERE FILE_TRGT_KEY = #{fileTrgtKey}

mapper.update:
UPDATE TB_SM10M01
	SET 
		  MAIL_YN 	 	= #{mailYn}	
		, MAIL_DTTM		= SYSDATE	
		, MAIL_SEND_ID	= #{userId} 
WHERE CO_CD = #{coCd} 
 AND COST_NO = #{ordrgNo}

mapper.insert:
    INSERT INTO TB_SM10D02
    (
		CO_CD
		, COST_NO
		, COST_SEQ
		, CREAT_ID
		, CREAT_PGM
		, CREAT_DTTM
      )
    VALUES
    (
		#{coCd}
		, #{costNo}
		, (SELECT  NVL(max(TO_NUMBER(COST_SEQ)),0)+1 FROM TB_SM10D02 WHERE CO_CD = #{coCd} AND COST_NO = #{costNo})
		, #{creatId}
		, #{pgmId}
		, SYSDATE	   
    )
