mapper.select:
WITH CT AS ( SELECT M.* , B.CLNT_NM FROM TB_SM11M01 M LEFT OUTER JOIN TB_BM02M01 B ON M.CLNT_CD = B.CLNT_CD WHERE 1=1
AND M.CO_CD = #{coCd}
AND M.CTRT_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD')
AND M.CLNT_CD = #{clntCd}
AND B.CLNT_NM LIKE '%'||#{clntNm}||'%'
AND M.ORDRS_NO LIKE '%'||#{ordrsNo}||'%'
AND M.SALES_CD LIKE '%'||#{salesCd}||'%'
) , PAY AS ( SELECT R.CTRT_NO , SUM(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2010',COST_AMT)) AS PAY_AMT11 --매입금액 , TO_CHAR(MAX(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2010',COST_DT)), 'MM-DD') AS PAY_DT11 --매입일자 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2010',PAY_DIV_DT)) AS PAY_TX_DT11 --세금계산서발행 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2010',PCHS_NO)) AS PAY_CF_NO11 --매입확정번호 , SUM(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2020',COST_AMT)) AS PAY_AMT12 , TO_CHAR(MAX(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2020',COST_DT)), 'MM-DD') AS PAY_DT12 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2020',PAY_DIV_DT)) AS PAY_TX_DT12 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2020',PCHS_NO)) AS PAY_CF_NO11 --매입확정번호 , SUM(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2030',COST_AMT)) AS PAY_AMT13 , TO_CHAR(MAX(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2030',COST_DT)), 'MM-DD') AS PAY_DT13 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2030',PAY_DIV_DT)) AS PAY_TX_DT13 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2030',PCHS_NO)) AS PAY_CF_NO11 --매입확정번호 , SUM(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2040',COST_AMT)) AS PAY_AMT14 , TO_CHAR(MAX(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2040',COST_DT)), 'MM-DD') AS PAY_DT14 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2040',PAY_DIV_DT)) AS PAY_TX_DT14 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2040',PCHS_NO)) AS PAY_CF_NO11 --매입확정번호 , SUM(R.COST_AMT) AS PAY_AMT19 FROM TB_SM10M01 R WHERE 1 = 1 AND EXISTS (SELECT 1 FROM CT WHERE R.CTRT_NO = CT.CTRT_NO) GROUP BY R.CTRT_NO ) SELECT count(*) FROM CT M LEFT OUTER JOIN PAY ON PAY.CTRT_NO = M.CTRT_NO --지급실적 WHERE 1=1
AND ((M.CTRT_AMT - NVL(PAY_AMT19, 0) = 0 AND #{cmpletYn} = 'Y') OR (M.CTRT_AMT - NVL(PAY_AMT19, 0) != 0 AND #{cmpletYn} = 'N'))

/* selectContractList */ WITH CT AS ( SELECT M.* , CASE WHEN M.CTRT_ITEMS LIKE '%CTRITEM01%' THEN 'Y' ELSE 'N' END AS FLAG1 , CASE WHEN M.CTRT_ITEMS LIKE '%CTRITEM02%' THEN 'Y' ELSE 'N' END AS FLAG2 , CASE WHEN M.CTRT_ITEMS LIKE '%CTRITEM03%' THEN 'Y' ELSE 'N' END AS FLAG3 , CASE WHEN M.CTRT_ITEMS LIKE '%CTRITEM04%' THEN 'Y' ELSE 'N' END AS FLAG4 , CASE WHEN M.CTRT_ITEMS LIKE '%CTRITEM05%' THEN 'Y' ELSE 'N' END AS FLAG5 , CASE WHEN M.CTRT_ITEMS LIKE '%CTRITEM06%' THEN 'Y' ELSE 'N' END AS FLAG6 , CASE WHEN M.CTRT_ITEMS LIKE '%CTRITEM99%' THEN 'Y' ELSE 'N' END AS FLAG9 , B.CLNT_NM , CR.EQP_NM --수주상세 설비명 , CR.ORDRS_QTY --수주상세 대수 FROM TB_SM11M01 M LEFT OUTER JOIN TB_BM02M01 B ON M.CLNT_CD = B.CLNT_CD LEFT OUTER JOIN ( SELECT MM.CTRT_NO, MM.ORDRS_CO_CD, MM.SALES_CD FROM TB_SM11D02 MM WHERE MM.CTRT_NO_SEQ = (SELECT min(CTRT_NO_SEQ) FROM TB_SM11D02 D WHERE D.CTRT_NO = MM.CTRT_NO) GROUP BY MM.CTRT_NO, MM.ORDRS_CO_CD, MM.SALES_CD ) AS MD ON MD.CTRT_NO = M.CTRT_NO LEFT OUTER JOIN TB_CR02D02 CR ON CR.SALES_CD = MD.SALES_CD WHERE 1=1
AND M.CO_CD = #{coCd}
AND M.CTRT_DT BETWEEN TO_DATE( #{reqDtFrom},'YYYYMMDD') AND TO_DATE( #{reqDtTo},'YYYYMMDD')
AND M.CLNT_CD = #{clntCd}
AND B.CLNT_NM LIKE '%'||#{clntNm}||'%'
AND M.ORDRS_NO LIKE '%'||#{ordrsNo}||'%'
AND M.SALES_CD LIKE '%'||#{salesCd}||'%'
) , PLAN AS ( SELECT CTRT_NO , SUM(decode(CLMN_PLAN_DIV,'PCHSCOSTDIV2010',CLMN_RATE)) AS PLAN_YUL1 , SUM(decode(CLMN_PLAN_DIV,'PCHSCOSTDIV2010',CLMN_AMT)) AS PLAN_AMT1 , MAX(decode(CLMN_PLAN_DIV,'PCHSCOSTDIV2010',CLMN_DT)) AS PLAN_DT1 , SUM(decode(CLMN_PLAN_DIV,'PCHSCOSTDIV2020',CLMN_RATE)) AS PLAN_YUL2 , SUM(decode(CLMN_PLAN_DIV,'PCHSCOSTDIV2020',CLMN_AMT)) AS PLAN_AMT2 , MAX(decode(CLMN_PLAN_DIV,'PCHSCOSTDIV2020',CLMN_DT)) AS PLAN_DT2 , SUM(decode(CLMN_PLAN_DIV,'PCHSCOSTDIV2030',CLMN_RATE)) AS PLAN_YUL3 , SUM(decode(CLMN_PLAN_DIV,'PCHSCOSTDIV2030',CLMN_AMT)) AS PLAN_AMT3 , MAX(decode(CLMN_PLAN_DIV,'PCHSCOSTDIV2030',CLMN_DT)) AS PLAN_DT3 , SUM(decode(CLMN_PLAN_DIV,'PCHSCOSTDIV2040',CLMN_RATE)) AS PLAN_YUL4 , SUM(decode(CLMN_PLAN_DIV,'PCHSCOSTDIV2040',CLMN_AMT)) AS PLAN_AMT4 , MAX(decode(CLMN_PLAN_DIV,'PCHSCOSTDIV2040',CLMN_DT)) AS PLAN_DT4 , SUM(CLMN_AMT) AS PLAN_AMT9 FROM TB_SM11D01 P WHERE 1 = 1 AND EXISTS (SELECT 1 FROM CT WHERE P.CTRT_NO = CT.CTRT_NO) GROUP BY P.CTRT_NO ) , PAY AS ( SELECT R.CTRT_NO , SUM(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2010',COST_AMT)) AS PAY_AMT11 --매입금액 , TO_CHAR(MAX(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2010',COST_DT)), 'MM-DD') AS PAY_DT11 --매입일자 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2010',PAY_DIV_DT)) AS PAY_TX_DT11 --세금계산서발행 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2010',PCHS_NO)) AS PAY_CF_NO11 --매입확정번호 , SUM(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2020',COST_AMT)) AS PAY_AMT12 , TO_CHAR(MAX(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2020',COST_DT)), 'MM-DD') AS PAY_DT12 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2020',PAY_DIV_DT)) AS PAY_TX_DT12 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2020',PCHS_NO)) AS PAY_CF_NO11 --매입확정번호 , SUM(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2030',COST_AMT)) AS PAY_AMT13 , TO_CHAR(MAX(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2030',COST_DT)), 'MM-DD') AS PAY_DT13 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2030',PAY_DIV_DT)) AS PAY_TX_DT13 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2030',PCHS_NO)) AS PAY_CF_NO11 --매입확정번호 , SUM(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2040',COST_AMT)) AS PAY_AMT14 , TO_CHAR(MAX(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2040',COST_DT)), 'MM-DD') AS PAY_DT14 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2040',PAY_DIV_DT)) AS PAY_TX_DT14 , MIN(decode(PCHS_COST_DIV20,'PCHSCOSTDIV2040',PCHS_NO)) AS PAY_CF_NO11 --매입확정번호 , SUM(R.COST_AMT) AS PAY_AMT19 FROM TB_SM10M01 R WHERE 1 = 1 AND EXISTS (SELECT 1 FROM CT WHERE R.CTRT_NO = CT.CTRT_NO) GROUP BY R.CTRT_NO ) SELECT * FROM( SELECT ROWNUM AS RNUM, A.*
FROM (SELECT M.FILE_TRGT_KEY , M.CO_CD , FN_CM05M01_CD_TO_NM(M.CO_CD) AS CO_CD_NM , M.CTRT_NO , M.CLNT_CD AS VEND_CD --공급사 , M.CLNT_NM AS VEND_NM --공급사명 , C.ORDRS_CLNT_CD AS CLNT_CD --수주고객사 , D.CLNT_NM --수주고객사명 , M.ORDRS_CO_CD --수주회사코드 , M.ORDRS_NO --수주번호 , C.CLNT_PJT --고객사프로젝트 , FN_CM05M01_CD_TO_NM(C.CLNT_PJT) AS CLNT_PJT_NM , M.CTRT_NM --외주계약명 , TO_CHAR(M.CTRT_DT, 'YYYY-MM-DD') AS CTRT_DT , M.CTRT_AMT , M.CTRT_ITEMS --외주계약 품목 , M.CTRT_PMNT_MTD_CD , FN_CM05M01_CD_TO_NM(M.CTRT_PMNT_MTD_CD) AS CTRT_PMNT_MTD_CD_NM , M.CTRT_CLMN_DIV_CD , FN_CM05M01_CD_TO_NM(M.CTRT_CLMN_DIV_CD) AS CTRT_CLMN_DIV_CD_NM , M.CTRT_CLMN_DAY , M.CTRT_CLMN_VAT , FN_CM05M01_CD_TO_NM(M.CTRT_CLMN_VAT) AS CTRT_CLMN_VAT_NM , TO_CHAR(M.CTRT_ORDRS_DT, 'YYYY-MM-DD') AS CTRT_ORDRS_DT , TO_CHAR(M.CTRT_PFMC_DT, 'YYYY-MM-DD') AS CTRT_PFMC_DT , TO_CHAR(M.CTRT_BF_PFMC_DT, 'YYYY-MM-DD') AS CTRT_BF_PFMC_DT , TO_CHAR(M.CTRT_AF_PFMC_DT, 'YYYY-MM-DD') AS CTRT_AF_PFMC_DT , M.CURR_CD , FN_CM05M01_CD_TO_NM(M.CURR_CD) AS CURR_CD_NM , M.EXRATE , (SELECT FN_CM06M01_ID_TO_NM(M.MNG_ID) FROM DUAL) MNG_ID_NM , M.CTRT_RMK , M.ETC_FIELD1 , M.ETC_FIELD2 , M.ETC_FIELD3 , M.CREAT_ID , (SELECT FN_CM06M01_ID_TO_NM(M.CREAT_ID) FROM DUAL) CREAT_ID_NM , M.CREAT_PGM , TO_CHAR(M.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM , M.UDT_ID , (SELECT FN_CM06M01_ID_TO_NM(M.UDT_ID) FROM DUAL) UDT_ID_NM , M.UDT_PGM , TO_CHAR(M.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM , TO_CHAR(M.DUDT_INTEND_DT, 'YYYY-MM-DD') AS DUDT_INTEND_DT --납기일자 , M.CTRT_AMT - NVL(PAY_AMT19, 0) AS REM_AMT --미지급금액 , M.FLAG1 , M.FLAG2 , M.FLAG3 , M.FLAG4 , M.FLAG5 , M.FLAG6 , M.FLAG9 , M.EQP_NM --수주상세 설비명 , M.ORDRS_QTY --수주상세 대수 , PLAN.* , PAY.* FROM CT M --------- LEFT OUTER JOIN TB_BM02M01 B ON M.CLNT_CD = B.CLNT_CD LEFT OUTER JOIN TB_CR02M01 C ON C.CO_CD = M.ORDRS_CO_CD AND C.ORDRS_NO = M.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 D ON D.CLNT_CD = C.ORDRS_CLNT_CD LEFT OUTER JOIN PLAN ON PLAN.CTRT_NO = M.CTRT_NO --지급계획 LEFT OUTER JOIN PAY ON PAY.CTRT_NO = M.CTRT_NO --지급실적 WHERE 1=1
AND ((M.CTRT_AMT - NVL(PAY_AMT19, 0) = 0 AND #{cmpletYn} = 'Y') OR (M.CTRT_AMT - NVL(PAY_AMT19, 0) != 0 AND #{cmpletYn} = 'N'))
ORDER BY M.CO_CD, M.CLNT_NM, M.CTRT_DT DESC, M.CTRT_NO DESC ) AS a ) A WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

