mapper.select:
SELECT count(*) FROM TB_SM11D01 P LEFT OUTER JOIN TB_SM11M01 M ON P.CTRT_NO = M.CTRT_NO LEFT OUTER JOIN TB_BM02M01 B ON M.CLNT_CD = B.CLNT_CD LEFT OUTER JOIN TB_CR02M01 C ON C.CO_CD = M.ORDRS_CO_CD AND C.ORDRS_NO = M.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 D ON D.CLNT_CD = C.ORDRS_CLNT_CD LEFT OUTER JOIN TB_SM10M01 R ON R.CTRT_NO = P.CTRT_NO AND R.CTRT_NO_SEQ = P.CTRT_NO_SEQ LEFT OUTER JOIN TB_SM12D01 RD ON RD.ORDRG_NO = R.COST_NO WHERE 1=1
AND M.CO_CD = #{coCd}
AND P.CLMN_DT BETWEEN ADD_MONTHS(TO_DATE(#{closeYm}||'26', 'YYYYMMDD'), -1) AND TO_DATE(#{closeYm}||'25', 'YYYYMMDD')
AND M.CLNT_CD = #{vendCd}
AND B.CLNT_NM LIKE '%'||#{vendNm}||'%'
AND C.ORDRS_CLNT_CD = #{clntCd}
AND D.CLNT_NM LIKE '%'||#{clntNm}||'%'
AND M.ORDRS_NO LIKE '%'||#{ordrsNo}||'%'
AND M.SALES_CD LIKE '%'||#{salesCd}||'%'
AND M.CTRT_CLMN_DAY = #{pchsClmnDay}
AND ((NVL(RD.CMPLET_YN,'N') = 'Y' AND 'Y' = #{completYn}) OR (NVL(RD.CMPLET_YN,'N') != 'Y' AND 'N' = #{completYn}))
ORDER BY M.CO_CD, M.CTRT_NO DESC, B.CLNT_NM

SELECT * FROM( SELECT ROWNUM AS RNUM, A.*
FROM ( SELECT M.FILE_TRGT_KEY , M.CO_CD , FN_CM05M01_CD_TO_NM(M.CO_CD) AS CO_CD_NM , M.CTRT_NO , P.CTRT_NO_SEQ , M.CLNT_CD AS VEND_CD --공급사 , B.CLNT_NM AS VEND_NM --공급사명 , C.ORDRS_CLNT_CD AS CLNT_CD --수주고객사 , D.CLNT_NM --수주고객사명 , M.ORDRS_CO_CD --수주회사코드 , M.ORDRS_NO --수주번호 , C.CLNT_PJT --고객사프로젝트 , FN_CM05M01_CD_TO_NM(C.CLNT_PJT) AS CLNT_PJT_NM , M.CTRT_NM --외주계약명 , TO_CHAR(M.CTRT_DT, 'YYYY-MM-DD') AS CTRT_DT , M.CTRT_AMT , M.CTRT_ITEMS --외주계약 품목 , M.CTRT_PMNT_MTD_CD , FN_CM05M01_CD_TO_NM(M.CTRT_PMNT_MTD_CD) AS CTRT_PMNT_MTD_CD_NM , M.CTRT_CLMN_DIV_CD , FN_CM05M01_CD_TO_NM(M.CTRT_CLMN_DIV_CD) AS CTRT_CLMN_DIV_CD_NM , M.CTRT_CLMN_DAY , M.CTRT_CLMN_VAT , FN_CM05M01_CD_TO_NM(M.CTRT_CLMN_VAT) AS CTRT_CLMN_VAT_NM , TO_CHAR(M.CTRT_ORDRS_DT, 'YYYY-MM-DD') AS CTRT_ORDRS_DT , TO_CHAR(M.CTRT_PFMC_DT, 'YYYY-MM-DD') AS CTRT_PFMC_DT , TO_CHAR(M.CTRT_BF_PFMC_DT, 'YYYY-MM-DD') AS CTRT_BF_PFMC_DT , TO_CHAR(M.CTRT_AF_PFMC_DT, 'YYYY-MM-DD') AS CTRT_AF_PFMC_DT , M.CURR_CD , FN_CM05M01_CD_TO_NM(M.CURR_CD) AS CURR_CD_NM , M.EXRATE , (SELECT FN_CM06M01_ID_TO_NM(M.MNG_ID) FROM DUAL) MNG_ID_NM , M.CTRT_RMK , M.ETC_FIELD1 , M.ETC_FIELD2 , M.ETC_FIELD3 , M.CREAT_ID , (SELECT FN_CM06M01_ID_TO_NM(M.CREAT_ID) FROM DUAL) CREAT_ID_NM , M.CREAT_PGM , TO_CHAR(M.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM , M.UDT_ID , (SELECT FN_CM06M01_ID_TO_NM(M.UDT_ID) FROM DUAL) UDT_ID_NM , M.UDT_PGM , TO_CHAR(M.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM , P.CLMN_PLAN_DIV , FN_CM05M01_CD_TO_NM(P.CLMN_PLAN_DIV) AS CLMN_PLAN_DIV_NM , P.CLMN_DIV_SEQ , P.CLMN_RATE , P.CLMN_AMT , TO_CHAR(P.CLMN_DT, 'YYYY-MM-DD') AS CLMN_DT , P.CLMN_PLAN_RMK , P.CLMN_MGMT_STATUS , R.COST_NO , TO_CHAR(R.COST_DT, 'YYYY-MM-DD') AS COST_DT , FN_CM05M01_CD_TO_NM(R.PCHS_COST_DIV20) AS PCHS_COST_DIV20_NM , R.COST_AMT , TO_CHAR(R.PAY_DIV_DT, 'YYYY-MM-DD') AS PAY_DIV_DT , R.PCHS_NO , decode(RD.PCHS_NO,'',R.COST_AMT,0 ) AS UNCONF_AMT --미확정금액 , RD.PCHS_AMT AS CONF_AMT --확정금액 , decode(RD.CMPLET_YN,'Y',RD.PCHS_AMT,0 ) AS BILL_AMT --지급금액 , decode(RD.CMPLET_YN,'Y',(P.CLMN_AMT-RD.PCHS_AMT), P.CLMN_AMT ) AS REM_AMT --미지급금 , DECODE(R.COST_NO,'','N','Y') AS CREATE_YN --매입생성완료 , DECODE(RD.PCHS_NO,'','N','Y') AS CONF_YN --매입확정완료 , NVL(RD.BILL_YN,'N') AS BILL_YN --계산서처리완료 , M.FILE_TRGT_KEY , TO_CHAR(ㅡ.DUDT_INTEND_DT, 'YYYY-MM-DD') AS DUDT_INTEND_DT --납기일자 FROM TB_SM11D01 P LEFT OUTER JOIN TB_SM11M01 M ON P.CTRT_NO = M.CTRT_NO LEFT OUTER JOIN TB_BM02M01 B ON M.CLNT_CD = B.CLNT_CD LEFT OUTER JOIN TB_CR02M01 C ON C.CO_CD = M.ORDRS_CO_CD AND C.ORDRS_NO = M.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 D ON D.CLNT_CD = C.ORDRS_CLNT_CD LEFT OUTER JOIN TB_SM10M01 R ON R.CTRT_NO = P.CTRT_NO AND R.CTRT_NO_SEQ = P.CTRT_NO_SEQ LEFT OUTER JOIN TB_SM12D01 RD ON RD.ORDRG_NO = R.COST_NO WHERE 1=1
AND M.CO_CD = #{coCd}
AND P.CLMN_DT BETWEEN ADD_MONTHS(TO_DATE(#{closeYm}||'26', 'YYYYMMDD'), -0) AND ADD_MONTHS(TO_DATE(#{closeYm}||'25', 'YYYYMMDD'), +1)
AND M.CLNT_CD = #{vendCd}
AND B.CLNT_NM LIKE '%'||#{vendNm}||'%'
AND C.ORDRS_CLNT_CD = #{clntCd}
AND D.CLNT_NM LIKE '%'||#{clntNm}||'%'
