mapper.select:
--입고내역-발주분 WITH IPGO AS ( SELECT CO_CD , SALES_CD , NVL(SALES_CD,'00999-9900000') AS VIEW_SALES_CD , decode(SUBSTRING(SALES_CD,0,2),'AS',9,1) AS SEQ_DIV --AS로 시작하는 salesCd 맨뒤로 보내기 위함 , REQ_NO , SUM(IN_QTY) AS IN_QTY , SUM(BAD_QTY) AS BAD_QTY , SUM(IN_DTL_AMT) AS IN_DTL_AMT , TO_CHAR(MIN(IN_DT), 'YYYY-MM-DD') MIN_IN_DT , TO_CHAR(max(IN_DT), 'YYYY-MM-DD') IN_DT , MAX(STS) AS STS , SUM(GER_AMT) AS GER_AMT , SUM(ELEC_AMT) AS ELEC_AMT , SUM(TRUN_AMT) AS TRUN_AMT , SUM(STD_AMT) AS STD_AMT , SUM(BAD_AMT) AS BAD_AMT , SUM(AS_AMT) AS AS_AMT , SUM(AS_PAY_AMT) AS AS_PAY_AMT , SUM(AS_NON_PAY_AMT) AS AS_NON_PAY_AMT
, SUM(PCHS_GOODS_AMT) AS PCHS_GOODS_AMT , SUM(RAW_MAT_AMT) AS RAW_MAT_AMT , SUM(CAM_AMT) AS CAM_AMT , SUM(PROC_GOODS_AMT) AS PROC_GOODS_AMT , SUM(POST_PROC_AMT) AS POST_PROC_AMT , SUM(TRANS_AMT) AS TRANS_AMT , SUM(ETC_AMT) AS ETC_AMT
, max(PART_CD) AS PART_CD /* 발주분류명 */ , max(REQ_CD) AS REQ_CD /* 발주요청구분명 */ , max(ORDRG_ID) AS ORDRG_ID /* 발주발행자명 */ FROM ( SELECT IPGO.CO_CD , M.REQ_NO , IPGO.SALES_CD , IPGO.ORDRG_NO , SUM(IPGO.IN_QTY) AS IN_QTY , SUM(IPGO.BAD_QTY) AS BAD_QTY , SUM(IPGO.IN_DTL_AMT) AS IN_DTL_AMT , MIN(IPGO.IN_DT) AS MIN_IN_DT , max(IPGO.IN_DT) AS IN_DT , '입고' AS STS , SUM(decode(J.GOODS_DIV,'GOODSDIV03', 0, 'GOODSDIV07', 0, 'GOODSDIV08', 0, decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT) )) AS GER_AMT --운송비('GOODSDIV08')는 제외 , SUM(decode(J.GOODS_DIV,'GOODSDIV03', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT) ,0)) AS ELEC_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV07', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT),0)) AS TRUN_AMT
, SUM(decode(J.GOODS_DIV,'GOODSDIV08', 0, decode(Q.PART_CD,'COBTP03',0,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT))) AS STD_AMT --정상금액 --운송비('GOODSDIV08')는 제외 , SUM(decode(J.GOODS_DIV,'GOODSDIV08', 0, decode(Q.PART_CD,'COBTP03',IPGO.IN_DTL_AMT,0))) AS BAD_AMT --장애금액 --운송비('GOODSDIV08')는 제외 , SUM(decode(J.GOODS_DIV,'GOODSDIV08', 0, decode(Q.PART_CD,'COBTP04',IPGO.IN_DTL_AMT,'COBTP05',IPGO.IN_DTL_AMT,0))) AS AS_AMT --AS유무상금액 --운송비('GOODSDIV08')는 제외 , SUM(decode(J.GOODS_DIV,'GOODSDIV08', 0, decode(Q.PART_CD,'COBTP04',IPGO.IN_DTL_AMT,0))) AS AS_PAY_AMT --AS유상금액 --운송비('GOODSDIV08')는 제외 , SUM(decode(J.GOODS_DIV,'GOODSDIV08', 0, decode(Q.PART_CD,'COBTP05',IPGO.IN_DTL_AMT,0))) AS AS_NON_PAY_AMT --AS무상금액 --운송비('GOODSDIV08')는 제외
, SUM(decode(J.GOODS_DIV,'GOODSDIV01', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT), 0)) AS PCHS_GOODS_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV02', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT), 'GOODSDIV03', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT), 0)) AS RAW_MAT_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV04', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT), 0)) AS CAM_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV05', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT), 0)) AS PROC_GOODS_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV06', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT), 0)) AS POST_PROC_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV08', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT), 0)) AS TRANS_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV07', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT), 'GOODSDIV09', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT), 'GOODSDIV10', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT), 'GOODSDIV11', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,IPGO.IN_DTL_AMT), 0)) AS ETC_AMT , max(Q.PART_CD) AS PART_CD /* 발주분류명 */ , max(Q.REQ_CD) AS REQ_CD /* 발주요청구분명 */ , max(Q.ORDRG_ID) AS ORDRG_ID /* 발주발행자명 */ FROM TB_SM03D01 IPGO LEFT OUTER JOIN TB_SM02M01 M ON(IPGO.ORDRG_NO = M.ORDRG_NO) /* 발주마스터 */ LEFT OUTER JOIN TB_BM02M01 D ON M.PCHS_CLNT_CD = D.CLNT_CD LEFT OUTER JOIN TB_BM05M01 J ON IPGO.MATR_CD = J.MATR_CD /* 자재마스터 */ LEFT OUTER JOIN TB_QM01M01 Q ON IPGO.REQ_NO = Q.REQ_NO WHERE 1 = 1
AND IPGO.CO_CD = #{coCd}
AND IPGO.SALES_CD LIKE '%'|| #{salesCd} || '%'
AND 'N' = #{pchsGb}
AND NVL2(IPGO.PCHS_NO,'Y','N') = #{cmpletYn}
GROUP BY IPGO.CO_CD, IPGO.SALES_CD, M.REQ_NO, IPGO.ORDRG_NO UNION ALL --구매비용집계현황 SELECT E.CO_CD , NVL(E.REQ_NO,'REQ9999999') AS REQ_NO , ED.SALES_CD , E.COST_NO , 0 AS IN_QTY , 0 AS BAD_QTY , SUM(NVL2(EC.SL_CNT,ROUND(E.COST_AMT/EC.SL_CNT,0),E.COST_AMT)) AS COST_AMT , MIN(E.COST_DT) AS MIN_COST_DT , max(E.COST_DT) AS COST_DT , '비용' AS STS , SUM(decode(E.PCHS_COST_DIV10,'PCHSCOSTDIV1050', 0, 'PCHSCOSTDIV1030', 0, decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,NVL2(EC.SL_CNT,ROUND(E.COST_AMT/EC.SL_CNT,0),E.COST_AMT)))) AS GER_AMT , SUM(decode(E.PCHS_COST_DIV10,'PCHSCOSTDIV1050', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,NVL2(EC.SL_CNT,ROUND(E.COST_AMT/EC.SL_CNT,0),E.COST_AMT)),0)) AS ELEC_AMT --전기비용 , SUM(decode(E.PCHS_COST_DIV10,'PCHSCOSTDIV1030', decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,NVL2(EC.SL_CNT,ROUND(E.COST_AMT/EC.SL_CNT,0),E.COST_AMT)),0)) AS TRUN_AMT --턴키비용
-- , MAX(EC.SL_CNT) AS SL_CNT , SUM(decode(Q.PART_CD,'',NVL2(EC.SL_CNT,ROUND(E.COST_AMT/EC.SL_CNT,0),E.COST_AMT),'COBTP03',0,'COBTP04',0,'COBTP05',0,NVL2(EC.SL_CNT,ROUND(E.COST_AMT/EC.SL_CNT,0),E.COST_AMT))) AS STD_AMT --정상금액 , SUM(decode(Q.PART_CD,'COBTP03',NVL2(EC.SL_CNT,ROUND(E.COST_AMT/EC.SL_CNT,0),E.COST_AMT),0)) AS BAD_AMT --장애금액 , SUM(decode(Q.PART_CD,'COBTP04',NVL2(EC.SL_CNT,ROUND(E.COST_AMT/EC.SL_CNT,0),E.COST_AMT), 'COBTP05',NVL2(EC.SL_CNT,ROUND(E.COST_AMT/EC.SL_CNT,0),E.COST_AMT),0)) AS AS_AMT --AS유무상금액 , SUM(decode(Q.PART_CD,'COBTP04',NVL2(EC.SL_CNT,ROUND(E.COST_AMT/EC.SL_CNT,0),E.COST_AMT),0)) AS AS_PAY_AMT --AS유상금액 , SUM(decode(Q.PART_CD,'COBTP05',NVL2(EC.SL_CNT,ROUND(E.COST_AMT/EC.SL_CNT,0),E.COST_AMT),0)) AS AS_NON_PAY_AMT --AS무상금액 , 0 , 0 , 0 , 0 , 0 , 0 , SUM(decode(Q.PART_CD,'COBTP04',0,'COBTP05',0,NVL2(EC.SL_CNT,ROUND(E.COST_AMT/EC.SL_CNT,0),E.COST_AMT))) , max(Q.PART_CD) AS PART_CD /* 발주분류명 */ , max(Q.REQ_CD) AS REQ_CD /* 발주요청구분명 */ , max(Q.ORDRG_ID) AS ORDRG_ID /* 발주발행자명 */ FROM TB_SM10M01 E /* 구매비용 */ LEFT OUTER JOIN TB_SM10D01 ED ON E.COST_NO = ED.COST_NO LEFT OUTER JOIN TB_BM02M01 D ON E.PCHS_CLNT_CD = D.CLNT_CD LEFT OUTER JOIN ( SELECT CO_CD , COST_NO , COUNT(SALES_CD) SL_CNT FROM TB_SM10D01 ED GROUP BY CO_CD, COST_NO) EC ON E.COST_NO = EC.COST_NO LEFT OUTER JOIN TB_QM01M01 Q ON E.REQ_NO = Q.REQ_NO WHERE 1 = 1 AND E.PCHS_CLNT_CD != 2 --건양트루넷이 구매처가 아닌것만 취합함
AND E.CO_CD = #{coCd}
AND ED.SALES_CD LIKE '%'|| #{salesCd} || '%'
AND 'Y' = #{pchsGb}
AND NVL2(E.PCHS_NO,'Y','N') = #{cmpletYn}
GROUP BY E.CO_CD, ED.SALES_CD, E.REQ_NO, E.COST_NO UNION ALL SELECT E.CO_CD , E.RET_NO , E.SALES_CD , E.RET_NO , SUM(R.RET_QTY) AS RET_QTY , 0 AS BAD_QTY , SUM(R.RET_DTL_AMT) AS RET_DTL_AMT , MIN(E.RET_DT) AS MIN_RET_DT , MAX(E.RET_DT) AS RET_DT , '반품' AS STS
, SUM(decode(J.GOODS_DIV,'GOODSDIV03', 0, 'GOODSDIV07', 0, 'GOODSDIV08', 0, R.RET_DTL_AMT)) AS GER_AMT --운송비('GOODSDIV08')는 제외ㅍ , SUM(decode(J.GOODS_DIV,'GOODSDIV03', R.RET_DTL_AMT,0)) AS ELEC_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV07', R.RET_DTL_AMT,0)) AS TRUN_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV08', 0, R.RET_DTL_AMT)) AS STD_AMT --정상금액 --운송비('GOODSDIV08')는 제외 , 0 AS BAD_AMT --장애금액 , 0 AS AS_AMT --AS유무상금액 , 0 AS AS_PAY_AMT --AS유상금액 --운송비('GOODSDIV08')는 제외 , 0 AS AS_NON_PAY_AMT --AS무상금액 --운송비('GOODSDIV08')는 제외
, SUM(decode(J.GOODS_DIV,'GOODSDIV01', R.RET_DTL_AMT, 0)) AS PCHS_GOODS_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV02', R.RET_DTL_AMT, 'GOODSDIV03',R.RET_DTL_AMT, 0)) AS RAW_MAT_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV04', R.RET_DTL_AMT, 0)) AS CAM_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV05', R.RET_DTL_AMT, 0)) AS PROC_GOODS_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV06', R.RET_DTL_AMT, 0)) AS POST_PROC_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV08', R.RET_DTL_AMT, 0)) AS TRANS_AMT , SUM(decode(J.GOODS_DIV,'GOODSDIV07', R.RET_DTL_AMT, 'GOODSDIV09', R.RET_DTL_AMT, 'GOODSDIV10', R.RET_DTL_AMT, 'GOODSDIV11', R.RET_DTL_AMT, 0)) AS ETC_AMT , '' AS PART_CD /* 발주분류명 */ , '' AS REQ_CD /* 발주요청구분명 */ , '' AS ORDRG_ID /* 발주발행자명 */ FROM TB_SM06M01 E /* 반품 */ LEFT OUTER JOIN TB_SM06D01 R ON R.CO_CD = E.CO_CD AND R.RET_NO = E.RET_NO LEFT OUTER JOIN TB_BM02M01 D ON E.PCHS_CLNT_CD = D.CLNT_CD LEFT OUTER JOIN TB_BM05M01 J ON R.MATR_CD = J.MATR_CD /* 자재마스터 */ WHERE 1 = 1
AND E.CO_CD = #{coCd}
AND E.SALES_CD LIKE '%'|| #{salesCd} || '%'
AND 'R' = #{pchsGb}
AND NVL2(R.PCHS_NO,'Y','N') = #{cmpletYn}
GROUP BY E.CO_CD, E.SALES_CD, E.RET_NO ) AS T GROUP BY CO_CD, SALES_CD, REQ_NO ) SELECT IPGO.* , (SELECT FN_CM05M01_CD_TO_NM(IPGO.PART_CD) FROM DUAL ) AS PART_CD_NM /* 발주분류명 */ , (SELECT FN_CM05M01_CD_TO_NM(IPGO.REQ_CD) FROM DUAL ) AS REQ_CD_NM /* 발주요청구분명 */ , (SELECT FN_CM06M01_ID_TO_NM(IPGO.ORDRG_ID) FROM DUAL ) AS ORDRG_ID_NM /* 발주발행자명 */ FROM IPGO ORDER BY IPGO.SEQ_DIV, IPGO.VIEW_SALES_CD DESC, IPGO.REQ_NO

SELECT IPGO.CO_CD , IPGO.IN_NO , IPGO.IN_SEQ , IPGO.ORDRG_NO , IPGO.ORDRG_SEQ , IPGO.REQ_NO , IPGO.SALES_CD , IPGO.MATR_CD , IPGO.MATR_TEST_DIV , IPGO.IN_PRC , IPGO.IN_QTY , IPGO.BAD_QTY , IPGO.IN_DTL_AMT , IPGO.CURR_CD , IPGO.EXRATE , IPGO.IN_DTL_RMK , TO_CHAR(IPGO.INOUT_KEY) AS INOUT_KEY , IPGO.CREAT_ID , IPGO.CREAT_PGM , TO_CHAR(IPGO.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM , IPGO.IO_DIV , TO_CHAR(IPGO.IN_DT, 'YYYY-MM-DD') AS IN_DT , C.CLNT_NM , D.DSGN_NO , J.MATR_NM , J.MATR_MAT , J.MATR_SIZE , J.MATR_SPEC , Q.PART_CD , (SELECT FN_CM05M01_CD_TO_NM(Q.PART_CD) FROM DUAL ) AS PART_CD_NM /* 발주분류명 */ , J.GOODS_DIV , (SELECT FN_CM05M01_CD_TO_NM(J.GOODS_DIV) FROM DUAL ) AS GOODS_DIV_NM /* 자재품목구분명 */ , IM.FILE_TRGT_KEY , 'N' AS IS_COST FROM TB_SM03D01 IPGO LEFT OUTER JOIN TB_SM02M01 M ON(IPGO.ORDRG_NO = M.ORDRG_NO) /* 발주마스터 */ LEFT OUTER JOIN TB_SM02D01 D ON(IPGO.ORDRG_NO = D.ORDRG_NO AND IPGO.ORDRG_SEQ = D.ORDRG_SEQ) /* 발주상세 */ LEFT OUTER JOIN TB_BM02M01 AS C ON M.PCHS_CLNT_CD = C.CLNT_CD --거래처마스터 LEFT OUTER JOIN TB_BM05M01 AS J ON IPGO.MATR_CD = J.MATR_CD --자재마스터 LEFT OUTER JOIN TB_QM01M01 Q ON IPGO.REQ_NO = Q.REQ_NO LEFT OUTER JOIN TB_SM03M01 IM ON IPGO.IN_NO = IM.IN_NO WHERE 1 = 1
AND IPGO.CO_CD = #{coCd}
AND 'N' = #{pchsGb}
AND J.GOODS_DIV IN ( ${goodsDiv} )
AND Q.PART_CD IN ( ${partCd} )
AND NVL2(IPGO.PCHS_NO,'Y','N') = #{cmpletYn}
AND IPGO.SALES_CD IS NULL
AND IPGO.SALES_CD LIKE '%'|| #{salesCd} || '%'
AND IPGO.REQ_NO IS NULL
AND IPGO.REQ_NO LIKE '%'|| #{reqNo} || '%'
UNION ALL --구매비용집계현황 SELECT E.CO_CD , E.COST_NO , 0 , E.ORDRS_NO , 0 , E.REQ_NO , E.SALES_CD , '' , '' , NVL2(EC.SL_CNT,ROUND(E.COST_AMT/EC.SL_CNT,0),E.COST_AMT) AS IN_PRC , 1 AS IN_QTY , 0 , NVL2(EC.SL_CNT,ROUND(E.COST_AMT/EC.SL_CNT,0),E.COST_AMT) AS IN_DTL_AMT , E.CURR_CD , E.EXRATE , E.COST_RMK , E.FILE_TRGT_KEY , E.CREAT_ID , E.CREAT_PGM , TO_CHAR(E.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM , '' AS IO_DIV , TO_CHAR(E.COST_DT, 'YYYY-MM-DD') AS IN_DT , C.CLNT_NM , '' AS DSGN_NO , DECODE( E.CTRT_NO, NULL, E.COST_RMK, TRIM( BOTH ',' FROM CASE WHEN INSTR(Y.CTRT_ITEMS, 'CTRITEM01') > 0 THEN '설계,' ELSE '' END || CASE WHEN INSTR(Y.CTRT_ITEMS, 'CTRITEM02') > 0 THEN '제작,' ELSE '' END || CASE WHEN INSTR(Y.CTRT_ITEMS, 'CTRITEM03') > 0 THEN '공사건,' ELSE '' END || CASE WHEN INSTR(Y.CTRT_ITEMS, 'CTRITEM04') > 0 THEN '슈퍼바이저,' ELSE '' END || CASE WHEN INSTR(Y.CTRT_ITEMS, 'CTRITEM05') > 0 THEN 'A/S,' ELSE '' END || CASE WHEN INSTR(Y.CTRT_ITEMS, 'CTRITEM09') > 0 THEN '단품티칭,' ELSE '' END || CASE WHEN INSTR(Y.CTRT_ITEMS, 'CTRITEM99') > 0 THEN '기타,' ELSE '' END )) || '-' || (SELECT FN_CM05M01_CD_TO_NM(E.PCHS_COST_DIV20) FROM DUAL ) /* 비용상세구분 */ AS MATR_NM , '' AS MATR_MAT , '' AS MATR_SIZE , '' AS MATR_SPEC , Q.PART_CD , (SELECT FN_CM05M01_CD_TO_NM(Q.PART_CD) FROM DUAL ) AS PART_CD_NM /* 발주분류명 */ , E.PCHS_COST_DIV10 , (SELECT FN_CM05M01_CD_TO_NM(E.PCHS_COST_DIV10) FROM DUAL ) AS GOODS_DIV_NM /* 자재품목구분명 */ , E.FILE_TRGT_KEY , 'Y' AS IS_COST FROM TB_SM10M01 E /* 구매비용 */ LEFT OUTER JOIN TB_BM02M01 C ON E.PCHS_CLNT_CD = C.CLNT_CD LEFT OUTER JOIN TB_QM01M01 Q ON E.REQ_NO = Q.REQ_NO LEFT OUTER JOIN TB_SM10D01 ED ON E.COST_NO = ED.COST_NO LEFT OUTER JOIN TB_SM11M01 Y ON E.CTRT_NO = Y.CTRT_NO --외주계약마스터 LEFT OUTER JOIN ( SELECT CO_CD , COST_NO , COUNT(SALES_CD) SL_CNT FROM TB_SM10D01 ED GROUP BY CO_CD, COST_NO) EC ON E.COST_NO = EC.COST_NO WHERE 1 = 1 AND E.PCHS_CLNT_CD != 2 --건양트루넷이 구매처가 아닌것만 취합함
AND E.CO_CD = #{coCd}
AND 'Y' = #{pchsGb}
AND E.PCHS_COST_DIV10 IN ( ${costCd} )
AND nvl(Q.PART_CD,'COBTP99') IN ( ${partCd} )
AND NVL2(E.PCHS_NO,'Y','N') = #{cmpletYn}
AND E.SALES_CD IS NULL
AND ED.SALES_CD LIKE '%'|| #{salesCd} || '%'
AND E.REQ_NO IS NULL
AND E.REQ_NO LIKE '%'|| #{reqNo} || '%'
UNION ALL --반품현황 SELECT E.CO_CD , E.RET_NO , R.RET_SEQ , I.ORDRG_NO , I.ORDRG_SEQ , I.REQ_NO , E.SALES_CD , R.MATR_CD , '' , R.RET_PRC , R.RET_QTY , 0 , R.RET_DTL_AMT , R.CURR_CD , R.EXRATE , R.RET_DTL_RMK , to_CHAR(R.INOUT_KEY) AS INOUT_KEY , R.CREAT_ID , R.CREAT_PGM , TO_CHAR(R.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM , '' , TO_CHAR(E.RET_DT, 'YYYY-MM-DD') AS RET_DT , C.CLNT_NM , R.DSGN_NO , J.MATR_NM , J.MATR_MAT , J.MATR_SIZE , J.MATR_SPEC , Q.PART_CD , (SELECT FN_CM05M01_CD_TO_NM(Q.PART_CD) FROM DUAL ) AS PART_CD_NM /* 발주분류명 */ , J.GOODS_DIV , (SELECT FN_CM05M01_CD_TO_NM(J.GOODS_DIV) FROM DUAL ) AS GOODS_DIV_NM /* 자재품목구분명 */ , E.FILE_TRGT_KEY , 'R' AS IS_COST FROM TB_SM06M01 E /* 반품 */ LEFT OUTER JOIN TB_SM06D01 R ON R.CO_CD = E.CO_CD AND R.RET_NO = E.RET_NO LEFT OUTER JOIN TB_SM03D01 I ON I.IN_NO = R.IN_NO AND I.IN_SEQ = R.IN_SEQ LEFT OUTER JOIN TB_QM01M01 Q ON I.REQ_NO = Q.REQ_NO LEFT OUTER JOIN TB_BM02M01 C ON E.PCHS_CLNT_CD = C.CLNT_CD LEFT OUTER JOIN TB_BM05M01 J ON R.MATR_CD = J.MATR_CD /* 자재마스터 */ WHERE 1 = 1
AND E.CO_CD = #{coCd}
AND 'R' = #{pchsGb}
AND J.GOODS_DIV IN ( ${goodsDiv} )
AND Q.PART_CD IN ( ${partCd} )
AND NVL2(R.PCHS_NO,'Y','N') = #{cmpletYn}
AND E.SALES_CD IS NULL
AND E.SALES_CD LIKE '%'|| #{salesCd} || '%'
AND Q.REQ_NO IS NULL
AND Q.REQ_NO LIKE '%'|| #{reqNo} || '%'

WITH IPGO AS ( SELECT A.* , D1.CLNT_NM AS PCHS_CLNT_NM , D1.CRN /*사업자등록번호*/ , D1.PCHS_CLMN_DAY /*결제일 - 10*/ , D1.PCHS_CLMN_RMK /*결제일 - 다음달 10일*/ , D1.PCHS_PMNT_MTD_CD --결제조건 코드 , FN_CM05M01_CD_TO_NM(D1.PCHS_PMNT_MTD_CD) AS PCHS_PMNT_MTD_NM /*결제조건명*/ FROM ( SELECT A.* , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN 'N' WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN 'R' ELSE 'Y' END IS_COST , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN '' WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN '' ELSE C.ORDRS_NO END ORDRS_NO --수주번호 , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN M.SALES_CD WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN RM.SALES_CD ELSE C.SALES_CD END SALES_CD , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN M.PCHS_CLNT_CD WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN RM.PCHS_CLNT_CD ELSE C.PCHS_CLNT_CD END PCHS_CLNT_CD , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN M.FILE_TRGT_KEY WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN RM.FILE_TRGT_KEY ELSE C.FILE_TRGT_KEY END FILE_TRGT_KEY , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN '발주' WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN '반품' ELSE '비용' END IS_DIV , (SELECT FN_CM06M01_ID_TO_NM(A.CREAT_ID) FROM DUAL) AS CREAT_ID_NM FROM TB_SM12D01 A /* 매입 확정 DETAIL */ LEFT OUTER JOIN TB_SM02M01 M ON(A.ORDRG_NO = M.ORDRG_NO) /* 발주마스터 */ LEFT OUTER JOIN TB_SM10M01 C ON(A.ORDRG_NO = C.COST_NO) /* 비용마스터 */ LEFT OUTER JOIN TB_SM06D01 R ON(A.ORDRG_NO = R.RET_NO) /* 반품 상세 */ LEFT OUTER JOIN TB_SM06M01 RM ON(RM.CO_CD = R.CO_CD AND RM.RET_NO = R.RET_NO) /* 반품 상세 */ WHERE 1=1 AND NVL(C.PAY_DIV,'PAYDIV02') = 'PAYDIV02' --기타비용의 경우 청구(PAYDIV02)인 경우만 구매현황에 출력함.
AND A.CO_CD = #{coCd}
AND A.PCHS_DT BETWEEN #{ordrgDtFrom} AND #{ordrgDtTo}
) A LEFT OUTER JOIN TB_BM02M01 D1 ON A.PCHS_CLNT_CD = D1.CLNT_CD --거래처마스터 WHERE 1 = 1
AND D1.PCHS_CLMN_DAY = #{pchsClmnDay}
) , IPGODTL AS --입고상세 ( SELECT * FROM TB_SM03D01 A /* 입고 DETAIL */ WHERE 1=1 AND EXISTS (SELECT 1 FROM IPGO WHERE IPGO.ORDRG_NO = A.ORDRG_NO) ) , IPGOAMT AS --입고집계 ( SELECT CO_CD, ORDRG_NO , SUM(IN_QTY) AS IN_QTY , SUM(BAD_QTY) AS BAD_QTY , SUM(IN_DTL_AMT) AS IN_DTL_AMT FROM IPGODTL A /* 입고 DETAIL */ GROUP BY CO_CD, ORDRG_NO ) , IPGOTOT AS --매입확정집계 ( SELECT CO_CD, ORDRG_NO , SUM(PCHS_AMT) AS PCHS_AMT_TOT , SUM(PCHS_VAT) AS PCHS_VAT_TOT , SUM(PCHS_QTY) AS PCHS_QTY_TO --수량은 입고 건별 1로 불일치 발생 FROM TB_SM12D01 A /* 매입확정 DETAIL */ WHERE 1=1 AND EXISTS (SELECT 1 FROM IPGO WHERE IPGO.ORDRG_NO = A.ORDRG_NO) GROUP BY CO_CD, ORDRG_NO ) , BAL AS --발주집계 ( SELECT A.CO_CD , A.ORDRG_NO , A.SALES_CD , SUM(A.ORDRG_QTY) ORDRG_QTY , SUM(A.ORDRG_AMT) ORDRG_AMT , MIN(A.DSGN_NO) AS DSGN_NO FROM TB_SM02D01 A WHERE 1=1 AND EXISTS (SELECT 1 FROM IPGO WHERE IPGO.ORDRG_NO = A.ORDRG_NO) GROUP BY A.CO_CD, A.ORDRG_NO, A.SALES_CD ) , QM AS ( SELECT DISTINCT M.CO_CD , M.ORDRG_NO , M.REQ_NO --발행번호 REQ2300099 , M.SALES_CD , Q.ORDRG_DT --발행일자 , Q.PART_CD --분류( 출도 추가 장애 A/S유상 A/S무상 SPARE유상 SPARE무상 고객E/O 설치시운전 기타) , Q.ORDRG_ID --발행자 FROM TB_SM02M01 M LEFT OUTER JOIN TB_QM01M01 Q ON M.REQ_NO = Q.REQ_NO WHERE 1=1 AND EXISTS (SELECT 1 FROM IPGO WHERE IPGO.ORDRG_NO = M.ORDRG_NO) ) SELECT count(*) FROM IPGO A /* 매입 확정 DETAIL */ LEFT OUTER JOIN BAL AS D ON A.CO_CD = D.CO_CD AND A.ORDRG_NO = D.ORDRG_NO --발주상세 LEFT OUTER JOIN IPGOTOT AS E ON A.CO_CD = E.CO_CD AND A.ORDRG_NO = E.ORDRG_NO --매입확정집계 LEFT OUTER JOIN IPGOAMT AS F ON A.CO_CD = F.CO_CD AND A.ORDRG_NO = F.ORDRG_NO --입고 집계 LEFT OUTER JOIN QM AS Q ON A.CO_CD = Q.CO_CD AND A.ORDRG_NO = Q.ORDRG_NO --발주요청 LEFT OUTER JOIN IPGODTL AS I ON A.CO_CD = I.CO_CD AND A.ORDRG_NO = I.ORDRG_NO --발주요청 LEFT OUTER JOIN TB_BM05M01 J ON I.MATR_CD = J.MATR_CD /* 자재마스터 */ WHERE 1=1 ORDER BY A.CO_CD, A.PCHS_CLNT_NM, A.PCHS_DT, A.ORDRG_NO, A.PCHS_NO

WITH IPGO AS ( SELECT A.* , D1.CLNT_NM AS PCHS_CLNT_NM , D1.CRN /*사업자등록번호*/ -- , D1.PCHS_CLMN_DAY /*결제일 - 10*/ -- , D1.PCHS_CLMN_RMK /*결제일 - 다음달 10일*/ -- , D1.PCHS_PMNT_MTD_CD --결제조건 코드 -- , FN_CM05M01_CD_TO_NM(D1.PCHS_PMNT_MTD_CD) AS PCHS_PMNT_MTD_NM /*결제조건명*/ , NVL(A.SM10_CLMN_DAY, D1.PCHS_CLMN_DAY) AS PCHS_CLMN_DAY /*결제일 - 10*/ , NVL2(A.SM10_CLMN_RMK, '', D1.PCHS_CLMN_RMK) AS PCHS_CLMN_RMK /*결제일 - 다음달 10일*/ , NVL(A.SM10_PMNT_MTD_CD, D1.PCHS_PMNT_MTD_CD) AS PCHS_PMNT_MTD_CD --결제조건 코드 , FN_CM05M01_CD_TO_NM(NVL(A.SM10_PMNT_MTD_CD, D1.PCHS_PMNT_MTD_CD)) AS PCHS_PMNT_MTD_NM /*결제조건명*/ FROM ( SELECT A.* , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN 'N' WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN 'R' ELSE 'Y' END IS_COST , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN '' WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN '' ELSE C.ORDRS_NO END COST_ORDRS_NO --수주번호 , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN M.SALES_CD WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN RM.SALES_CD ELSE C.SALES_CD END SALES_CD , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN M.PCHS_CLNT_CD WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN RM.PCHS_CLNT_CD ELSE C.PCHS_CLNT_CD END PCHS_CLNT_CD , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN TO_CHAR(I.INOUT_KEY) WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN RM.FILE_TRGT_KEY ELSE C.FILE_TRGT_KEY END FILE_TRGT_KEY , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN '발주' WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN '반품' ELSE '비용' END IS_DIV , (SELECT FN_CM06M01_ID_TO_NM(A.CREAT_ID) FROM DUAL) AS CREAT_ID_NM , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN I.IN_DT WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN RM.RET_DT ELSE C.COST_DT END IN_DT , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN I.IN_NO WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN R.RET_NO ELSE C.COST_NO END IPGO_IN_NO , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN I.IN_SEQ WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN R.RET_SEQ ELSE '' END IN_SEQ , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN I.MATR_CD WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN R.MATR_CD ELSE C.MATR_CD END MATR_CD , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN I.IN_PRC WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN R.RET_PRC ELSE C.COST_AMT END IN_PRC , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN I.IN_QTY WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN R.RET_QTY ELSE 1 END IN_QTY , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN I.BAD_QTY WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN '' ELSE '' END BAD_QTY , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN I.IN_DTL_AMT WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN R.RET_DTL_AMT ELSE C.COST_AMT END IN_DTL_AMT , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN I.ORDRG_SEQ WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN '' ELSE '' END IPGO_ORDRG_SEQ , CASE WHEN substr(A.ORDRG_NO,0,3) = 'BAL' THEN '' WHEN substr(A.ORDRG_NO,0,3) = 'RET' THEN R.RET_DTL_RMK ELSE C.COST_RMK END COST_RMK , DECODE (substr(A.ORDRG_NO,0,3), 'COS', C.PCHS_CLMN_DAY, '') AS SM10_CLMN_DAY , DECODE (substr(A.ORDRG_NO,0,3), 'COS', C.PCHS_CLMN_DIV_CD, '') AS SM10_CLMN_DIV_CD , DECODE (substr(A.ORDRG_NO,0,3), 'COS', C.PCHS_PMNT_MTD_CD, '') AS SM10_PMNT_MTD_CD , DECODE (substr(A.ORDRG_NO,0,3), 'COS', 'COST', '') AS SM10_CLMN_RMK FROM TB_SM12D01 A /* 매입 확정 DETAIL */ LEFT OUTER JOIN TB_SM02M01 M ON(A.ORDRG_NO = M.ORDRG_NO) /* 발주마스터 */ -- LEFT OUTER JOIN TB_SM03D01 I ON(A.PCHS_NO = I.PCHS_NO AND A.PCHS_SEQ = I.PCHS_SEQ) /* 입고 상세 */ LEFT OUTER JOIN TB_SM03D01 I ON(M.ORDRG_NO = I.ORDRG_NO) /* 입고 상세 */ LEFT OUTER JOIN TB_SM10M01 C ON(A.ORDRG_NO = C.PCHS_NO) /* 비용마스터 */ LEFT OUTER JOIN TB_SM06D01 R ON(A.ORDRG_NO = R.PCHS_NO) /* 반품 상세 */ LEFT OUTER JOIN TB_SM06M01 RM ON(RM.CO_CD = R.CO_CD AND RM.RET_NO = R.RET_NO) /* 반품 상세 */ WHERE 1=1 AND NVL(C.PAY_DIV,'PAYDIV02') = 'PAYDIV02' --기타비용의 경우 청구(PAYDIV02)인 경우만 구매현황에 출력함.
AND A.CO_CD = #{coCd}
AND A.PCHS_DT BETWEEN #{ordrgDtFrom} AND #{ordrgDtTo}
) A LEFT OUTER JOIN TB_BM02M01 D1 ON A.PCHS_CLNT_CD = D1.CLNT_CD --거래처마스터 WHERE 1 = 1
AND NVL(A.SM10_CLMN_DAY, D1.PCHS_CLMN_DAY) = #{pchsClmnDay}
AND A.SALES_CD LIKE '%' || #{salesCd} || '%'
AND D1.CLNT_NM LIKE '%' || #{pchsClntNm} || '%'
AND A.IS_DIV = #{pchsGb}
) , IPGOAMT AS --입고집계 ( SELECT CO_CD, ORDRG_NO , SUM(IN_QTY) AS IN_QTY , SUM(BAD_QTY) AS BAD_QTY , SUM(IN_DTL_AMT) AS IN_DTL_AMT_TOT FROM TB_SM03D01 A /* 입고 DETAIL */ WHERE 1=1 AND EXISTS (SELECT 1 FROM IPGO WHERE IPGO.ORDRG_NO = A.ORDRG_NO) GROUP BY CO_CD, ORDRG_NO ) , IPGOTOT AS --매입확정집계 ( SELECT CO_CD, ORDRG_NO , SUM(PCHS_AMT) AS PCHS_AMT_TOT , SUM(PCHS_VAT) AS PCHS_VAT_TOT , SUM(PCHS_QTY) AS PCHS_QTY_TO --수량은 입고 건별 1로 불일치 발생 FROM TB_SM12D01 A /* 매입확정 DETAIL */ WHERE 1=1 AND EXISTS (SELECT 1 FROM IPGO WHERE IPGO.ORDRG_NO = A.ORDRG_NO) GROUP BY CO_CD, ORDRG_NO ) , BAL AS --발주집계 ( SELECT A.CO_CD , A.ORDRG_NO , A.SALES_CD , SUM(A.ORDRG_QTY) ORDRG_QTY , SUM(A.ORDRG_AMT) ORDRG_AMT , MIN(A.DSGN_NO) AS DSGN_NO FROM TB_SM02D01 A WHERE 1=1 AND EXISTS (SELECT 1 FROM IPGO WHERE IPGO.ORDRG_NO = A.ORDRG_NO) GROUP BY A.CO_CD, A.ORDRG_NO, A.SALES_CD ) , QM AS ( SELECT DISTINCT M.CO_CD , M.ORDRG_NO , M.REQ_NO --발행번호 REQ2300099 , M.SALES_CD , Q.ORDRG_DT --발행일자 , Q.PART_CD --분류( 출도 추가 장애 A/S유상 A/S무상 SPARE유상 SPARE무상 고객E/O 설치시운전 기타) , Q.ORDRG_ID --발행자 FROM TB_SM02M01 M LEFT OUTER JOIN TB_QM01M01 Q ON M.REQ_NO = Q.REQ_NO WHERE 1=1 AND EXISTS (SELECT 1 FROM IPGO WHERE IPGO.ORDRG_NO = M.ORDRG_NO) ) SELECT ROWNUM AS RN , T.* FROM ( SELECT A.CO_CD , A.ORDRG_NO , A.SALES_CD , A.PCHS_CLNT_CD , A.PCHS_CLNT_NM , A.PCHS_DT AS ORDRG_DT , D.DSGN_NO AS DSGN_NO /* 도번 MIN */ , E.PCHS_AMT_TOT , E.PCHS_VAT_TOT , D.ORDRG_QTY AS ORDRG_QTY /*발주수량 */ , D.ORDRG_AMT AS ORDRG_AMT /*발주 금액 */ , F.IN_DTL_AMT_TOT - E.PCHS_AMT_TOT AS REMAIN_AMT , A.PCHS_NO AS PCHS_NO /*매입확정번호 */ , A.PCHS_AMT AS PCHS_AMT /* 매입금액 */ , A.PCHS_VAT AS PCHS_VAT /* 부가세 */ , NVL(A.PCHS_AMT, 0) + NVL(A.PCHS_VAT, 0) AS PCHS_SUM_AMT /* 합계금액 */ , A.PCHS_QTY AS PCHS_QTY /* 수량 --> 매입확정시 무조건 1로 입력됨 의미없음*/ , TO_CHAR(A.PCHS_DT, 'YYYY-MM-DD') AS PCHS_DT , A.CMPLET_YN AS CMPLET_YN /* 확정여부 */ , DECODE(A.CMPLET_YN, 'Y', 'Y', 'N') AS USER_CHK_V /* DETAIL로 변경 */ , A.IS_COST AS IS_COST /* 기타비용여부 */ , A.BILL_YN AS BILL_YN /*계산서 여부*/ , A.FILE_TRGT_KEY , A.PCHS_SEQ , A.CREAT_ID_NM , A.CRN /*사업자등록번호*/ , A.PCHS_CLMN_DAY /*결제일 - 10*/ , A.PCHS_CLMN_RMK /*결제일 - 다음달 10일*/ , A.PCHS_PMNT_MTD_CD --결제조건 코드 , A.PCHS_PMNT_MTD_NM --결제조건 코드 , A.IS_DIV , DECODE(substr(A.ORDRG_NO,0,3),'BAL', F.IN_DTL_AMT_TOT, E.PCHS_AMT_TOT) IN_DTL_AMT_TOT --입고금액계 , Q.REQ_NO --발주요청서 번호 , Q.ORDRG_DT AS REQ_ORDRG_DT --발주요청서 발행일자 , Q.PART_CD --발주요청서 분류코드 , FN_CM05M01_CD_TO_NM(Q.PART_CD) AS PART_CD_NM , Q.ORDRG_ID AS REQ_ORDRG_ID ---발주요청서 발행자ID , FN_CM06M01_ID_TO_NM(Q.ORDRG_ID) AS ORDRG_ID_NM , A.CREAT_ID_NM --매입확정담당자면 -- , A.IN_DTL_AMT AS IPGO_DTL_AMT_TOT , TO_CHAR(A.IN_DT, 'YYYY-MM-DD') AS IN_DT , A.MATR_CD , A.IN_PRC , A.IN_QTY , A.BAD_QTY , A.IN_DTL_AMT AS IPGO_AMT , A.IPGO_IN_NO , A.IN_SEQ , A.IPGO_ORDRG_SEQ , A.COST_RMK , J.MATR_NM , J.MATR_MAT , J.MATR_SIZE , J.MATR_SPEC , J.GOODS_DIV , (SELECT FN_CM05M01_CD_TO_NM(J.GOODS_DIV) FROM DUAL ) AS GOODS_DIV_NM /* 자재품목구분명 */ FROM IPGO A /* 매입 확정 DETAIL */ LEFT OUTER JOIN BAL AS D ON A.CO_CD = D.CO_CD AND A.ORDRG_NO = D.ORDRG_NO --발주상세 LEFT OUTER JOIN IPGOTOT AS E ON A.CO_CD = E.CO_CD AND A.ORDRG_NO = E.ORDRG_NO --매입확정집계 LEFT OUTER JOIN IPGOAMT AS F ON A.CO_CD = F.CO_CD AND A.ORDRG_NO = F.ORDRG_NO --입고 집계 LEFT OUTER JOIN QM AS Q ON A.CO_CD = Q.CO_CD AND A.ORDRG_NO = Q.ORDRG_NO --발주요청 LEFT OUTER JOIN TB_BM05M01 J ON A.MATR_CD = J.MATR_CD /* 자재마스터 */ WHERE 1=1 ORDER BY A.CO_CD, A.PCHS_CLNT_NM, A.PCHS_DT, A.ORDRG_NO, A.PCHS_NO ) T WHERE 1=1 AND ROWNUM BETWEEN ${firstIndex} AND ${lastIndex}
