mapper.select:
SELECT COUNT(*) FROM ( SELECT * FROM ( SELECT A.CO_CD , NVL(D.PCHS_NO, 'PC'||TO_CHAR(SYSDATE, 'YY')||'00000') AS PCHS_NO --매입확정번호 , A.ORDRG_SEQ , A.SALES_CD , SM02.PCHS_CLNT_CD -- 구매처cd , TO_CHAR(SM02.ORDRG_DT, 'YYYY-MM-DD') ORDRG_DT --발주일자 , 'TRGTDIV03' AS TRGT_DIV
, BM2.CLNT_NM AS PCHS_CLNT_NM /*거래처명*/ , BM2.CRN /*사업자등록번호*/ , C.EQP_NM /*설비명 - 프로젝트명*/ , A.DSGN_NO /*도번*/ , D.AS_YN --AS여부
, FN_CM05M01_CD_TO_NM(D.AS_YN) AS AS_YN_NM --AS여부
, D.PCHS_WT --중량 , BM5.MATR_NM /*품목명 - 자재명*/ , BM5.MATR_MAT /*소재*/ , BM5.MATR_MAKR_NM /*MAKER*/ , BM5.MATR_SPEC /*규격*/ , NVL(A.ORDRG_QTY, 0) AS ORDRG_QTY /*수량 - 발주수량*/ , NVL(A.ORDRG_PRC, 0) AS ORDRG_PRC /*단가-발주단가*/ , NVL(A.ORDRG_AMT, 0) AS ORDRG_AMT /*공급가액 -발주금액*/ , NVL(D.PCHS_VAT,0) AS PCHS_VAT --부가세 , NVL(A.ORDRG_AMT + D.PCHS_VAT, 0) AS PCHS_TOTAL --합계(VAT 포함) , TO_CHAR(D.PCHS_DT, 'YYYY-MM-DD') AS PCHS_DT /*매입일자*/ , D.PCHS_AMT --매입금액 , BM2.PCHS_CLMN_DAY /*결제일 - 10*/ , BM2.PCHS_CLMN_RMK /*결제일 - 다음달 10일*/ , BM2.PCHS_PMNT_MTD_CD --결제조건 코드 , FN_CM05M01_CD_TO_NM(BM2.PCHS_PMNT_MTD_CD) AS PCHS_PMNT_MTD_NM /*결제조건명*/ , TO_CHAR(D.PCHS_DT, 'YYYY-MM') AS CLOSE_YM FROM TB_SM02D01 AS A /* 매입 DETAIL */ INNER JOIN TB_SM02M01 AS SM02 ON(A.CO_CD=SM02.CO_CD AND A.ORDRG_NO=SM02.ORDRG_NO AND A.SALES_CD=SM02.SALES_CD) /*발주*/ LEFT OUTER JOIN TB_SM12D01 AS D ON(D.ORDRG_NO=A.ORDRG_NO AND D.ORDRG_SEQ=A.ORDRG_SEQ) /* 매입 DETAIL */ LEFT OUTER JOIN TB_SM12M01 AS M ON(D.CO_CD=M.CO_CD AND D.PCHS_NO=M.PCHS_NO) /* 매입 MASTER */ LEFT OUTER JOIN TB_BM02M01 AS BM2 ON BM2.CLNT_CD = SM02.PCHS_CLNT_CD /*거래처 마스터*/ LEFT OUTER JOIN TB_BM05M01 AS BM5 ON(BM5.CO_CD = A.CO_CD AND BM5.MATR_CD = A.MATR_CD) --자재마스터 LEFT OUTER JOIN TB_CR02D02 AS C ON(C.CO_CD = A.CO_CD AND C.SALES_CD = A.SALES_CD) /*수주상세*/ WHERE 1=1
) Z WHERE 1=1
AND Z.CO_CD = #{coCd}
AND Z.PCHS_DT BETWEEN TO_DATE( #{closeYmFrom},'YYYYMMDD') AND TO_DATE( #{closeYmTo},'YYYYMMDD') --발주일자
AND Z.PCHS_CLMN_DAY = #{pchsClmnDay}
ORDER BY Z.SALES_CD, Z.CO_CD, Z.PCHS_DT ) T

SELECT * FROM ( SELECT X.* , ROWNUM AS RN FROM ( SELECT * FROM ( SELECT A.CO_CD , NVL(D.PCHS_NO, 'PC'||TO_CHAR(SYSDATE, 'YY')||'00000') AS PCHS_NO --매입확정번호 , A.ORDRG_SEQ , A.SALES_CD , SM02.PCHS_CLNT_CD -- 구매처cd , TO_CHAR(SM02.ORDRG_DT, 'YYYY-MM-DD') ORDRG_DT --발주일자 , 'TRGTDIV03' AS TRGT_DIV
, BM2.CLNT_NM AS PCHS_CLNT_NM /*거래처명*/ , BM2.CRN /*사업자등록번호*/ , C.EQP_NM /*설비명 - 프로젝트명*/ , A.DSGN_NO /*도번*/ , D.AS_YN --AS여부
, FN_CM05M01_CD_TO_NM(D.AS_YN) AS AS_YN_NM --AS여부
, D.PCHS_WT --중량 , BM5.MATR_NM /*품목명 - 자재명*/ , BM5.MATR_MAT /*소재*/ , BM5.MATR_MAKR_NM /*MAKER*/ , BM5.MATR_SPEC /*규격*/ , NVL(A.ORDRG_QTY, 0) AS ORDRG_QTY /*수량 - 발주수량*/ , NVL(A.ORDRG_PRC, 0) AS ORDRG_PRC /*단가-발주단가*/ , NVL(A.ORDRG_AMT, 0) AS ORDRG_AMT /*공급가액 -발주금액*/ , NVL(D.PCHS_VAT,0) AS PCHS_VAT --부가세 , NVL(A.ORDRG_AMT + D.PCHS_VAT, 0) AS PCHS_TOTAL --합계(VAT 포함) , TO_CHAR(D.PCHS_DT, 'YYYY-MM-DD') AS PCHS_DT /*매입일자*/ , D.PCHS_AMT --매입금액 , BM2.PCHS_CLMN_DAY /*결제일 - 10*/ , BM2.PCHS_CLMN_RMK /*결제일 - 다음달 10일*/ , BM2.PCHS_PMNT_MTD_CD --결제조건 코드 , FN_CM05M01_CD_TO_NM(BM2.PCHS_PMNT_MTD_CD) AS PCHS_PMNT_MTD_NM /*결제조건명*/ , TO_CHAR(D.PCHS_DT, 'YYYY-MM') AS CLOSE_YM FROM TB_SM02D01 AS A /* 매입 DETAIL */ INNER JOIN TB_SM02M01 AS SM02 ON(A.CO_CD=SM02.CO_CD AND A.ORDRG_NO=SM02.ORDRG_NO AND A.SALES_CD=SM02.SALES_CD) /*발주*/ LEFT OUTER JOIN TB_SM12D01 AS D ON(D.ORDRG_NO=A.ORDRG_NO AND D.ORDRG_SEQ=A.ORDRG_SEQ) /* 매입 DETAIL */ LEFT OUTER JOIN TB_SM12M01 AS M ON(D.CO_CD=M.CO_CD AND D.PCHS_NO=M.PCHS_NO) /* 매입 MASTER */ LEFT OUTER JOIN TB_BM02M01 AS BM2 ON BM2.CLNT_CD = SM02.PCHS_CLNT_CD /*거래처 마스터*/ LEFT OUTER JOIN TB_BM05M01 AS BM5 ON(BM5.CO_CD = A.CO_CD AND BM5.MATR_CD = A.MATR_CD) --자재마스터 LEFT OUTER JOIN TB_CR02D02 AS C ON(C.CO_CD = A.CO_CD AND C.SALES_CD = A.SALES_CD) /*수주상세*/ WHERE 1=1
) Z WHERE 1=1
AND Z.CO_CD = #{coCd}
AND Z.PCHS_DT BETWEEN TO_DATE( #{closeYmFrom},'YYYYMMDD') AND TO_DATE( #{closeYmTo},'YYYYMMDD') --발주일자
AND Z.PCHS_CLMN_DAY = #{pchsClmnDay}
ORDER BY Z.SALES_CD, Z.CO_CD, Z.PCHS_DT ) X ) WHERE 1=1 AND RN BETWEEN ${firstIndex} AND ${lastIndex}
