mapper.select:
SELECT DISTINCT '' PID, #{coCd} ID, #{coCd} CO_CD, -- 회사코드 FN_CM05M01_CD_TO_NM(#{coCd}) AS CO_CD_NM, '전체' ORDRS_NO, '전사 현황' AS SALES_CD, '' ORGN_SALES_CD, '' ORDRS_CLNT_CD, -- 고객사 '전체' AS ORDRS_CLNT_NM, '' CLNT_PJT, '' ORDRS_DTL_DIV20, -- 신작구분 '' AS ORDRS_DTL_DIV20_NM, '' EQP_NM, -- 설비명 0 PLAN_CNT, 'E' WBS_PLAN_OS_YN, 'E' PLAN_LOCK_YN, 'E' PLAN_CLOSE_YN, 0 RSULT_CNT, 'E' RSULT_LOCK_YN, 'E' RSULT_CLOSE_YN, '' PRDT_CD, '' AS PRDT_CD_NM, '' ITEM_DIV, '' ITEM_DIV_NM, '' DATA_GBN FROM DUAL
UNION ALL --회사명을 Top LEVEL 생성 SELECT DISTINCT X.CO_CD PID, TO_CHAR(Y.ORDRS_CLNT_CD) ID, X.CO_CD, -- 회사코드 FN_CM05M01_CD_TO_NM(X.CO_CD) AS CO_CD_NM, A.CLNT_NM, A.CLNT_NM AS SALES_CD, '' ORGN_SALES_CD, Y.ORDRS_CLNT_CD , -- 고객사 A.CLNT_NM AS ORDRS_CLNT_NM, '' CLNT_PJT, '' ORDRS_DTL_DIV20, -- 신작구분 '' AS ORDRS_DTL_DIV20_NM, '', -- 설비명 0 PLAN_CNT, 'E' WBS_PLAN_OS_YN, 'E' PLAN_LOCK_YN, 'E' PLAN_CLOSE_YN, 0 RSULT_CNT, 'E' RSULT_LOCK_YN, 'E' RSULT_CLOSE_YN, '' PRDT_CD, '' AS PRDT_CD_NM, '' ITEM_DIV, '' ITEM_DIV_NM, '' DATA_GBN FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 A ON Y.ORDRS_CLNT_CD = A.CLNT_CD --거래처마스터 WHERE X.CO_CD = #{coCd}
AND X.SALES_CD like '%${salesCd}%'
UNION ALL --오더번호로 1 LEVEL 생성 SELECT DISTINCT TO_CHAR(Y.ORDRS_CLNT_CD) PID, X.ORDRS_NO ID, X.CO_CD, -- 회사코드 FN_CM05M01_CD_TO_NM(X.CO_CD) AS CO_CD_NM, X.ORDRS_NO AS ORDRS_NO, X.ORDRS_NO AS SALES_CD, '', Y.ORDRS_CLNT_CD, -- 고객사 A.CLNT_NM AS ORDRS_CLNT_NM, '', '', '', '', -- 설비명 0 PLAN_CNT, 'E' WBS_PLAN_OS_YN, 'E' PLAN_LOCK_YN, 'E' PLAN_CLOSE_YN, 0 RSULT_CNT, 'E' RSULT_LOCK_YN, 'E' RSULT_CLOSE_YN, '' PRDT_CD, '' AS PRDT_CD_NM, '' ITEM_DIV, '' ITEM_DIV_NM, '' DATA_GBN FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 A ON Y.ORDRS_CLNT_CD = A.CLNT_CD --거래처마스터 WHERE X.CO_CD = #{coCd}
AND X.SALES_CD like '%${salesCd}%'
UNION ALL SELECT --Sales 코드 추출 X.ORDRS_NO PID, X.SALES_CD ID, X.CO_CD, -- 회사코드 FN_CM05M01_CD_TO_NM(X.CO_CD) AS CO_CD_NM, X.ORDRS_NO, -- 수주번호 X.SALES_CD, -- SALES CODE max(Y.ORGN_SALES_CD), -- 원천 SALES CODE max(Y.ORDRS_CLNT_CD), -- 고객사 max(A.CLNT_NM) AS ORDRS_CLNT_NM, max(Y.CLNT_PJT), -- 고객사 PJT max(X.ORDRS_DTL_DIV20), -- 신작구분 FN_CM05M01_CD_TO_NM(max(X.ORDRS_DTL_DIV20)) AS ORDRS_DTL_DIV20_NM, max(X.EQP_NM), -- 설비명 COUNT(W.FILE_TRGT_KEY) PLAN_CNT, NVL(max(W.WBS_PLAN_OS_YN),'N') WBS_PLAN_OS_YN, NVL(max(W.LOCK_YN),'N') PLAN_LOCK_YN, NVL(max(W.CLOSE_YN),'N') PLAN_CLOSE_YN, COUNT(Z.FILE_TRGT_KEY) RSULT_CNT, NVL(max(Z.LOCK_YN),'N') RSULT_LOCK_YN, NVL(max(Z.CLOSE_YN),'N') RSULT_CLOSE_YN, max(X.PRDT_CD) PRDT_CD, FN_BM01M01_ID_TO_NM(max(X.PRDT_CD)) AS PRDT_CD_NM, max(X.ITEM_DIV) ITEM_DIV, FN_CM05M01_CD_TO_NM(max(X.ITEM_DIV)) AS ITEM_DIV_NM, 'D' DATA_GBN --원시데이타 FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO AND X.CO_CD = #{coCd}
AND X.SALES_CD like '%${salesCd}%'
CROSS JOIN ( SELECT CASE WHEN CODE_KIND = 'ROOT' THEN '' ELSE CODE_KIND END CODE_KIND, CODE_ID, CODE_RPRC, CODE_NM, CASE WHEN LEVEL = '1' THEN CODE_NM ELSE '' END LVL1, CASE WHEN LEVEL = '2' THEN CODE_NM ELSE '' END LVL2, CASE WHEN LEVEL = '3' THEN CODE_NM ELSE '' END LVL3, LPAD('', 3*(LEVEL-1)) || LEVEL LVL, REGEXP_REPLACE(SYS_CONNECT_BY_PATH(CODE_ID, ' -> '), '^\s+\-\>\s+', '') as PATH, CONNECT_BY_ISLEAF AS IS_LEAF, CASE WHEN CONNECT_BY_ISLEAF = 0 THEN 'unit' ELSE 'leaf' END TYPE FROM TB_CM05M01 START WITH CODE_ID = 'WBSDIV' CONNECT BY PRIOR CODE_ID = CODE_KIND ) CD LEFT OUTER JOIN TB_WB01M01 W ON X.CO_CD = W.CO_CD AND X.SALES_CD = w.SALES_CD AND CD.CODE_ID = W.WBS_PLAN_CODE_ID LEFT OUTER JOIN TB_WB02M01 Z ON X.CO_CD = Z.CO_CD AND X.SALES_CD = Z.SALES_CD AND CD.CODE_ID = Z.WBS_PLAN_CODE_ID LEFT OUTER JOIN TB_BM02M01 A ON Y.ORDRS_CLNT_CD = A.CLNT_CD --거래처마스터 GROUP BY X.ORDRS_NO, X.SALES_CD, X.CO_CD,Y.ORGN_SALES_CD,Y.ORDRS_CLNT_CD, Y.CLNT_PJT

mapper.update:
UPDATE TB_WB01M01
 SET WBS_PLANS_DT = #{wbsPlansDt},
     WBS_PLANE_DT = #{wbsPlaneDt}
 WHERE CO_CD = #{coCd}
       AND SALES_CD = #{salesCd}
       AND WBS_PLAN_CODE_ID = #{codeId}

mapper.select:
SELECT W.CO_CD, W.PID, W.ID, W.CLNT_NM, W.LVL, W.PATH, TO_DATE(TO_CHAR(W.WBS_PLANE_DT,'YYYYMMDD'),'YYYYMMDD') - TO_DATE(TO_CHAR(W.WBS_PLANS_DT,'YYYYMMDD'),'YYYYMMDD') AS DUR, NVL(W.SALES_CD,' ') AS SALES_CD, W.ORDRS_CLNT_CD, W.WBS_RSLTS_RATE, W.GANTT_TIT, W.WBS_PLANS_DT, W.WBS_PLANE_DT, W.CODE_KIND, W.CODE_ID, NVL(P.FILE_TRGT_KEY,'0') AS FILE_TRGT_KEY_P, NVL(R.FILE_TRGT_KEY,'0') AS FILE_TRGT_KEY_R, NVL(I.FILE_TRGT_KEY,'0') AS FILE_TRGT_KEY_I FROM ( SELECT W.CO_CD, W.PID, W.ID, W.CLNT_NM, W.LVL, W.PATH, W.DUR, NVL((SELECT X.WBS_RSLTS_RATE FROM TB_WB02M01 X WHERE X.CO_CD = #{coCd} AND X.WBS_PLAN_NO = W.WBS_PLAN_NO AND X.SALES_CD = W.SALES_CD),0) AS WBS_RSLTS_RATE, W.CODE_KIND, W.CODE_ID, W.CODE_NM, W.SALES_CD, W.WBS_PLAN_NO, W.CTRT_NM, W.WBS_PLAN_CNTS, W.SALES_CD_GR, W.ORDRS_CLNT_CD, CASE WHEN W.LVL = 1 THEN W.ID WHEN W.LVL = 2 THEN W.CLNT_NM WHEN W.LVL = 3 THEN W.CTRT_NM WHEN W.LVL = 4 AND NVL(W.WBS_PLAN_NO,' ') != ' ' THEN '계획번호 : '||W.WBS_PLAN_NO WHEN W.LVL = 4 AND NVL(W.WBS_PLAN_NO,' ') = ' ' THEN ' ' WHEN W.LVL = 5 OR W.LVL = 6 OR W.LVL = 7 THEN WBS_PLAN_CNTS END GANTT_TIT, CASE WHEN W.LVL = 1 THEN W1.WBS_PLANS_DT_ALL WHEN W.LVL = 2 THEN W2.WBS_PLANS_DT_ORDRS_CLNT_CD WHEN W.LVL = 3 THEN W3.WBS_PLANS_DT_ORDRS_NO WHEN W.LVL = 4 THEN W4.WBS_PLANS_DT_SALES_CD ELSE W.WBS_PLANS_DT END AS WBS_PLANS_DT, CASE WHEN W.LVL = 1 THEN W1.WBS_PLANE_DT_ALL WHEN W.LVL = 2 THEN W2.WBS_PLANE_DT_ORDRS_CLNT_CD WHEN W.LVL = 3 THEN W3.WBS_PLANE_DT_ORDRS_NO WHEN W.LVL = 4 THEN W4.WBS_PLANE_DT_SALES_CD ELSE W.WBS_PLANe_DT END AS WBS_PLANE_DT FROM ( SELECT CO_CD, PID, ID, CLNT_NM, LVL, PATH, WBS_PLANS_DT, WBS_PLANE_DT, DUR, WBS_RSLTS_RATE, CODE_KIND, CODE_ID, CODE_NM, SALES_CD, WBS_PLAN_NO, (SELECT X.CTRT_NM FROM TB_CR02M01 X WHERE X.CO_CD = #{coCd} AND X.ORDRS_NO = W.ID) AS CTRT_NM, (SELECT X.WBS_PLAN_CNTS FROM TB_WB01M01 X WHERE X.CO_CD = #{coCd} AND X.SALES_CD = W.SALES_CD AND X.WBS_PLAN_CODE_KIND = W.CODE_KIND AND X.WBS_PLAN_CODE_ID = W.CODE_ID) AS WBS_PLAN_CNTS, SALES_CD_GR, ORDRS_CLNT_CD FROM ( SELECT CO_CD, PID, ID, CLNT_NM, LEVEL AS LVL, LPAD(' ', 3*LEVEL-1) || SYS_CONNECT_BY_PATH(CLNT_NM, '>') PATH, WBS_PLANS_DT, WBS_PLANE_DT, TO_DATE(TO_CHAR(WBS_PLANE_DT,'YYYYMMDD'),'YYYYMMDD') - TO_DATE(TO_CHAR(WBS_PLANS_DT,'YYYYMMDD'),'YYYYMMDD') AS DUR, 0 AS WBS_RSLTS_RATE, CODE_KIND, CODE_ID, CODE_NM, SALES_CD, WBS_PLAN_NO, SALES_CD_GR, ORDRS_CLNT_CD FROM ( -- 1 : 회사 그룹 SELECT #{coCd} AS CO_CD, '0' AS PID, '건양ITT' AS ID, '전사 현황' AS CLNT_NM, '' AS WBS_PLANS_DT, '' AS WBS_PLANE_DT, '' AS CODE_KIND, '' AS CODE_ID, '' AS CODE_NM, '' AS SALES_CD, '' AS WBS_PLAN_NO, '' AS SALES_CD_GR, '' AS ORDRS_CLNT_CD FROM DUAL
UNION ALL
-- 2 : 고객사 그룹 SELECT DISTINCT X.CO_CD AS CO_CD, '건양ITT' AS PID, TO_CHAR(ORDRS_CLNT_CD) AS ID, (SELECT P.CLNT_NM FROM TB_BM02M01 P WHERE P.CLNT_CD = X.ORDRS_CLNT_CD) AS CLNT_NM, '' AS WBS_PLANS_DT, '' AS WBS_PLANE_DT, '' AS CODE_KIND, '' AS CODE_ID, '' AS CODE_NM, '' AS SALES_CD, '' AS WBS_PLAN_NO, '' AS SALES_CD_GR, X.ORDRS_CLNT_CD FROM TB_CR02M01 X
WHERE X.CO_CD = #{coCd} AND X.ORDRS_CLNT_CD > 1
AND X.ORDRS_CLNT_CD = #{clntCd}
UNION ALL
-- 3 : 수주번호 그룹 SELECT DISTINCT Y.CO_CD AS CO_CD, TO_CHAR(Y.ORDRS_CLNT_CD) AS PID, SUBSTR(X.SALES_CD,0,5) AS ID, SUBSTR(X.SALES_CD,0,5) AS CLNT_NM, '' AS WBS_PLANS_DT, '' AS WBS_PLANE_DT, '' AS CODE_KIND, '' AS CODE_ID, '' AS CODE_NM, '' AS SALES_CD, '' AS WBS_PLAN_NO, SUBSTR(X.SALES_CD,0,5) AS SALES_CD_GR, Y.ORDRS_CLNT_CD AS ORDRS_CLNT_CD FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO
WHERE Y.CO_CD = #{coCd}
AND Y.ORDRS_CLNT_CD = #{clntCd}
AND X.SALES_CD LIKE '%${salesCd}%'
UNION ALL
-- 4 : SALES_CD 그룹 SELECT DISTINCT Y.CO_CD AS CO_CD, SUBSTR(X.SALES_CD,0,5) AS PID, X.SALES_CD AS ID, X.SALES_CD AS CLNT_NM, '' AS WBS_PLANS_DT, '' AS WBS_PLANE_DT, '' AS CODE_KIND, '' AS CODE_ID, '' AS CODE_NM, '' AS SALES_CD, (SELECT T.WBS_PLAN_NO FROM TB_WB01M01 T WHERE T.CO_CD = #{coCd} AND T.SALES_CD = X.SALES_CD AND ROWNUM = 1) AS WBS_PLAN_NO, SUBSTR(X.SALES_CD,0,5) AS SALES_CD_GR, Y.ORDRS_CLNT_CD AS ORDRS_CLNT_CD FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO
WHERE Y.CO_CD = #{coCd}
AND Y.ORDRS_CLNT_CD = #{clntCd}
AND X.SALES_CD LIKE '%${salesCd}%'
UNION ALL
-- 5 : WBS계획 1레벨 SELECT X.CO_CD AS CO_CD, SALES_CD AS PID, SALES_CD||WBS_PLAN_CODE_ID AS ID, CODE_NM AS CLNT_NM, WBS_PLANS_DT, WBS_PLANE_DT, CODE_KIND, CODE_ID, CODE_NM, SALES_CD, WBS_PLAN_NO, SUBSTR(SALES_CD,0,5) AS SALES_CD_GR, '' AS ORDRS_CLNT_CD FROM TB_WB01M01 X INNER JOIN ( SELECT CODE_KIND, CODE_ID, CODE_RPRC, CODE_NM, LEVEL AS LVL, LPAD(' ', 3*LEVEL-1) || SYS_CONNECT_BY_PATH(CODE_NM, '>') PATH FROM TB_CM05M01 START WITH CODE_KIND = 'WBSDIV' CONNECT BY PRIOR CODE_ID = CODE_KIND ORDER BY CODE_ID ASC ) Y ON X.WBS_PLAN_CODE_KIND = Y.CODE_KIND AND X.WBS_PLAN_CODE_ID = Y.CODE_ID
WHERE LVL = 1 AND X.CO_CD = #{coCd}
UNION ALL
-- 6 : WBS계획 2레벨 SELECT X.CO_CD AS CO_CD, X.SALES_CD||WBS_PLAN_CODE_KIND AS PID, X.SALES_CD||WBS_PLAN_CODE_ID AS ID, CODE_NM AS CLNT_NM, WBS_PLANS_DT, WBS_PLANE_DT, CODE_KIND, CODE_ID, CODE_NM, SALES_CD, WBS_PLAN_NO, SUBSTR(SALES_CD,0,5) AS SALES_CD_GR, '' AS ORDRS_CLNT_CD FROM TB_WB01M01 X INNER JOIN ( SELECT CODE_KIND, CODE_ID, CODE_RPRC, CODE_NM, LEVEL AS LVL, LPAD(' ', 3*LEVEL-1) || SYS_CONNECT_BY_PATH(CODE_NM, '>') PATH FROM TB_CM05M01 START WITH CODE_KIND = 'WBSDIV' CONNECT BY PRIOR CODE_ID = CODE_KIND ORDER BY CODE_ID ASC ) Y ON X.WBS_PLAN_CODE_KIND = Y.CODE_KIND AND X.WBS_PLAN_CODE_ID = Y.CODE_ID
WHERE LVL = 2 AND X.CO_CD = #{coCd}
UNION ALL
-- 7 : WBS계획 3레벨 SELECT X.CO_CD AS CO_CD, X.SALES_CD||WBS_PLAN_CODE_KIND AS PID, X.SALES_CD||WBS_PLAN_CODE_ID AS ID, CODE_NM AS CLNT_NM, WBS_PLANS_DT, WBS_PLANE_DT, CODE_KIND, CODE_ID, CODE_NM, SALES_CD, WBS_PLAN_NO, SUBSTR(SALES_CD,0,5) AS SALES_CD_GR, '' AS ORDRS_CLNT_CD FROM TB_WB01M01 X INNER JOIN ( SELECT CODE_KIND, CODE_ID, CODE_RPRC, CODE_NM, LEVEL AS LVL, LPAD(' ', 3*LEVEL-1) || SYS_CONNECT_BY_PATH(CODE_NM, '>') PATH FROM TB_CM05M01 START WITH CODE_KIND = 'WBSDIV' CONNECT BY PRIOR CODE_ID = CODE_KIND ORDER BY CODE_ID ASC ) Y ON X.WBS_PLAN_CODE_KIND = Y.CODE_KIND AND X.WBS_PLAN_CODE_ID = Y.CODE_ID
WHERE LVL = 3 AND X.CO_CD = #{coCd}
) W START WITH PID = '0' CONNECT BY PRIOR ID = PID ) W ) W
LEFT OUTER JOIN ( SELECT MAX(X.CO_CD) AS CO_CD, MIN(Y.WBS_PLANS_DT) AS WBS_PLANS_DT_ALL, MAX(Y.WBS_PLANE_DT) AS WBS_PLANE_DT_ALL FROM ( SELECT X.CO_CD, X.SALES_CD FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO ) X LEFT OUTER JOIN TB_WB01M01 Y ON X.CO_CD = Y.CO_CD AND X.SALES_CD = Y.SALES_CD GROUP BY X.CO_CD ) W1 ON W.CO_CD = W1.CO_CD
LEFT OUTER JOIN ( SELECT CO_CD, ORDRS_CLNT_CD, WBS_PLANS_DT_ORDRS_CLNT_CD, WBS_PLANE_DT_ORDRS_CLNT_CD FROM ( SELECT MAX(X.CO_CD) AS CO_CD, MAX(X.ORDRS_CLNT_CD) AS ORDRS_CLNT_CD, MIN(Y.WBS_PLANS_DT) AS WBS_PLANS_DT_ORDRS_CLNT_CD, MAX(Y.WBS_PLANE_DT) AS WBS_PLANE_DT_ORDRS_CLNT_CD FROM ( SELECT Y.CO_CD, Y.ORDRS_NO, Y.ORDRS_CLNT_CD, X.SALES_CD FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO ) X LEFT OUTER JOIN TB_WB01M01 Y ON X.CO_CD = Y.CO_CD AND X.SALES_CD = Y.SALES_CD GROUP BY X.CO_CD, X.ORDRS_CLNT_CD ) W2 WHERE WBS_PLANS_DT_ORDRS_CLNT_CD IS NOT NULL ) W2 ON W.CO_CD = W2.CO_CD AND W.ORDRS_CLNT_CD = W2.ORDRS_CLNT_CD
LEFT OUTER JOIN ( SELECT CO_CD, ORDRS_NO, WBS_PLANS_DT_ORDRS_NO, WBS_PLANE_DT_ORDRS_NO FROM ( SELECT MAX(X.CO_CD) AS CO_CD, MAX(X.ORDRS_NO) AS ORDRS_NO, MIN(Y.WBS_PLANS_DT) AS WBS_PLANS_DT_ORDRS_NO, MAX(Y.WBS_PLANE_DT) AS WBS_PLANE_DT_ORDRS_NO FROM ( SELECT X.CO_CD, Y.ORDRS_NO, X.SALES_CD FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO ) X LEFT OUTER JOIN TB_WB01M01 Y ON X.CO_CD = Y.CO_CD AND X.SALES_CD = Y.SALES_CD GROUP BY X.CO_CD, X.ORDRS_NO ) W3 WHERE WBS_PLANS_DT_ORDRS_NO IS NOT NULL )W3 ON W.CO_CD = W3.CO_CD AND W.SALES_CD_GR = W3.ORDRS_NO
LEFT OUTER JOIN ( SELECT CO_CD, SALES_CD, WBS_PLANS_DT_SALES_CD, WBS_PLANE_DT_SALES_CD FROM ( SELECT MAX(X.CO_CD) AS CO_CD, MAX(X.SALES_CD) AS SALES_CD, MIN(Y.WBS_PLANS_DT) AS WBS_PLANS_DT_SALES_CD, MAX(Y.WBS_PLANE_DT) AS WBS_PLANE_DT_SALES_CD FROM ( SELECT X.CO_CD, Y.ORDRS_NO, X.SALES_CD FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO ) X LEFT OUTER JOIN TB_WB01M01 Y ON X.CO_CD = Y.CO_CD AND X.SALES_CD = Y.SALES_CD GROUP BY X.CO_CD, X.SALES_CD ) W4 WHERE WBS_PLANS_DT_SALES_CD IS NOT NULL )W4 ON W.CO_CD = W4.CO_CD AND W.LVL = 4 AND W.ID = W4.SALES_CD )W LEFT OUTER JOIN TB_WB01M01 P ON W.CO_CD = P.CO_CD AND W.CODE_KIND = P.WBS_PLAN_CODE_KIND AND W.CODE_ID = P.WBS_PLAN_CODE_ID AND W.SALES_CD = P.SALES_CD LEFT OUTER JOIN TB_WB02M01 R ON W.CO_CD = R.CO_CD AND W.CODE_KIND = R.WBS_PLAN_CODE_KIND AND W.CODE_ID = R.WBS_PLAN_CODE_ID AND W.SALES_CD = R.SALES_CD LEFT OUTER JOIN TB_WB03M01 I ON W.CO_CD = I.CO_CD AND W.CODE_KIND = I.WBS_PLAN_CODE_KIND AND W.CODE_ID = I.WBS_PLAN_CODE_ID AND W.SALES_CD = I.SALES_CD
WHERE TO_CHAR(W.WBS_PLANS_DT,'YYYY-MM-DD') IS NOT NULL
