mapper.select:
SELECT DISTINCT '' PID, #{coCd} ID, #{coCd} CO_CD, -- 회사코드 FN_CM05M01_CD_TO_NM(#{coCd}) AS CO_CD_NM, '전체' ORDRS_NO, '전사 현황' AS SALES_CD, '' ORGN_SALES_CD, '' ORDRS_CLNT_CD, -- 고객사 '전체' AS ORDRS_CLNT_NM, '' CLNT_PJT, '' ORDRS_DTL_DIV20, -- 신작구분 '' AS ORDRS_DTL_DIV20_NM, '' EQP_NM, -- 설비명 0 PLAN_CNT, 'E' WBS_PLAN_OS_YN, 'E' PLAN_LOCK_YN, 'E' PLAN_CLOSE_YN, 0 RSULT_CNT, 'E' RSULT_LOCK_YN, 'E' RSULT_CLOSE_YN, '' PRDT_CD, '' AS PRDT_CD_NM, '' ITEM_DIV, '' ITEM_DIV_NM, '' DATA_GBN FROM DUAL
UNION ALL --회사명을 Top LEVEL 생성 SELECT DISTINCT X.CO_CD PID, TO_CHAR(Y.ORDRS_CLNT_CD) ID, X.CO_CD, -- 회사코드 FN_CM05M01_CD_TO_NM(X.CO_CD) AS CO_CD_NM, A.CLNT_NM, A.CLNT_NM AS SALES_CD, '' ORGN_SALES_CD, Y.ORDRS_CLNT_CD , -- 고객사 A.CLNT_NM AS ORDRS_CLNT_NM, '' CLNT_PJT, '' ORDRS_DTL_DIV20, -- 신작구분 '' AS ORDRS_DTL_DIV20_NM, '', -- 설비명 0 PLAN_CNT, 'E' WBS_PLAN_OS_YN, 'E' PLAN_LOCK_YN, 'E' PLAN_CLOSE_YN, 0 RSULT_CNT, 'E' RSULT_LOCK_YN, 'E' RSULT_CLOSE_YN, '' PRDT_CD, '' AS PRDT_CD_NM, '' ITEM_DIV, '' ITEM_DIV_NM, '' DATA_GBN FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 A ON Y.ORDRS_CLNT_CD = A.CLNT_CD --거래처마스터 WHERE X.CO_CD = #{coCd}
AND X.SALES_CD like '%${salesCd}%'
UNION ALL --오더번호로 1 LEVEL 생성 SELECT DISTINCT TO_CHAR(Y.ORDRS_CLNT_CD) PID, X.ORDRS_NO ID, X.CO_CD, -- 회사코드 FN_CM05M01_CD_TO_NM(X.CO_CD) AS CO_CD_NM, X.ORDRS_NO AS ORDRS_NO, X.ORDRS_NO AS SALES_CD, '', Y.ORDRS_CLNT_CD, -- 고객사 A.CLNT_NM AS ORDRS_CLNT_NM, '', '', '', '', -- 설비명 0 PLAN_CNT, 'E' WBS_PLAN_OS_YN, 'E' PLAN_LOCK_YN, 'E' PLAN_CLOSE_YN, 0 RSULT_CNT, 'E' RSULT_LOCK_YN, 'E' RSULT_CLOSE_YN, '' PRDT_CD, '' AS PRDT_CD_NM, '' ITEM_DIV, '' ITEM_DIV_NM, '' DATA_GBN FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 A ON Y.ORDRS_CLNT_CD = A.CLNT_CD --거래처마스터 WHERE X.CO_CD = #{coCd}
AND X.SALES_CD like '%${salesCd}%'
UNION ALL SELECT --Sales 코드 추출 X.ORDRS_NO PID, X.SALES_CD ID, X.CO_CD, -- 회사코드 FN_CM05M01_CD_TO_NM(X.CO_CD) AS CO_CD_NM, X.ORDRS_NO, -- 수주번호 X.SALES_CD, -- SALES CODE max(Y.ORGN_SALES_CD), -- 원천 SALES CODE max(Y.ORDRS_CLNT_CD), -- 고객사 max(A.CLNT_NM) AS ORDRS_CLNT_NM, max(Y.CLNT_PJT), -- 고객사 PJT max(X.ORDRS_DTL_DIV20), -- 신작구분 FN_CM05M01_CD_TO_NM(max(X.ORDRS_DTL_DIV20)) AS ORDRS_DTL_DIV20_NM, max(X.EQP_NM), -- 설비명 COUNT(W.FILE_TRGT_KEY) PLAN_CNT, NVL(max(W.WBS_PLAN_OS_YN),'N') WBS_PLAN_OS_YN, NVL(max(W.LOCK_YN),'N') PLAN_LOCK_YN, NVL(max(W.CLOSE_YN),'N') PLAN_CLOSE_YN, COUNT(Z.FILE_TRGT_KEY) RSULT_CNT, NVL(max(Z.LOCK_YN),'N') RSULT_LOCK_YN, NVL(max(Z.CLOSE_YN),'N') RSULT_CLOSE_YN, max(X.PRDT_CD) PRDT_CD, FN_BM01M01_ID_TO_NM(max(X.PRDT_CD)) AS PRDT_CD_NM, max(X.ITEM_DIV) ITEM_DIV, FN_CM05M01_CD_TO_NM(max(X.ITEM_DIV)) AS ITEM_DIV_NM, 'D' DATA_GBN --원시데이타 FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO AND X.CO_CD = #{coCd}
AND X.SALES_CD like '%${salesCd}%'
CROSS JOIN ( SELECT CASE WHEN CODE_KIND = 'ROOT' THEN '' ELSE CODE_KIND END CODE_KIND, CODE_ID, CODE_RPRC, CODE_NM, CASE WHEN LEVEL = '1' THEN CODE_NM ELSE '' END LVL1, CASE WHEN LEVEL = '2' THEN CODE_NM ELSE '' END LVL2, CASE WHEN LEVEL = '3' THEN CODE_NM ELSE '' END LVL3, LPAD('', 3*(LEVEL-1)) || LEVEL LVL, REGEXP_REPLACE(SYS_CONNECT_BY_PATH(CODE_ID, ' -> '), '^\s+\-\>\s+', '') as PATH, CONNECT_BY_ISLEAF AS IS_LEAF, CASE WHEN CONNECT_BY_ISLEAF = 0 THEN 'unit' ELSE 'leaf' END TYPE FROM TB_CM05M01 START WITH CODE_ID = 'WBSDIV' CONNECT BY PRIOR CODE_ID = CODE_KIND ) CD LEFT OUTER JOIN TB_WB01M01 W ON X.CO_CD = W.CO_CD AND X.SALES_CD = w.SALES_CD AND CD.CODE_ID = W.WBS_PLAN_CODE_ID LEFT OUTER JOIN TB_WB02M01 Z ON X.CO_CD = Z.CO_CD AND X.SALES_CD = Z.SALES_CD AND CD.CODE_ID = Z.WBS_PLAN_CODE_ID LEFT OUTER JOIN TB_BM02M01 A ON Y.ORDRS_CLNT_CD = A.CLNT_CD --거래처마스터 GROUP BY X.ORDRS_NO, X.SALES_CD, X.CO_CD,Y.ORGN_SALES_CD,Y.ORDRS_CLNT_CD, Y.CLNT_PJT

mapper.update:
UPDATE TB_WB01M01
 SET WBS_PLANS_DT = #{wbsPlansDt},
     WBS_PLANE_DT = #{wbsPlaneDt}
 WHERE CO_CD = #{coCd}
       AND SALES_CD = #{salesCd}
       AND WBS_PLAN_CODE_ID = #{codeId}

mapper.select:
WITH SJD AS --수주상세 ( SELECT M.CO_CD AS CO_CD --회사코드 , (SELECT FN_CM05M01_CD_TO_NM(M.CO_CD) FROM DUAL) AS CO_NM --회사명 , M.ORDRS_NO AS ORDRS_NO --수주번호 , X.SALES_CD AS SALES_CD --SALES Code , M.ORDRS_CLNT_CD AS CLNT_CD --고객코드 , C.CLNT_NM AS CLNT_NM --고객명 , M.CLNT_PJT AS CLNT_PJT --고객사프로젝트 , (SELECT FN_CM05M01_CD_TO_NM(M.CLNT_PJT) FROM DUAL) AS CLNT_PJT_NM --고객사프로젝트명 FROM TB_CR02M01 M --수주마스터 INNER JOIN TB_CR02D02 AS X ON X.CO_CD = M.CO_CD AND X.ORDRS_NO = M.ORDRS_NO--수주상세 INNER JOIN TB_BM02M01 AS C ON M.ORDRS_CLNT_CD = C.CLNT_CD --거래처정보 WHERE 1=1 AND M.CO_CD = #{coCd} ) , SUBJ AS --확정된 과제정보 ( SELECT X.FILE_TRGT_KEY , X.CO_CD AS CO_CD --회사코드 , X.SALES_CD AS SALES_CD --SALES Code , X.SJ_NO AS SJ_NO --과제번호 , X.SJ_NM AS SJ_NM --과제명 , X.VER_NO AS VER_NO --버젼 , (SELECT FN_CM06M01_ID_TO_NM(X.SMRIZE_ID) FROM DUAL) AS SMRIZE_NM --총괄PM명 , X.BEGIN_DT AS BEGIN_DT --시작일 , X.DE_DT AS DE_DT --출고일 , X.ACPTNC_DT AS ACPTNC_DT --완료검수일 , X.REQRE_DAYCNT AS REQRE_DAYCNT --소요일수 FROM TB_WB21M01 X WHERE X.CO_CD = #{coCd} AND X.BEGIN_DT BETWEEN #{beginDt} AND #{deDt} --과제시작일 AND X.CLOSE_YN = 'Y' --조회조건 : 과제확정 여부
AND X.SALES_CD LIKE '%${salesCd}%'
) , RSLT AS --WBS 실적 이슈 ( SELECT WR.CO_CD --회사코드 , WR.SALES_CD --SALES Code , WR.WBS_PLAN_CODE_KIND --WBS계획 LVL1 , WR.WBS_PLAN_CODE_ID --WBS계획 LVL2 , WR.WBS_RSLTS_RATE --WBS실적 진척율 , DECODE(ISU.ISS_NO, NULL, 'N', 'Y') AS ISU_YN --이슈유무 , WR.FILE_TRGT_KEY AS RSLTS_FILE_TRGT_KEY , ISU.ISSUE_FILE_TRGT_KEY AS ISSUE_FILE_TRGT_KEY FROM TB_WB23M01 WR--WBS실적 정보 LEFT OUTER JOIN (SELECT CO_CD --회사코드 , SALES_CD --SALES Code , WBS_PLAN_CODE_KIND --WBS계획 LVL1 , WBS_PLAN_CODE_ID --WBS계획 LVL2 , WBS_RSLTS_NO --WBS실적 번호 , ISS_NO --WBS실적 이슈번호 , FILE_TRGT_KEY AS ISSUE_FILE_TRGT_KEY FROM TB_WB24M01 --WBS이슈 정보 WHERE CO_CD = #{coCd} ) AS ISU ON WR.CO_CD = ISU.CO_CD AND WR.SALES_CD = ISU.SALES_CD AND WR.WBS_PLAN_CODE_KIND = ISU.WBS_PLAN_CODE_KIND AND WR.WBS_PLAN_CODE_ID = ISU.WBS_PLAN_CODE_ID AND WR.WBS_RSLTS_NO = ISU.WBS_RSLTS_NO WHERE 1=1
AND WR.SALES_CD LIKE '%${salesCd}%'
-- ORDER BY 1,2,3 ) , PLAN AS --WBS 계획정보 ( SELECT A.* , LEVEL AS LVL , CASE WHEN LEVEL = 2 THEN ( SELECT DECODE( NVL(SUM(DECODE(R.ISU_YN,'N',0,'Y',1)),0), 0, 'N', 'Y') FROM RSLT AS R WHERE 1=1 AND A.CO_CD = R.CO_CD AND A.SALES_CD = R.SALES_CD AND A.WBS_PLAN_CODE_KIND = 'WBSCODE' AND A.WBS_PLAN_CODE_ID = R.WBS_PLAN_CODE_KIND ) ELSE ISSUE_YN END ISSU_YN , REGEXP_REPLACE(SYS_CONNECT_BY_PATH(A.LOWER_CD, ' -> '), '^\s+\-\>\s+', '') AS PATH FROM ( SELECT DISTINCT 'TOP' AS UPPER_CD , P.WBS_PLAN_CODE_KIND||P.SALES_CD AS LOWER_CD , P.CO_CD AS CO_CD , P.SALES_CD AS SALES_CD , '' AS WBS_PLAN_CODE_KIND , '' AS WBS_PLAN_CODE_ID , '' AS WBS_PLAN_CODE_NM , '' AS WBS_PLAN_MNG_ID , '' AS WBS_PLANS_DT , '' AS WBS_PLANE_DT , '' AS DAYCNT , '' AS WBS_RSLTS_RATE , DECODE( NVL(ISU.ISS_CNT,0), 0, 'N', 'Y') AS ISSUE_YN , '' AS RSLTS_FILE_TRGT_KEY , '' AS ISSUE_FILE_TRGT_KEY FROM TB_WB22M01 P--WBS계획 LEFT OUTER JOIN (SELECT CO_CD --회사코드 , SALES_CD --SALES Code , COUNT(ISS_NO) AS ISS_CNT --WBS실적 이슈번호 COUNT FROM TB_WB24M01 --WBS이슈 정보 WHERE CO_CD = #{coCd} GROUP BY CO_CD, SALES_CD ) AS ISU ON P.CO_CD = ISU.CO_CD AND P.SALES_CD = ISU.SALES_CD WHERE P.CO_CD = #{coCd} AND P.WBS_PLAN_CODE_KIND = 'WBSCODE' UNION ALL SELECT P.WBS_PLAN_CODE_KIND||P.SALES_CD AS UPPER_CD , P.WBS_PLAN_CODE_ID||P.SALES_CD AS LOWER_CD , P.CO_CD AS CO_CD , P.SALES_CD AS SALES_CD , P.WBS_PLAN_CODE_KIND AS WBS_PLAN_CODE_KIND , P.WBS_PLAN_CODE_ID AS WBS_PLAN_CODE_ID , P.WBS_PLAN_CODE_NM AS WBS_PLAN_CODE_NM , P.WBS_PLAN_MNG_ID AS WBS_PLAN_MNG_ID , P.WBS_PLANS_DT AS WBS_PLANS_DT , P.WBS_PLANE_DT AS WBS_PLANE_DT , P.DAYCNT AS DAYCNT , R.WBS_RSLTS_RATE AS WBS_RSLTS_RATE --WBS실적 진행율 , R.ISU_YN AS ISSUE_YN --WBS실적 이슈유무 , R.RSLTS_FILE_TRGT_KEY AS RSLTS_FILE_TRGT_KEY , R.ISSUE_FILE_TRGT_KEY AS ISSUE_FILE_TRGT_KEY FROM TB_WB22M01 AS P --WBS계획 LEFT OUTER JOIN RSLT AS R ON P.CO_CD = R.CO_CD AND P.SALES_CD = R.SALES_CD AND P.WBS_PLAN_CODE_KIND = R.WBS_PLAN_CODE_KIND AND P.WBS_PLAN_CODE_ID = R.WBS_PLAN_CODE_ID ) A WHERE 1=1 START WITH UPPER_CD = 'TOP' CONNECT BY PRIOR LOWER_CD = UPPER_CD ORDER BY PATH ) SELECT T.* FROM ( SELECT X.UPPER_CD --상위코드 , X.LOWER_CD --하위코드 , X.CO_CD --회사코드 , CR.CLNT_CD --고객코드 , CR.CLNT_NM --고객명 , X.SALES_CD --SALES Code , SJ.SJ_NO --과제번호 , SJ.SJ_NM --과제명 , SJ.VER_NO --버젼 , SJ.SMRIZE_NM --총괄PM명 , SJ.BEGIN_DT --과제시작일 , SJ.DE_DT --과제0출고일
, TO_CHAR(SJ.BEGIN_DT, 'YYYY-MM-DD') AS BEGIN_DT_S --과제시작일 , TO_CHAR(SJ.DE_DT, 'YYYY-MM-DD') AS DE_DT_S --과제0출고일
, SJ.ACPTNC_DT --과제완료검수일 , SJ.REQRE_DAYCNT --과제소요일수 , X.WBS_PLAN_CODE_KIND --WBS계획 LVL1 , X.WBS_PLAN_CODE_ID --WBS계획 LVL2 , CASE WHEN X.UPPER_CD != 'TOP' AND X.WBS_PLAN_CODE_NM IS NULL THEN (SELECT FN_CM05M01_CD_TO_NM(X.WBS_PLAN_CODE_ID) FROM DUAL) ELSE X.WBS_PLAN_CODE_NM END AS WBS_PLAN_CODE_NM , X.WBS_PLAN_MNG_ID --WBS계획 LVL1 담당자 , CASE WHEN X.UPPER_CD = 'TOP' THEN SJ.SMRIZE_NM ELSE (SELECT FN_CM06M01_ID_TO_NM(X.WBS_PLAN_MNG_ID) FROM DUAL) END AS WBS_PLAN_MNG_NM , X.WBS_PLANS_DT --WBS계획 시작일 , X.WBS_PLANE_DT --WBS계획 종료일 ,TO_CHAR(X.WBS_PLANS_DT, 'YYYY-MM-DD') AS WBS_PLANS_DT_S ,TO_CHAR(X.WBS_PLANE_DT, 'YYYY-MM-DD') AS WBS_PLANE_DT_S , X.DAYCNT --WBS계획 소요일 , CR.CLNT_PJT --고객사프로젝트 , CR.CLNT_PJT_NM --고객사프로젝트명 , NVL(X.WBS_RSLTS_RATE,0) AS WBS_RSLTS_RATE --진도율 , X.LVL --LEVEL , NVL(X.ISSU_YN,'N') AS ISSU_YN --이슈유무 , X.RSLTS_FILE_TRGT_KEY--실적 FILE_TRGT_KEY , X.ISSUE_FILE_TRGT_KEY--이슈 FILE_TRGT_KEY FROM PLAN X INNER JOIN SJD AS CR ON X.CO_CD = CR.CO_CD AND X.SALES_CD = CR.SALES_CD INNER JOIN SUBJ AS SJ ON X.CO_CD = SJ.CO_CD AND X.SALES_CD = SJ.SALES_CD WHERE 1=1 AND X.CO_CD = #{coCd}
AND CR.CLNT_CD = #{clntCd}
AND X.SALES_CD LIKE '%${salesCd}%'
AND SJ.BEGIN_DT BETWEEN #{beginDt} AND #{deDt} --과제시작일 ) T WHERE 1=1
