mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RN, S.* FROM ( SELECT DISTINCT '' PID, #{coCd} ID, #{coCd} CO_CD, -- 회사코드 FN_CM05M01_CD_TO_NM(#{coCd}) AS CO_CD_NM, '전체' ORDRS_NO, '전사 현황' AS SALES_CD, '' ORGN_SALES_CD, '' ORDRS_CLNT_CD, -- 고객사 '전체' AS ORDRS_CLNT_NM, '' CLNT_PJT, '' ORDRS_DTL_DIV20, -- 신작구분 '' AS ORDRS_DTL_DIV20_NM, '', -- 설비명 0 PLAN_CNT, 'E' WBS_PLAN_OS_YN, 'E' PLAN_LOCK_YN, 'E' PLAN_CLOSE_YN, 0 RSULT_CNT, 'E' RSULT_LOCK_YN, 'E' RSULT_CLOSE_YN, '' DATA_GBN FROM DUAL
UNION ALL --회사명을 Top LEVEL 생성 SELECT DISTINCT X.CO_CD PID, TO_CHAR(Y.ORDRS_CLNT_CD) ID, X.CO_CD, -- 회사코드 FN_CM05M01_CD_TO_NM(X.CO_CD) AS CO_CD_NM, A.CLNT_NM, A.CLNT_NM AS SALES_CD, '' ORGN_SALES_CD, Y.ORDRS_CLNT_CD , -- 고객사 A.CLNT_NM AS ORDRS_CLNT_NM, '' CLNT_PJT, '' ORDRS_DTL_DIV20, -- 신작구분 '' AS ORDRS_DTL_DIV20_NM, '', -- 설비명 0 PLAN_CNT, 'E' WBS_PLAN_OS_YN, 'E' PLAN_LOCK_YN, 'E' PLAN_CLOSE_YN, 0 RSULT_CNT, 'E' RSULT_LOCK_YN, 'E' RSULT_CLOSE_YN, '' FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 A ON Y.ORDRS_CLNT_CD = A.CLNT_CD --거래처마스터 WHERE X.CO_CD = #{coCd}
AND X.SALES_CD LIKE '%${salesCd}%'
UNION ALL --오더번호로 1 LEVEL 생성 SELECT DISTINCT TO_CHAR(Y.ORDRS_CLNT_CD) PID, X.ORDRS_NO ID, X.CO_CD, -- 회사코드 FN_CM05M01_CD_TO_NM(X.CO_CD) AS CO_CD_NM, X.ORDRS_NO AS ORDRS_NO, X.ORDRS_NO AS SALES_CD, '', Y.ORDRS_CLNT_CD, -- 고객사 A.CLNT_NM AS ORDRS_CLNT_NM, '', '', '', '', -- 설비명 0 PLAN_CNT, 'E' WBS_PLAN_OS_YN, 'E' PLAN_LOCK_YN, 'E' PLAN_CLOSE_YN, 0 RSULT_CNT, 'E' RSULT_LOCK_YN, 'E' RSULT_CLOSE_YN, '' FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO LEFT OUTER JOIN TB_BM02M01 A ON Y.ORDRS_CLNT_CD = A.CLNT_CD --거래처마스터 WHERE X.CO_CD = #{coCd}
AND X.SALES_CD LIKE '%${salesCd}%'
UNION ALL SELECT --Sales 코드 추출 X.ORDRS_NO PID, X.SALES_CD ID, X.CO_CD, -- 회사코드 FN_CM05M01_CD_TO_NM(X.CO_CD) AS CO_CD_NM, X.ORDRS_NO, -- 수주번호 X.SALES_CD, -- SALES CODE max(Y.ORGN_SALES_CD), -- 원천 SALES CODE max(Y.ORDRS_CLNT_CD), -- 고객사 max(A.CLNT_NM) AS ORDRS_CLNT_NM, max(Y.CLNT_PJT), -- 고객사 PJT max(X.ORDRS_DTL_DIV20), -- 신작구분 FN_CM05M01_CD_TO_NM(max(X.ORDRS_DTL_DIV20)) AS ORDRS_DTL_DIV20_NM, max(X.EQP_NM), -- 설비명 COUNT(W.FILE_TRGT_KEY) PLAN_CNT, NVL(max(W.WBS_PLAN_OS_YN),'N') WBS_PLAN_OS_YN, NVL(max(W.LOCK_YN),'N') PLAN_LOCK_YN, NVL(max(W.CLOSE_YN),'N') PLAN_CLOSE_YN, COUNT(Z.FILE_TRGT_KEY) RSULT_CNT, NVL(max(Z.LOCK_YN),'N') RSULT_LOCK_YN, NVL(max(Z.CLOSE_YN),'N') RSULT_CLOSE_YN, 'D' DATA_GBN --원시데이타 FROM TB_CR02D02 X INNER JOIN TB_CR02M01 Y ON X.CO_CD = Y.CO_CD AND X.ORDRS_NO = Y.ORDRS_NO AND X.CO_CD = #{coCd}
AND X.SALES_CD LIKE '%${salesCd}%'
CROSS JOIN ( SELECT CASE WHEN CODE_KIND = 'ROOT' THEN '' ELSE CODE_KIND END CODE_KIND, CODE_ID, CODE_RPRC, CODE_NM, CASE WHEN LEVEL = '1' THEN CODE_NM ELSE '' END LVL1, CASE WHEN LEVEL = '2' THEN CODE_NM ELSE '' END LVL2, CASE WHEN LEVEL = '3' THEN CODE_NM ELSE '' END LVL3, LPAD('', 3*(LEVEL-1)) || LEVEL LVL, REGEXP_REPLACE(SYS_CONNECT_BY_PATH(CODE_ID, ' -> '), '^\s+\-\>\s+', '') as PATH, CONNECT_BY_ISLEAF AS IS_LEAF, CASE WHEN CONNECT_BY_ISLEAF = 0 THEN 'unit' ELSE 'leaf' END TYPE FROM TB_CM05M01 START WITH CODE_ID = 'WBSDIV' CONNECT BY PRIOR CODE_ID = CODE_KIND ) CD LEFT OUTER JOIN TB_WB01M01 W ON X.CO_CD = W.CO_CD AND X.SALES_CD = w.SALES_CD AND CD.CODE_ID = W.WBS_PLAN_CODE_ID LEFT OUTER JOIN TB_WB02M01 Z ON X.CO_CD = Z.CO_CD AND X.SALES_CD = Z.SALES_CD AND CD.CODE_ID = Z.WBS_PLAN_CODE_ID LEFT OUTER JOIN TB_BM02M01 A ON Y.ORDRS_CLNT_CD = A.CLNT_CD GROUP BY X.ORDRS_NO, X.SALES_CD, X.CO_CD,Y.ORGN_SALES_CD,Y.ORDRS_CLNT_CD, Y.CLNT_PJT
)S WHERE CO_CD = #{coCd} AND SALES_CD LIKE '%${salesCd}%' ) X

SELECT *  FROM TB_WB22M01
    WHERE CO_CD  = #{coCd}
          AND SALES_CD  = #{salesCd}
          AND WBS_PLAN_CODE_KIND  = #{wbsPlanCodeKind}

mapper.delete:
DELETE FROM TB_WB22M01
    WHERE CO_CD = #{coCd}
    AND SALES_CD = #{salesCd}
    AND WBS_PLAN_CODE_KIND = #{wbsPlanCodeKind}
